vec3 sphere_color(float size, vec2 ponto, vec2 center,vec3 cor){


    if(size - length(center - ponto) <0.)return vec3(0.,0.,0.);

    //get z for lightning
    float z =sqrt( pow(size,2.) - pow(ponto.x - center.x,2.) - pow(center.y - ponto.y,2.));
    vec3 normal = normalize(vec3(ponto - center,z));

    float cosTheta = clamp( dot( normal,   light_origin   - vec3(center/iResolution.xy,0.) ), 0.3,1.0);

    //cor mais luz
    cor = cor*cosTheta 
        // a luz forte
        + light_color.rgb * pow( length( light_origin - vec3(center/iResolution.xy,0.) - normal )/2., 8.0);

    return cor;
}
vec4 cores(vec2 ponto,  vec2 iRes){
