float box(vec2 st, float size)
{
    size = 0.5 + size * 0.5;
    st = step(st, vec2(size)) * step(vec2(1.0 - st.x,1.0 - st.y),  vec2(size));
    return st.x * st.y;
}

vec3 buildingTex(vec2 uv)
{
    float n = 10.0;
    vec2 st = fract(uv * n);
    return vec3(box(st, 0.5));
}

float noise(float x) {
    return fract(sin(dot(vec2(x), vec2(12.9898, 78.233)))* 43758.5453);
}

float sdBox( vec3 p, vec3 b )
{
    vec3 d = abs(p) - b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

// Tunnel pattern studied from shane & shau
// i.e. https://www.shadertoy.com/view/4tKXzV
vec2 path(float t) {
    float a = sin(t*.2 + 1.5), b = sin(t*.2);
    return vec2(a*2., a*b);
}

vec3 camera(vec3 ro, vec2 uv, vec3 ta) {
    vec3 fwd = normalize(ta - ro);
    vec3 left = cross(vec3(0, 1, 0), fwd);
    vec3 up = cross(fwd, left);
    return normalize(fwd + uv.x*left + up*uv.y);
}

float sdSphere (vec3 p, float r) {
    return length(p)-r; 
}

vec4 map(vec3 p, vec3 p2) {
    vec3 lTex = buildingTex(p.xy);

    p.xy -= path(p.z)*0.1;
    float t = 100.0;
    float w = 0.0;

    float n = floor(p.z/0.5);

    vec3 oldp = p;
    p.z = mod(p.z, 1.0)-0.5;

    w = sdBox(p-vec3(0.4,-1.2,0.0),vec3(0.02, 0.01, 0.3));
    t = min(t, w);

    w = 0.1 + dot(oldp, vec3(0, 0.0, 0.0));
    t = min(t, w);


    vec4 temp1 = vec4(0.0,0.0,0.0, t);
    vec4 temp2 = vec4(0.0,0.0,0.0, sdBox(p-vec3(-0.4,-1.2,0.0),vec3(0.02, 0.01, 0.3)));
    vec4 temp3 = vec4(0.8,0.8,0.1, sdBox(p-vec3(0.0,-1.21,0.0),vec3(0.4, 0.01, 1.0)));

    p.y += noise(n)*1.0+sin(2.0)*0.1;
    p.x += noise(n)*0.3-0.15;


    vec4 temp4 = vec4(lTex, sdBox(p+vec3(0.8,0.0,0.0),vec3(0.2, 1.0, 0.3)));
    vec4 temp5 = vec4(lTex, sdBox(p+vec3(-0.8,0.0,0.0),vec3(0.2, 1.0, 0.3)));

    vec4 carbody = vec4(0.8,0.4,0.4, sdBox(p2-vec3(0.0,-1.0,0.8),vec3(0.05, 0.05, 0.1)));
    vec4 frontTire = vec4(0.2,0.1,0.1, sdBox(p2-vec3(0.0,-1.0,0.88),vec3(0.08, 0.03, 0.01)));
    vec4 backTire = vec4(0.2,0.1,0.1, sdBox(p2-vec3(0.0,-1.0,0.72),vec3(0.08, 0.03, 0.01)));

    float xCarPos = sin(iTime)*0.12;
    float zCarPos = 0.6;
    vec4 carbody2 = vec4(0.4,0.4,0.8, sdBox(p2-vec3(xCarPos,-1.0,0.8+zCarPos),vec3(0.05, 0.05, 0.1)));
    vec4 frontTire2 = vec4(0.1,0.1,0.2, sdBox(p2-vec3(xCarPos,-1.0,0.88+zCarPos),vec3(0.08, 0.03, 0.01)));
    vec4 backTire2 = vec4(0.1,0.1,0.2, sdBox(p2-vec3(xCarPos,-1.0,0.72+zCarPos),vec3(0.08, 0.03, 0.01)));


    vec4 res1 = combine(temp1,temp2);
    vec4 res2 = combine(temp3,temp4);
    vec4 res3 = combine(temp5,carbody);
    vec4 res4 = combine(frontTire,backTire);
    vec4 res5 = combine(carbody2,frontTire2);
    vec4 res6 = combine(backTire2,res1);

    vec4 res7 = combine(res2,res3);
    vec4 res8 = combine(res4,res5);
    vec4 res9 = combine(res6,res7);
    vec4 res10 = combine(res8,res9);

    return res10;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ( fragCoord.xy / iResolution.xy ) * 2.0 - 1.0;
    vec3  dir;

    vec3 pos = vec3(0.0, 0.0, iTime*SPEED);

    float dt = iTime * 6.;
    vec3 ro = vec3(0, 0, -4. + dt);
    vec3 ta = vec3(0, -2, dt);
    vec3 rd;

    ro.xy += path(ro.z);
    ta.xy += path(ta.z);

    dir = camera(ro, uv, ta);

    float t = 0.0;
    vec3 modelCl;
    for(int i = 0 ; i < 50; i++) {
        vec4 temp = map(t * dir + pos, t * dir);
        modelCl = temp.xyz;
        if(temp.w < 0.01) break;
        t += temp.w * 1.0;
    }

    vec3 ip = pos + dir * t;
    vec4 cl = vec4(t * 0.01) * map(ip - 0.02, ip - 0.02) + t * 0.3;

    // UI
    uv = ( fragCoord.xy /iResolution.xy ) * vec2(80,40);

    vec2 cpos = vec2(37,28);
    float dc = Digit(mod(iTime,60.0),floor(uv-cpos));
    cpos.x += 3.5;
    dc += Digit(mod(iTime*10.0,10.0),floor(uv-cpos));

    vec3 cl2 = vec3(dc)*vec3(1,1,1);

    uv = ( fragCoord.xy * 2.0 - iResolution.xy ) / min( iResolution.x, iResolution.y );
    vec4 res = drawGuage(uv);
	vec3 resColor = res.xyz;

    if(res.w>0.0){
        fragColor = (cl+vec4(modelCl,1.0))-vec4(cl2,1.0);
    } else {
        fragColor = vec4( vec3(resColor), 1.0 );
    }
}

#define time iTime

#define PI acos(-1.0)
#define TAU (2.*PI)

vec3 v01 = vec3(0.0, 1.0, 0.1);

// Taken from the book of shaders
float random (vec2 st) {
    return fract(sin(dot(st,vec2(12.9898,78.233)))*
        43758.5453123);
}
float random (float st) { return random(vec2(st)); }

float frandom (vec2 st) {
	vec2 t = fract(st);
	vec2 a = floor(st);
	vec2 b =  ceil(st);
	return mix(
		mix(random(vec2(a.x, a.y)), random(vec2(b.x, a.y)), t.x),
		mix(random(vec2(a.x, b.y)), random(vec2(b.x, b.y)), t.x),
		t.y); 
}

float sat(float n) { return clamp(n, 0.0, 1.0); }
vec3 sat(vec3 n) { return clamp(n, v01.xxx, v01.yyy); }

vec2 rotate(inout vec2 p, float a) {
	p = p*cos(a) - vec2(p.y, -p.x)*sin(a);
	return p;
}


vec4 egg = vec4(0, 0., 30., 8.);

float sphere(vec3 point, vec3 p, float r) {
	return distance(point, p) - r;
}

float vmax(vec3 v) { return max(max(v.x, v.y), v.z); }
float box(vec3 point, vec3 p, vec3 r) {
	return vmax(abs(point - p) - r);
}	

float rod(vec2 point, vec2 c, float r) {
	return distance(point, c) - r;
}


int mi = 0;
float map(vec3 point) {
	vec3 op = point.xyz;
	point.y += 4.;

	vec3 p1 = point.xyz;

	if (p1.y > egg.y) {
		p1.y /=2.;
	}

	float result = sphere(p1, egg.xyz, egg.w);

	vec3 p2 = point.xyz;
	p2 -= egg.xyz;
	rotate(p2.xz, time/2.);
	rotate(p2.xy, -TAU/10.);
	p2 += egg.xyz;



	float d = -box(p2, egg.xyz + v01.yxx*10.15*(1.5 + 0.5*sin(time*1.)), vec3(8., 30, 10.));
	// d = max(d, -sphere(p1, egg.xyz, egg.w*0.93));

	d = max(d, -sphere(p1, egg.xyz, egg.w*0.93));
    if (d > result) { result = d; mi = 1; }



	p1 = op.xyz;

	p1.y = p1.y*p1.y/12.;

	d = sphere(p1, egg.xyz, egg.w*.7);
	d = min(d, max(box(p1, egg.xyz, vec3(10., .25, 10.)), rod(p1.xz, egg.xz, egg.w*.715)));

    if (d < result) { result = d; mi = 2; }

	return result;
}


vec3 hsv2rgb(float h, float s, float v) {
	vec3 p = sat(abs(fract(vec3(3.0, 2.0, 1.0)/3.0 + h)*6.0 - 3.0) - 1.0);
	return v*mix(p, vec3(1.0), s);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.x;

	vec3 camP = vec3(0), rayD = normalize(vec3(uv, 1.));

	float marched = 0.01, d, far = distance(egg.xyz, camP) + egg.w*2.;
	vec3 p;

	for (int i = 0; i < 300; ++i) {
		p = camP + rayD*marched;
		d = map(p);
		marched += d;

		if (abs(d) < 0.001 || d > far) break;
	}




	float starT = random(uv)*time*2.;
	float value = 4.*mix(
		pow(random(uv + floor(starT)), 120.),
		pow(random(uv +  ceil(starT)), 120.),
		fract(starT));

	value *= (pow(frandom((uv * 7. + time/8.)) + 0.2, 4.));
	vec3 c = hsv2rgb(0.7133, 0.9, value);


	if (marched < far) {
        vec3 normal = normalize(vec3(
			map(p + v01.zxx),
            map(p + v01.xzx),
            map(p + v01.xxz)
        ));

        vec2 m = (2.*iMouse.xy - iResolution.xy) / iResolution.x;

        vec3 lightDir = normalize(vec3(2.*m, -1.));


		if (mi == 0) {
			c = vec3(1.0);
			if (p.y + sin(p.x*2. + time*10.) < -3.) {
				c.gb *= 0.;
			}

			vec3 egg2p = p - egg.xyz;
			float u = atan(egg2p.z, egg2p.x) + time/1.5;
			normal = normalize(normal + 2.*normalize(egg2p)*frandom(vec2(u*79. + p.y*20., random(floor(u*10. + p.y*2.))*4. + u*5. + p.y*2.)));

			c += pow(dot(rayD, reflect(lightDir, normal)) + 0.1, 13.)*.05;

			c *= sat(dot(normal, lightDir));

		} else if (mi == 1) {
			c = vec3(.3, .2, 0.13);
			c *= dot(normal, lightDir);

		} else if (mi == 2) {
			c = vec3(1., .8, 0.3);
			c += pow(dot(rayD, reflect(lightDir, normal)), 50.)*1.;
			c *= dot(normal, lightDir);

		} else {
			c += 0.5; // This is just so I can see which material indexes I missed
		}

	}

	fragColor = vec4(c, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 p = fragCoord/iResolution.y - vec2(1.15, .5);
    vec2 c = 2.3*p; vec2 z = vec2(0); float i = 0., t; t = iTime;
    float lim = 32.;

    for(; z.x*z.x < 12. && ++i < lim ;)
        z = mat2(z, -z.y, z.x) * z + c;

    fragColor = vec4((.5 + .5*cos(t+p.xyx+vec3(0,2,4))) * i / lim, 1.);
}

// "Helical Frame" by dr2 - 2018
