// - shadertoy contains a lot of pseudo Perlin noise, showing indeed value noise 
//   (possibly spline-interpolated) rather than gradient noise
// - original Perlin noise shows poor derivatives
// - in 2002, Perlin proposed an improved version
// - here, I show a 2D version (z=0) and replace permutations by quality integer hash.

float noise_A = .5,               // displacement amplitude
      noise_S = 10.,              // noise scale
        WIDTH = .2;               // vein width: 1. .2 .01
vec2 CYCLE = 0.*vec2(1./4.);      // tilable domain
vec2 R;

// === Perlin noise ( true gradient noise + improved scheme for nice derivatives )
// Ref implem of 2002 Perlin improved noise: http://mrl.nyu.edu/~perlin/noise/
// Here, 2D & integer hash instead of permutations.

// std int hash, inspired from https://www.shadertoy.com/view/XlXcW4
vec3 hash3i( uvec3 x ) 
{
#   define scramble  x = ( (x>>8U) ^ x.yzx ) * 1103515245U // GLIB-C const
    scramble; scramble; scramble; 
    return vec3(x) / float(0xffffffffU) + 1e-30; // <- eps to fix a windows/angle bug
}
#define unsigned(v) ( (v) >= 0. ? uint(v) : -1U-uint(-(v)) ) // for uint(float < 0) is bugged
#define hash(v) hash3i(uvec3(unsigned((v).x),unsigned((v).y),11)).x 
//#define hash(v) hash3i(uvec3(v+float(0x0000ffffU),11)).x // why ?
//#define hash(x)  fract(sin(dot(x,vec2(1,17.53))) * 43758.5453123)

float grad(float r, vec2 p) {
      int h = int(r*256.) & 15;
      float u = h<8 ? p.x : p.y,                 // 12 gradient directions
            v = h<4 ? p.y : h==12||h==14 ? p.x : 0.; // p.z;
      return ((h&1) == 0 ? u : -u) + ((h&2) == 0 ? v : -v);
}

#define G(v) grad(hash(I+v),p-v)
#define fade(t)  t * t * t * (t * (t * 6. - 15.) + 10.) // super-smoothstep

float Inoise2(vec2 p) {
    vec2 I = floor(p); p -= I;    
    vec2 f = fade(p);

    return mix( mix( G(vec2(0,0)),G(vec2(1,0)), f.x),
                mix( G(vec2(0,1)),G(vec2(1,1)), f.x), f.y );
}  

//#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
float fbm2(vec2 p) {
    float v = 0.,  a = .5;
    //mat2 R = rot(.37); // should no longer be necessary

    for (int i = 0; i < 3; i++, p*=2.,a/=2.) 
        //p *= R,
        v += a * Inoise2(p);
        //v += a *a*2.* Inoise2(p); // trying to get derivatives respect the scale profile

    return v;
}

// --- tilable version 
#define Gt(v) grad(hash(mod(I+v,cycle)),p-v)

float InoiseT2(vec2 p, vec2 cycle) {
    vec2 I = floor(p); p -= I;    
    vec2 f = fade(p);

    return mix( mix( Gt(vec2(0,0)),Gt(vec2(1,0)), f.x),
                mix( Gt(vec2(0,1)),Gt(vec2(1,1)), f.x), f.y );
}  

//#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))
float fbmT2(vec2 p, vec2 cycle) {
    float v = 0.,  a = .5;
    //mat2 R = rot(.37); // should no longer be necessary

    for (int i = 0; i < 3; i++, p*=2.,a/=2.,cycle*=2.) 
        //p *= R,
        v += a * InoiseT2(p,cycle);
        //v += a *a*2.* Inoise2(p); // trying to get derivatives respect the scale profile

    return v;
}

// --- version returning vec2
#define G2(v) vec2( grad(hash(I+v),p-v), grad(hash(I+v+117.),p-v))
vec2 Inoise22(vec2 p) {
    vec2 I = floor(p); p -= I;    
    vec2 f = fade(p);

    return mix( mix( G2(vec2(0,0)),G2(vec2(1,0)), f.x),
                mix( G2(vec2(0,1)),G2(vec2(1,1)), f.x), f.y );
}  

vec2 fbm22(vec2 p) {
    vec2 v = vec2(0); float  a = .5;
    //mat2 R = rot(.37);

    for (int i = 0; i < 3; i++, p*=2.,a/=2.) 
        //p *= R,
        v += a * Inoise22(p);

    return v;
}

// --- tilable version
#define Gt2(v) vec2( grad(hash(mod(I+v,cycle)),p-v), grad(hash(mod(I+v,cycle)+117.),p-v))
vec2 InoiseT22(vec2 p, vec2 cycle) {
    vec2 I = floor(p); p -= I;    
    vec2 f = fade(p);

    return mix( mix( Gt2(vec2(0,0)),Gt2(vec2(1,0)), f.x),
                mix( Gt2(vec2(0,1)),Gt2(vec2(1,1)), f.x), f.y );
}  

vec2 fbmT22(vec2 p, vec2 cycle) {
    vec2 v = vec2(0); float  a = .5;
    //mat2 R = rot(.37);

    for (int i = 0; i < 3; i++, p*=2.,a/=2., cycle*=2.) 
        //p *= R,
        v += a * InoiseT22(p,cycle);

    return v;
}

// --- variant: multifractal 
vec2 mfbm22(vec2 p) {
    vec2 v = vec2(0), m=vec2(1), n; float  a = .5;
    //mat2 R = rot(.37);

    for (int i = 0; i < 3; i++, p*=2.,a/=2.) 
        //p *= R,
        n = Inoise22(p),
        v += a * m * n,
        m *= n;

    return v;
}

// --- variant: make 2D noise using derivative of base
vec2 gradInoise2(vec2 p) { 
    float n = Inoise2(p); 
    return vec2( dFdx(n), dFdy(n) )/1.875/1.3 * R.y/noise_S ; // / vec2(dFdx(p.x) ,dFdy(p.y) ); 
}
vec2 gradfbm2(vec2 p) {
    vec2 v = vec2(0); float  a = .5;
    //mat2 R = rot(.37); // should no longer be necessary

    for (int i = 0; i < 3; i++, p*=2.,a/=2.) 
        //p *= R,
        v += a * gradInoise2(p) * a/.5;
        //v += a *a*2.* Inoise2(p); // trying to get derivatives respect the scale profile

    return v;
}
// -- variant: make 2D noise using curl-derivative of base
vec2 div0Inoise2(vec2 p) { 
    float n = Inoise2(p); 
    return vec2( -dFdy(n), dFdx(n) )/1.875/1.3  * R.y/noise_S ;  // / vec2(dFdx(p.x) ,dFdy(p.y) ); 
}
vec2 div0fbm2(vec2 p) {
    vec2 v = vec2(0); float  a = .5;
    //mat2 R = rot(.37); // should no longer be necessary

    for (int i = 0; i < 3; i++, p*=2.,a/=2.) 
        //p *= R,
        v += a * div0Inoise2(p) * a/.5;

    return v;
}

