    U /= R.y; 
    vec2 U0 = U, D;
    U.y -= .1*iTime;
    O -= O;
    //i=0; j=1;
    U *= noise_S; // U-=100.;
    if (i==0) {        // raw noise
        float v = j==1 ? CYCLE==vec2(0) ? fbm2(U)    : fbmT2(U,CYCLE*noise_S)
                       : CYCLE==vec2(0) ? Inoise2(U) : InoiseT2(U,CYCLE*noise_S);
        D = vec2(v);
    } if (i==1) {      // displacement: 2D noise
        vec2 v = j==1 ? CYCLE==vec2(0) ? fbm22(U)    : fbmT22(U,CYCLE*noise_S)
                      : CYCLE==vec2(0) ? Inoise22(U) : InoiseT22(U,CYCLE*noise_S);
        D = v;
        O.b += .15;
    } else if (i==2) { // displacement: grad(1D noise)
        vec2 v = j==1 ? gradfbm2(U)
                      : gradInoise2(U);
        D = v;
        O.g += .15;   
    } else if (i==3) { // displacement: grad-curl(1D noise): conservative
        vec2 v = j==1 ? div0fbm2(U)
                      : div0Inoise2(U);
        D = v;
        O.r += .15;   
    } else if (i==4) { // displacement: grad(cascade)
        float v = j==1 ? fbm2(U)
                       : Inoise2(U);
        D = vec2( dFdx(v),dFdy(v) ) / 1.875  * R.y/noise_S;
        O.rg += .15;
    } 
    if (i==0 || keyToggle(32) )
            O = vec4(.5+.5*D.xyxy);       // raw noise
        else {
            U = U0 + noise_A/noise_S * D; // displacement
            WIDTH = .01+WIDTH*(.5+.5*sin(iTime));
            O += pow(.5-.5*cos(10.*R.y/R.x*6.28*U.x), 1./WIDTH );
            //U = (U*R.y-.5*R)/R.x*5.; U.x = fract(U.x-.5)-.5; O += pow(.5-.5*cos(6.28*length(U)*1.2), 1./WIDTH );
            //O = texture(iChannel0, U);          
        }
}

const int iterations = 5;
const float scale = 3.0;
const float scape = 3.8;
const float eps = 0.003;

vec3 BoxFold(vec3 vec)
{
    vec3 o = vec3(0.,0.,0.);
    for (int k = 0; k < 3; k++)
    {
        float axis = vec[k];
        if (axis > 1.0)
        {
            axis = 2.0 - axis;
        }
        else if (axis < -1.0)
        {
            axis = -2.0 - axis;
        }
        o[k] = axis;
    }
    return o;
}

vec3 SphereFold(vec3 vec)
{
    float mag = length(vec);
    if (mag < .5)
    {
        vec = vec * 4.0;
    }
    else if (mag < 1.0)
    {
        vec = vec / (mag * mag);
    }
    return vec;
}

float Iteration(vec3 vec)
{
    for (int i = 0; i < iterations; i++)
    {
        vec = BoxFold(vec);
        vec = SphereFold(vec);
        vec = vec * scale;
    }
    float mag = length(vec);
    if(mag < scape){
        float amt = mag / scape;
        return amt;
    }
    return 1.0/mag;

}

float AAIteration(vec3 v){
    float aa = Iteration(v);

    aa += Iteration(vec3(v.x + eps, v.y, v.z));
    aa += Iteration(vec3(v.x - eps, v.y, v.z));
    aa += Iteration(vec3(v.x, v.y + eps, v.z));
    aa += Iteration(vec3(v.x, v.y - eps, v.z));
    aa /= 5.0;

    return aa;
}

