float line(vec2 uv, vec2 p, float s){
    vec2 v = uv;
    v -= p;
    v /= max(s, 0.01);

    float t = 0.0;

    t += circ(v, vec2(0.0, 1.0), s);
    t += circ(v, vec2(0.0, -1.0), s);
    t += box(v, vec2(s * 1.8 + 0.1, 2.0)); // Randomly found out that the value I used for testing scale was unique so this is just me trying to make it seem like it works with a weird formula


    return t;//smoothstep(0.4, 0.41, t);
}

vec3 lineCol(vec2 uv){
    vec3 col = mix(
        vec3(0.453125, 0.75, 0.828125),
        vec3(0.59375, 0.81640625, 0.828125),
        sin(uv.y * 6.0 + cos(uv.x * 3.6))
    );

    return col;
}

float getLines(vec2 uv){
    float lines = 0.0;

    for (float i = 0.0; i < 64.0; i++){
        float id = i / 64.0;

        mat2 a = rot(0.7853981633974483); // PI / 4
        vec2 v = uv * a;

        float y = yPos(id);
        float x = xPos(id, y);
        float s = xPos(id, y) * 0.2;

        float l = line(v, vec2(-s) + vec2(x, y) * 2.5 - vec2(1.6, 0.0), s);
        lines += max(0.0, l);
    }

    lines = max(0.0, min(1.0, lines));
    return lines;
}

vec3 background(vec2 uv){
    vec3 col = mix(
        mix(
            vec3(0.51953125, 0.52734375, 0.5859375),
            vec3(0.21875, 0.2890625, 0.37890625),
            cos(uv.x * sin(uv.y * 4.0 + iTime) + uv.y * 2.0)
            ),
        vec3(0.21, 0.4, 0.5),
        sin(uv.y * 7.24 + cos(uv.x * uv.y + iTime) * sin(iTime + uv.x * 5.3)) / 2.0 + 1.0
    );

    uv.x *= (iResolution.x / iResolution.y);

    col = max(vec3(0.0), col - 0.2 * getLines(uv * vec2(1.04, 1.0) - vec2(0.04, 0.0)));

    float l = getLines(uv);
    if (l > 0.0)
    	col = lineCol(uv) * l;

    return col;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = COLOR;

    // Generate all dem triangles
    for (float i = TRI; i > 0.0; i--){
        float id = i / TRI;
        float y = yPos(id);
        float x = xPos(id, y);
        float s = min(0.89, max(0.071, id * 0.5));
        float shad = tri(
            uv,
            vec2(x, mix(-s, 1.0 + s / 2.0, y)),
            s
        );

        if (shad < 0.1)
        	col = triCol(id, y) * (1.0 - shad);
    }

    // Set background mask
    float dist = distance(uv, vec2(0.5 * iResolution.x / iResolution.y, 0.5));
    float l = abs(dist - 0.4);
    if (dist > 0.4)
        col = background(vec2(uv.x / (iResolution.x / iResolution.y), uv.y));

    // Make circle logo shadow
    float dist_shad = distance(uv, vec2(0.5 * iResolution.x / iResolution.y, 0.49));
    float l_shad = abs(dist_shad - 0.4);
    col *= mix(0.3, 1.0, min(1.0, l_shad * 30.0));

    // Make circle logo
    col = max(col, vec3(smoothstep(0.97, 0.971, 1.0 - l)));

    fragColor = vec4(col,1.0);
}

#define MAX_STEPS 200
#define MIN_DIST 0.005
#define zoom 1.

float sphere(vec3 ro, float r) {
	return length(ro)-r;   
}

vec3 getColor(vec3 ro, vec3 s) {
 	return vec3(.5+ro.x-s.x,.5+ro.y-s.y,.5+ro.z-s.z);   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = vec3(0.5*cos(iTime+2.*uv.x));

    col += mod(sin(iTime*uv.x+uv.y)*cos(uv.x)*4., uv.x);
    col.g += 2.*uv.x*sin(iTime);
    col /= 10.*uv.y;
    uv -= .5;
    uv *= 2.;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(2.*sin(iTime),0.6*sin(iTime),2.*cos(iTime));
    vec3 sph = vec3(0.);
    vec3 f = normalize(sph-ro);
    vec3 r = cross(vec3(0.,1.,0.), f);
    vec3 u = cross(f, r);
    vec3 c = ro + f * zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 rd = i - ro;

    for (int i = 0; i < MAX_STEPS; i++) {
    	float d = sphere(ro-sph, 1.);
        if (d < MIN_DIST) {
        	col.rgb = getColor(ro,sph);
            break;
        }
        ro += d*rd;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}

vec2 sqr(vec2 z) {
 	return vec2(z.x*z.x - z.y*z.y, 2.0*z.x*z.y); 
}

const float iterations = 1000.0;

float iterate(vec2 num) {
    vec2 z = num;
    float i = 0.0;    
    for(; i < iterations; i++) {
        if(length(z) > 2.0) {
        	break;   
        }

        z = sqr(z) + num;
    }
    return i;
}

const float PI = 3.14159265359;

vec3 color(float i) {
    if(i >= iterations) {
     	return vec3(0);   
    }
    i = log(i) + 5.0*iTime;
    vec3 result;
    result.r = 0.5*sin(i) + 0.5;
    result.g = 0.5*sin(i + 2.0*PI*1.0/3.0) + 0.5;
    result.b = 0.5*sin(i + 2.0*PI*2.0/3.0) + 0.5;
    return result;
}

