const float pi = 3.14159;

#define DMINQ(id) if (d < dMin) { dMin = d;  idObj = id;  qHit = q; }

float SparkDf (vec3 p, float dMin)
{
  vec3 q;
  float d;
  dMin *= 2.;
  for (int k = 0; k < 4; k ++) {
    if (illum[k] > 0.) {
      q = p;
      q.xz = Rot2D (q.xz, (0.5 + float (k)) * 0.5 * pi + 0.1 * tCur);
      q.y -= 10. + 0.2 * Fbm1 (5. * tCur);
      q.z += 3.;
      q.yz = Rot2D (q.yz, -0.25 * pi);
      q.z += 16.;
      d = PrCylDf (q, 0.9, 20.);
      if (d < dMin) {
        q.xy -= (1. - smoothstep (19., 19.5, q.z)) * (2. * vec2 (Fbm2p (vec2 (0.4 * q.z, 0.) +
           pth[k].xy), Fbm2p (vec2 (0.3 * q.z, 0.) + pth[k].zw)) - 1.);
        d = PrCylDf (q, 0.02, 20.);
        DMINQ (7);
      }
    }
  }
  return 0.5 * dMin;
}

float ObjDf (vec3 p)
{
  vec3 q, qq;
  vec2 b;
  float dMin, d, r, w, ww;
  dMin = dstFar;
  p.y -= 5.36;
  r = length (p.xz);
  qq = p;
  qq.xz = Rot2D (qq.xz, 2. * pi * (floor (16. * ((r > 0.) ?
     atan (qq.z, - qq.x) / (2. * pi) : 0.) + 0.5) / 16.));
  q = qq;
  w = (0.5 - q.y / 10.);
  ww = (1. + w * w);
  q.x += 1.6 * ww;
  q.y = mod (q.y, 1.) - 0.5;
  d = PrBox2Df (q.xy, vec2 (0.05 * ww, 0.5));
  b = abs (q.yz) - vec2 (0.45, 0.265 * ww);
  d = max (max (d, - max (min (max (b.x, b.y), 0.), max (0.1 * (1. + 0.1 * ww) - length (q.yz),
     0.07 - abs ((1.85 - w) * abs (b.y) - abs (b.x))))), abs (qq.y) - 5.05);
  DMINQ (1);
  d = PrCylDf (q.yzx, 0.02, 0.06 * ww);
  q.yz = abs (q.yz) - vec2 (0.46, 0.27 * ww);
  d = max (min (d, PrCylDf (q.yzx, 0.02, 0.06 * ww)), abs (qq.y) - 5.05);
  DMINQ (2);
  dMin *= 0.6;
  q = p;
  q.y -= 5.1;
  d = PrCylAnDf (q.xzy, 1.7, 0.2, 0.05);
  q.y = abs (q.y - 0.45) - 0.15;
  d = min (d, PrTorusDf (q.xzy, 0.03, 1.85));
  DMINQ (3);
  q = qq;
  q.xy -= vec2 (-1.85, 5.4);
  d = PrCylDf (q.xzy, 0.03, 0.3);
  DMINQ (3);
  q = p;
  q.y -= 7.;
  d = PrSphDf (q, 1.);
  DMINQ (4);
  qq = p;
  qq.xz = Rot2D (qq.xz, pi / 16.);
  qq.xz = Rot2D (qq.xz, 2. * pi * (floor (16. * ((r > 0.) ?
     atan (qq.z, - qq.x) / (2. * pi) : 0.) + 0.5) / 16.));
  q = qq;
  q.xy -= vec2 (-3.3, -5.2);
  d = PrCylDf (q.xzy,  0.25, 0.15);
  DMINQ (6);
  q = p;
  q.y -= 0.65;
  d = PrCylDf (q.xzy, 0.1 + 0.02 * abs (sin (6. * pi * q.y)), 6.);
  DMINQ (5);
  q = qq;
  q.y -= 7.7;
  q.xy = Rot2D (q.xy, -0.33 * pi);
  q.x += 1.5;
  d = PrCylDf (q.yzx, 0.04 + 0.01 * abs (sin (12. * pi * q.x)), 1.5);
  DMINQ (5);
  dMin = SparkDf (p, dMin);
  return dMin;
}

float ObjRay (vec3 ro, vec3 rd)
{
  float dHit, d;
  dHit = 0.;
  for (int j = 0; j < 150; j ++) {
    d = ObjDf (ro + rd * dHit);
    if (d < 0.0005 || dHit > dstFar) break;
    dHit += d;
  }
  return dHit;
}

vec3 ObjNf (vec3 p)
{
  vec4 v;
  vec2 e = vec2 (0.0001, -0.0001);
  v = vec4 (ObjDf (p + e.xxx), ObjDf (p + e.xyy), ObjDf (p + e.yxy), ObjDf (p + e.yyx));
  return normalize (vec3 (v.x - v.y - v.z - v.w) + 2. * v.yzw);
}

vec3 CloudNf (vec3 p, float d)
{
  vec2 s, e;
  e = vec2 (0.0005 * d, 0.);
  s = p.xz + 0.03 * tCur;
  return normalize (vec3 (0.1 * (Fbm2p (s) - vec2 (Fbm2p (s + e.xy), Fbm2p (s + e.yx))), e.x).xzy);
}

vec3 BgCol (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float d;
  if (rd.y < 0.) { 
    ro += (- ro.y / rd.y) * rd;
    col = 0.5 * mix (vec3 (0.35, 0.4, 0.2), vec3 (0.4, 0.45, 0.1),
       smoothstep (0.3, 0.7, Fbm2p (ro.xz)));
    vn = VaryNf (ro, vec3 (0., 1., 0.), 2.);
    col = col * (0.2 + 0.8 * max (dot (vn, sunDir), 0.)) * (0.7 + 0.3 * smoothstep (3.5, 4., length (ro.xz)));
   } else {
    d = (30. - ro.y) / max (rd.y, 0.001);
    ro += d * rd;
    vn = mix (vec3 (0., 1., 0.), CloudNf (0.02 * ro, d), smoothstep (0., 0.3, rd.y));
    col = vec3 (1.) * (0.3 + 0.7 * Fbm2p (0.01 * ro.zx)) * (0.2 + 0.8 * max (dot (rd, vn), 0.));
    col += vec3 (0.1, 0.1, 0.05) * Fbm1 (4. * tCur) * smoothstep (0., 0.3, rd.y);
  }
  col = mix (vec3 (0.14), col, smoothstep (0., 0.1, abs (rd.y)));
  return col;
}

vec3 ShowScene (vec3 ro, vec3 rd)
{
  vec3 col, vn;
  float dstObj, illTot, f, t, r, c, ae;
  illTot = 0.;
  for (int k = 0; k < 4; k ++) {
    f = 0.1 * float (k);
    t = 0.2 * tCur;
    pth[k] = vec4 (4. + f - (10. + 3. * f) * t + (0.8 - f) * sin ((0.1 + 0.1 * f) * t),
       sin ((0.11 + 0.1 * f) * t) + (0.3 + 0.2 * f) * sin ((0.17 + 0.5 * f) * t),
       5. + 2. * f - (12. - 2. * f) * t + (0.5 + 0.3 * f) * sin ((0.15 - 0.2 * f) * t),
       sin ((0.12 - 0.1 * f) * t) + (0.4 - 0.2 * f) * sin ((0.12 + 0.2 * f) * t));
    illum[k] = step (0.5, Fbm1 (4. * tCur + 100. * f));
    illTot += 0.25 * illum[k];
  }
  dstObj = ObjRay (ro, rd);
  if (dstObj < dstFar) {
    ro += dstObj * rd;
    vn = ObjNf (ro);
    if (idObj == 7) {
      col = vec3 (0.9, 0.9, 0.6) * clamp (1.3 - 0.3 * Fbm1 (5. * qHit.z), 0., 1.) *
         (0.2 + 1.3 * max (0.3 - dot (rd, vn), 0.));
    } else {
      r = length (ro.xz);
      if (idObj == 1) {
        col = mix (vec3 (0.8, 0.8, 0.9), vec3 (0.9, 0.9, 0.3),
           step (dot (normalize (vn.xz), normalize (ro.xz)), -0.9)) * (0.8 + 0.2 * Fbm3 (16. * ro));
      } else if (idObj == 2) {
        col = vec3 (0.3, 0.7, 0.3);
      } else if (idObj == 3) {
        col = vec3 (0.7, 0.5, 0.2) * (0.8 + 0.2 * SmoothBump (0.1, 0.9, 0.03, mod (9. * r, 1.)));
      } else if (idObj == 4) {
        ae = asin (ro.y / length (ro)) / (0.5 * pi);
        c = cos (0.5 * pi * ae);
        col = vec3 (0.2, 0.2, 0.5) * (1. + illTot) * (1. - 0.2 * max (SmoothBump (0.5 - c, 0.5 + c,
           0.5 * c, mod (6. * ro.y, 1.)), (1. - smoothstep (0.99, 0.995, abs (ae))) *
           SmoothBump (0.5 - 0.005 / c, 0.5 + 0.005 / c, 0.5 * 0.005 / c,
           mod (16. * ((r > 0.) ? atan (ro.z, - ro.x) / (2. * pi) : 0.), 1.))));
      } else if (idObj == 5) {
         col = vec3 (0.7, 0.4, 0.2) * (0.7 + 0.3 * illTot * step (r, 0.15));
      } else if (idObj == 6) {
        col = vec3 (0.4, 0.2, 0.1);
      }
      col = col * (0.3 + 0.7 * max (dot (vn, sunDir), 0.)) * (0.8 + 0.2 * illTot) +
         0.05 * pow (max (dot (normalize (sunDir - rd), vn), 0.), 32.);
    }
  } else col = BgCol (ro, rd) * (0.7 + 0.3 * illTot);
  return clamp (col, 0., 1.);
}

void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
  mat3 vuMat;
  vec4 mPtr;
  vec3 ro, rd, col;
  vec2 canvas, uv, uvd, ori, ca, sa;
  float el, az, zmFac;
  canvas = iResolution.xy;
  uv = 2. * fragCoord.xy / canvas - 1.;
  uv.x *= canvas.x / canvas.y;
  tCur = iTime;
  mPtr = iMouse;
  mPtr.xy = mPtr.xy / canvas - 0.5;
  az = 0.;
  el = -0.03 * pi;
  if (mPtr.z > 0.) {
    az += 3. * pi * mPtr.x;
    el += 0.45 * pi * mPtr.y;
  } else {
    az = 2. * pi * (2. * mod (floor (0.01 * tCur), 2.) - 1.) *
       SmoothBump (0.25, 0.75, 0.24, mod (0.01 * tCur, 1.));
    el -= 0.17 * pi * sin (0.02 * pi * tCur);
  }
  ori = vec2 (el, az);
  ca = cos (ori);
  sa = sin (ori);
  vuMat = mat3 (ca.y, 0., - sa.y, 0., 1., 0., sa.y, 0., ca.y) *
          mat3 (1., 0., 0., 0., ca.x, - sa.x, 0., sa.x, ca.x);
  ro = vuMat * vec3 (0., 6., -40.);
  if (ro.y < 1.) {
    ro.y = 1.;
    ro.xz *= sqrt (1635.) / length (ro.xz);
  }
  zmFac = 7. - 3. * cos (2. * az);
  dstFar = 200.;
  sunDir = vuMat * normalize (vec3 (0.5, 2., -1.));

  #if ! AA
  rd = vuMat * normalize (vec3 (uv, zmFac));
  col = ShowScene (ro, rd);
#else
  col = vec3 (0.);
  for (int iy = 0; iy <= 1; iy ++) {
    for (int ix = 0; ix <= 1; ix ++) {
      uvd = (0.5 / canvas.y) * (2. * vec2 (float (ix), float (iy)) - 1.);
      rd = vuMat * normalize (vec3 (uv + uvd, zmFac));
      col += ShowScene (ro, rd);
    }
  }
  col *= 0.25;
#endif
  fragColor = vec4 (col, 1.);
}

float PrBox2Df (vec2 p, vec2 b)
{
  vec2 d;
  d = abs (p) - b;
  return min (max (d.x, d.y), 0.) + length (max (d, 0.));
}

float PrSphDf (vec3 p, float r)
{
  return length (p) - r;
}

float PrCylDf (vec3 p, float r, float h)
{
  return max (length (p.xy) - r, abs (p.z) - h);
}

float PrCylAnDf (vec3 p, float r, float w, float h)
{
  return max (abs (length (p.xy) - r) - w, abs (p.z) - h);
}

float PrTorusDf (vec3 p, float ri, float rc)
{
  return length (vec2 (length (p.xy) - rc, p.z)) - ri;
}

float SmoothBump (float lo, float hi, float w, float x)
{
  return (1. - smoothstep (hi - w, hi + w, x)) * smoothstep (lo - w, lo + w, x);
}

vec2 Rot2D (vec2 q, float a)
{
  return q * cos (a) + q.yx * sin (a) * vec2 (-1., 1.);
}

const float cHashM = 43758.54;

vec2 Hashv2f (float p)
{
  return fract (sin (p + vec2 (0., 1.)) * cHashM);
}

vec2 Hashv2v2 (vec2 p)
{
  vec2 cHashVA2 = vec2 (37., 39.);
  return fract (sin (vec2 (dot (p, cHashVA2), dot (p + vec2 (1., 0.), cHashVA2))) * cHashM);
}

vec4 Hashv4v3 (vec3 p)
{
  vec3 cHashVA3 = vec3 (37., 39., 41.);
  vec2 e = vec2 (1., 0.);
  return fract (sin (vec4 (dot (p + e.yyy, cHashVA3), dot (p + e.xyy, cHashVA3),
     dot (p + e.yxy, cHashVA3), dot (p + e.xxy, cHashVA3))) * cHashM);
}

float Noiseff (float p)
{
  vec2 t;
  float ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = Hashv2f (ip);
  return mix (t.x, t.y, fp);
}

float Noisefv2 (vec2 p)
{
  vec2 t, ip, fp;
  ip = floor (p);  
  fp = fract (p);
  fp = fp * fp * (3. - 2. * fp);
  t = mix (Hashv2v2 (ip), Hashv2v2 (ip + vec2 (0., 1.)), fp.y);
  return mix (t.x, t.y, fp.x);
}

float Noisefv3 (vec3 p)
{
  vec4 t;
  vec3 ip, fp;
  ip = floor (p);
  fp = fract (p);
  fp *= fp * (3. - 2. * fp);
  t = mix (Hashv4v3 (ip), Hashv4v3 (ip + vec3 (0., 0., 1.)), fp.z);
  return mix (mix (t.x, t.y, fp.x), mix (t.z, t.w, fp.x), fp.y);
}

float Fbm1 (float p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    f += a * Noiseff (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbm2p (vec2 p)
{
  float f, a, s;
  f = 0.;
  s = 0.;
  a = 1.;
  for (int j = 0; j < 4; j ++) {
    f += a * Noisefv2 (p);
    s += a;
    a *= 1./2.5;
    p *= 2.5;
  }
  return f / s;
}

float Fbm3 (vec3 p)
{
  float f, a;
  f = 0.;
  a = 1.;
  for (int i = 0; i < 5; i ++) {
    f += a * Noisefv3 (p);
    a *= 0.5;
    p *= 2.;
  }
  return f * (1. / 1.9375);
}

float Fbmn (vec3 p, vec3 n)
{
  vec3 s;
  float a;
  s = vec3 (0.);
  a = 1.;
  for (int j = 0; j < 5; j ++) {
    s += a * vec3 (Noisefv2 (p.yz), Noisefv2 (p.zx), Noisefv2 (p.xy));
    a *= 0.5;
    p *= 2.;
  }
  return dot (s, abs (n));
}

vec3 VaryNf (vec3 p, vec3 n, float f)
{
  vec3 g;
  vec2 e = vec2 (0.1, 0.);
  g = vec3 (Fbmn (p + e.xyy, n), Fbmn (p + e.yxy, n), Fbmn (p + e.yyx, n)) - Fbmn (p, n);
  return normalize (n + f * (g - n * dot (n, g)));
}

#define PI 3.14159265359

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 rotate2D (vec2 _st, float _angle) {
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.y;
	vec3 color = hsb2rgb(vec3(fract(iTime*.1),.5,1.2));
    float r = length(p)*1.;
    float w = 1.5;
    float t = sin(r*2.8+iTime*PI*1.)*4.5;
    p *= t;
    p = rotate2D(p,(r*PI*2.8-iTime*1.));
    color *= smoothstep(-w,.0,p.x)*smoothstep(w,.0,p.x);
    color *= distance(vec2(0.), vec2(cos(r*4.+iTime*PI*1.)));
	fragColor = vec4(color, 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	uv -= .4;
    uv *= 2.;
    uv.x *= iResolution.x/iResolution.y;

    uv *= 1.+sin(-iTime/10.);
    float it = 0.;

    uv.x -= .745;
    uv.y += .1;

    const int MAX_STEPS = 1000;

    vec2 c = uv;
    vec2 z = c;

    for (int i = 0; i < MAX_STEPS; i++) {
        float x = pow(z.x, 2.) - pow(z.y, 2.);
        float y = 2. * z.x * z.y;
     	z = vec2(x, y) + c;

        if (length(z) > 2.) break;
        it++;
    }

    // Time varying pixel color
    vec3 col = vec3(0.5 + 0.5*cos(iTime+it));
    col.g = 1.-sin(it);
    col.b = cos(it);

    // Output to screen
    fragColor = vec4(col,1.0);
}

