}

// (2,1)
vec3 window4(vec2 uv, float t) {
    t = sqrt(anim(t));
    float size = t / 2.0;
	float box = float((uv.x > size && uv.x < 1.0-size) && (uv.y > size && uv.y < 1.0-size));
    return vec3(box);
}

// (0,2)
vec3 window5(vec2 uv, float t) {
    //t = sqrt(anim(t));
	float box = length(vec2(anim(t)) - uv) < 0.5 ? 1.0 : 0.0;
    return vec3(box);
}

// (1,2)
vec3 window6(vec2 uv, float t) {
    //t = sqrt(anim(t));
	float box = length(vec2(0.5) - uv) < fract(pow(anim(t), 0.3)) / 2.0 ? 1.0 : 0.0;
    return vec3(box);
}

// (2,2)
vec3 window7(vec2 uv, float t) {
    //t = sqrt(anim(t));
	float box = length(vec2(0.5) - uv) < (fract(anim(t)) + fract(6.0*anim(t)) / 6.0) / 2.35 ? 1.0 : 0.0;
    return vec3(box);
}

// (3,2)
vec3 window8(vec2 uv, float t) {
    //t = sqrt(anim(t));
	float box = length(vec2(0.5) - uv) < (fract(8.0*anim(t)) * pow(anim(t), 1.2) + anim(t)) / 4.0 ? 1.0 : 0.0;
    return vec3(box);
}

// (0,3)
vec3 window9(vec2 uv, float t) {
    float d = length(uv - vec2(0.5, 0.5)) * 2.0;
    float vary = 0.1 * anim(t);
    d = smoothstep(0.3, 0.4+vary, d)-smoothstep(0.6,0.9-vary,d);
    return vec3(d) * anim(t) * 1.618;
}

// (3,1)
vec3 window10(vec2 uv, float t) {
    float tau = 3.14159265359*2.;
    float speed = 0.125;
    mat2 m = mat2(cos(fract(t*speed)*tau), -sin(fract(t*speed)*tau), sin(fract(t*speed)*tau), cos(fract(t*speed)*tau));
    uv -= vec2(0.5);
    uv = m * uv;
    uv += vec2(0.5);
	float box = float((uv.x > 0.2 && uv.x < 0.8) && (uv.y > 0.2 && uv.y < 0.8));
    return vec3(box);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 norm = vec2(iResolution.y);
    vec2 uv = fragCoord / norm * SCALE;
    vec2 mouse = iMouse.xy / norm * SCALE;
    float t = iTime;

    // Used to create subspaces.
	vec2 ipart, fpart; 
    fpart = modf(uv, ipart);

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(t+fpart.xyx+vec3(0,2,4));

    // Mouse cursor
    if( length(uv - mouse.xy) < MOUSE_CURSOR_SIZE ) 
        col = mix(col, vec3(1), 0.618); 

    // Selected cell
    if((mouse.x > ipart.x && mouse.x < ipart.x + 1.0)
       && (mouse.y > ipart.y && mouse.y < ipart.y + 1.0)) 
        col = mix(col, vec3(1.0), 0.5);

    // Window selection
    if(ipart.x == 0.0 && ipart.y == 0.0) col = mix(col, window0(fpart, t), 0.5); // window0
    if(ipart.x == 4.0 && ipart.y == 0.0) col = mix(col, window7(fpart, t), 0.5); // window1
    if(ipart.x == 0.0 && ipart.y == 1.0) col = mix(col, window5(fpart, t), 0.5); // window2
    if(ipart.x == 1.0 && ipart.y == 1.0) col = mix(col, window9(fpart, t), 0.5); // window3
    if(ipart.x == 2.0 && ipart.y == 1.0) col = mix(col, window4(fpart, t), 0.5); // window4
    if(ipart.x == 0.0 && ipart.y == 2.0) col = mix(col, window2(fpart, t), 0.5); // window5
    if(ipart.x == 1.0 && ipart.y == 2.0) col = mix(col, window6(fpart, t), 0.5); // window6
    if(ipart.x == 4.0 && ipart.y == 1.0) col = mix(col, window1(fpart, t), 0.5); // window7
    if(ipart.x == 3.0 && ipart.y == 2.0) col = mix(col, window8(fpart, t), 0.5); // window8
    if(ipart.x == 2.0 && ipart.y == 0.0) col = mix(col, window3(fpart, t), 0.5); // window9
    if(ipart.x == 3.0 && ipart.y == 1.0) col = mix(col, window10(fpart, t), 0.5); // window10

    // Gridlines
	//float epsilon = 0.000001; // To remove flickering gridlines (why does this work??).
    //float dx = (1.0 / iResolution.x) * iResolution.x / iResolution.y + epsilon;
    //float dy = 1.0 / iResolution.y + epsilon;
    //if(fpart.x < dx * GRID_THICKNESS || fpart.y < dy * GRID_THICKNESS ) 
    //    col = mix(col, vec3(0), 1.0);

    // Output to screen
    fragColor = vec4(col,1.0);
}

