const float EPSILON_NORM = .001;

const vec3 A = vec3(-3.142, -3.142, .618);
const vec3 B = vec3(-3.142, -3.142,-.362);
const vec3 C = vec3(-3.142, -3.142, 2.0);
const vec3 D = vec3(-3.142, -3.142, -3.142);

