}


float noise2(vec3 x)
{
        return fract(sin(dot(x.xy,
                         vec2(12.9898,78.233)))
                * 43758.5453123);
}

vec2 rot(vec2 r, float a) {
	return vec2(
		cos(a) * r.x - sin(a) * r.y,
