        }*/

        vec3 colorPoint = vec3(cos(p.x) + offSet + p.x + sin(iTime), cos(p.y) + offSet + p.y + iTime, sin(p.z) + offSet + p.z + iTime);
        float redpt = PolkaDot3D(mod(p, 3.0), .0, .50);
        vec3 redDots = vec3(redpt, 0.0, 0.0);
        float yellowpt = PolkaDot3D(p, .30, .5);
        vec3 yellowDots = vec3(yellowpt, yellowpt, 0.0);

        float ptOffY = PolkaDot3D(colorPoint + p, .0, .2);
        float ptOffR = PolkaDot3D(mod(colorPoint, 25.0), .0, .2);
        vec3 redDotsOff = vec3(ptOffR, 0.0, 0.0);
        vec3 yellowDotsOff = vec3(ptOffY, ptOffY, 0.0);

        vec3 blue =  palette(p.z, A, B, C, D);

        if(redDots.x > 0.0)
        {
            redDots.x = 1.0;
           // K_d = vec3(1.0, 0.0, 0.0);
             K_d = palette(redpt, vec3(0.478, 1.068, -0.212), vec3(2.238, 1.118, 1.388), vec3(0.108, 0.528, 0.478), vec3(0.907, 1.547, 1.657));
        }
        else if(redDotsOff.x > 0.0)
        {
            redDotsOff.x = 1.0;
           // K_d = vec3(1.0, 0.0, 0.0);
             K_d = palette(ptOffR, vec3(0.478, 1.068, -0.212), vec3(2.238, 1.118, 1.388), vec3(0.108, 0.528, 0.478), vec3(0.000, 0.478, 0.478));
        }
         else if(yellowDots.x > 0.0)
        {
            yellowDots.y = 1.0;
            yellowDots.x = 1.0;
            K_d =  palette(-yellowpt, vec3(.5, .5, .0), vec3(.5, .5, .0), vec3(.10, .50, .0), vec3(0.0, .0, .0));
        }
        else if(yellowDotsOff.x > 0.0)
        {
            yellowDotsOff.y = 1.0;
            yellowDotsOff.x = 1.0;
            K_d = vec3(1.0, 1.0, 0.0);
        } else {
			K_d = yellowDots + redDots + redDotsOff + yellowDotsOff + vec3(0, 0, blue.z);      
        }

    }
    float gap = 360.0 / float(numTentacles) * deg2rad;

    for(int i = 0; i < numTentacles; i++)
    {

       	float angle = float(i) * gap;     

        float offsetX = (amplitude) * sin(angle);
    	float offsetY = (amplitude) * cos(angle);

        vec3 localP = p;
        localP += vec3(offsetX, offsetY, 0.0);
        localP = rotateZ(-angle) * localP;


        float tentacle = opRep(localP);

        if(tentacleRing == -1.0)
        {
            tentacleRing = tentacle;
        }

        tentacle = unionSDF(tentacle, m); //smin(tentacle, m, .1);
        tentacleRing = unionSDF(tentacle, tentacleRing);

    }



    return tentacleRing;//smin2(tentacleRing, m, 4.0);

}


