// Stolen hsl2rgb function from https://www.shadertoy.com/view/lsS3Wc

//========================================================================

vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

vec3 color(in vec2 p)
{
    float pi = 3.141593;
    float theta = atan(p.y,p.x);
    float h = theta/(2.0*pi);
    float s = 0.5;
    float v = 0.6*length(p);
    vec3 hsv = vec3 (h,s,v);
    return hsl2rgb(hsv);
}

vec2 func(in vec3 p)
{
    float x = p.x;
    float y = p.y;
    float t = p.z;
    float f = sin(2.0*sin(0.02*t)*y - 3.0*cos(0.03*t)*x)*exp(-abs (sin(0.11*t)*sin (3.0*x+1.0-2.0*y) - sin(0.19*t)*cos(x-3.0*y+1.0)));
    float g = cos(2.0*sin(0.07*t)*y - 3.0*cos(0.05*t)*x)*exp(-abs (cos(0.13*t)*cos (3.0*x+1.0-2.0*y) - cos(0.17*t)*cos(x-3.0*y+1.0)));
    return vec2(f,g);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //Stretch to be between from -4 to 4
    float h = 4.0;
    uv = vec2(h*2.0)*uv - vec2(h);

    //Scale time by 10
    float t = iTime * 10.0;

    uv = func(vec3(uv,t));

    vec3 col = color(uv);

    fragColor = vec4(col,1.0);
}

// ===========================================
// Times tables and mandelbrot set
// a great video by Mathologer
// https://www.youtube.com/watch?v=qhbuKbxJsk8
// ===========================================
//
// Mandelbrot set and modular times tables relation
//
// in order to match times table with the mandelbrot set
// the circle is rotated by amount of -pi/(N-1) where N is the power.
// notice that "N" is also the "times" value for Times table
//
// the circle has a radius of (7+N)/(10+N)
// this is where the cardioid (for N=2) attaches to secondary circle.
// it holds for all N>=2. (teseted by zooming 100x at attachment positions)
//
// ============================================
// all of this was achieved by experiment. i have no mathematical proof 
// or what so ever that this is correct or not. 
// but it seems to be some how working this way.

const float pi = 3.14159265359;
const float epsilon = 1e-5;

const float scale = 1.5;
const float thickness = 3.0*scale;

// mandelbrot set properties
const int iterations = 100;
const float bailout = 100.0;

// times table properties
const int modular = 200;

// shared properties
const float minPower = 0.0; // minimum power of Z.
const float maxPower = 5.0; // maximum power of Z.
const float duration = 50.0; // transition cycle duration in seconds.


vec2 uvmap(vec2 uv) {
    return (2.0*uv-iResolution.xy)/iResolution.y;
}

vec3 pickColor(float n) {
    return 0.6+0.6*cos(6.3*n+vec3(0,23,21));
}

float smoothout(float dist){
    return smoothstep(thickness/iResolution.y,0.0,dist);
}

float smoothfloor(float x) {
    return x - sin(2.0*pi*x)/(2.0*pi);
}

float clock(){
    float t = 1.-abs(mod(2.*iTime/duration,2.)-1.);
    return (maxPower-minPower)*t + minPower;
}

float circle(vec2 uv, vec2 C, float r, bool fill)
{
    vec2 p = uv-C;
    float fx = length(p)-r;
    float dist = fill? fx:abs(fx);
    return smoothout(dist);
}

float line(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a, ba = b - a; 
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float dist = length(pa - ba * h);
    return smoothout(dist);
}

vec2 function(vec2 z, float n) {
    float r = pow(dot(z, z), n/2.0);
    float th = n*atan(z.y,z.x+epsilon);
    return r*vec2(cos(th), sin(th));
}

vec2 dfunction(vec2 z, vec2 dz, float n){
    // f(f(z))' = f'(f(z))*f'(z)
	vec2 df = n*function(z, n - 1.0);
	return vec2(df.x*dz.x-df.y*dz.y, df.x*dz.y + df.y*dz.x) + vec2(1,0);
}

float mandelbrotDistance(float r, float dr) {
    float dist = r*log(r)/dr;
    return clamp(pow(dist,0.25),0.0,1.0);
}

vec3 mandelbrot(vec2 uv, float n) {
    vec3 set = vec3(0);
    vec2 c = uv;
    vec2 z = c;
    vec2 dz = vec2(1, 0);

    for(int i = 0; i < iterations && dot(z, z) <= bailout; i++) {
	    dz = dfunction(z,dz,n);
	    z = function(z,n) + c;
    }

    float dist = mandelbrotDistance(length(z), length(dz));

    if(dot(z, z) > bailout) set = dist+pickColor(n/5.0)/2.0;

    return clamp(set*0.8,0.0,1.0);
}

vec3 timesTable(vec2 uv, float times)
{
    vec3 col = vec3(0);
    float len = 2.0*pi/float(modular);
    float r = (7.0+times)/(10.0+times); // radius of the circle
    float phase = -pi/(times-1.0);

    col+=circle(uv,vec2(0),r,false);

    for(int i = 0; i < modular; i++) {
        float n = float(i);

        vec2 c = vec2(cos(n*len+phase),sin(n*len+phase))*r;
        vec2 p = vec2(cos(n*len*times+phase),sin(n*len*times+phase))*r;

        col+= circle(uv,c,0.005,true);
        col+= line(uv,c,p)*pickColor(n/float(modular)/3.+iTime/10.0);
    }
    return clamp(col*0.5,0.0,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = uvmap(fragCoord.xy)*scale;

    float time = smoothfloor(clock());

    vec3 color = mandelbrot(uv,time)
               + timesTable(uv,time);

    fragColor = vec4(color,1.0);
}

vec4 colorA = vec4(52./255., 152./255., 219./255., 1.);
vec4 colorB = vec4(241./255., 196./255., 15./255., 1.);

float min_zoom = 3.5f;
float max_zoom = 19.5f;

float bars = 20.f;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy - 0.5;

    float size = (sin(iTime)+1.)*((max_zoom-min_zoom)/2.)+min_zoom;
    float index = round(uv.x*size) + round(uv.y*size);
    float even = max(sign(mod(index, 2.)), 0.);

    fragColor = vec4(even > 0.? colorA : colorB );
}

const vec3 YELLOW = vec3(.9921, .898, .4823);
const vec3 RED = vec3(.5294, .1294, .2862);
const vec3 PINK = vec3(.9764, .7568, .8705);
const vec3 BLACK = vec3(0.);
const vec3 WHITE = vec3(1.);

const vec3 DARK_YELLOW_1 = vec3(.949, .8627, .2313);
const vec3 DARK_YELLOW_2 = vec3(.945, .9058, .6627);
const vec3 LIGHT_YELLOW_1 = vec3(.9921, .9843, .5019);
const vec3 LIGHT_YELLOW_2 = vec3(.9372, .9294, .8313);

#define saturate(x) clamp(x, 0., 1.)

float circle(vec2 uv, vec2 center, float r, float sm)
{
    return 1. - smoothstep(r - sm, r, distance(uv, center));
}

float rectangle(vec2 uv, vec2 center, vec2 size, float sm)
{
    vec2 lb = center - size * .5;
    vec2 rt = center + size * .5;
    vec2 lbRes = smoothstep(lb, lb + sm, uv);
    vec2 rtRes = 1. - smoothstep(rt - sm, rt, uv);
    return lbRes.x * lbRes.y * rtRes.x * rtRes.y;
}

//sidesToCut -> radius percentage [l, b, r, t]
float circleCut(vec2 uv, vec2 center, float r, vec4 sidesToCut, float sm)
{
    float c = circle(uv, center, r, sm);

    vec2 posToCut = center - (1. - sidesToCut.xy) * r;
    vec2 lb = smoothstep(posToCut, posToCut + sm, uv);
    posToCut = center + (1. - sidesToCut.zw) * r;
    vec2 rt = 1. - smoothstep(posToCut - sm, posToCut, uv);    
    return c * lb.x * lb.y * rt.x * rt.y;
}

//range and target -> [minX, minY, maxX, maxY]
vec2 map(vec2 v, vec4 range, vec4 target)
{
    return ((v - range.xy) / (range.zw - range.xy)) * (target.zw - target.xy) + target.xy;
}

vec2 map01(vec2 v, vec4 range) 
{
	return map(v, range, vec4(.0, .0, 1., 1.));
}

vec4 head(vec2 uv)
{
    float outline = circle(uv, vec2(.0), .45, .005);
    float inside = circle(uv, vec2(.0), .425, .005);
    float shadow = circle(uv, vec2(.015, -.015), .49, .12);

    vec3 col = YELLOW * inside;
	return vec4(col, saturate(outline + shadow));
}

vec4 eye(vec2 uv)
{
    uv -= .5;

    float outline = circleCut(uv, vec2(0.), .5, vec4(0., .65, 0., 0.), .012);
    float inside = circleCut(uv, vec2(0.), .424, vec4(0., .73, 0., 0.), .012);
    float pupil = circle(uv, vec2(.27 , .25), .15, .012);

	vec3 col = vec3(inside - pupil);
    return vec4(col, outline);
}

vec4 mouth(vec2 uv)
{
    uv -= .5;
    uv.x += .15 * uv.y ;

    float outline = circleCut(uv, vec2(0.), .5, vec4(0., .0, 0., 1.15), .005);
    float inside = circleCut(uv, vec2(0.), .465, vec4(0., .0, 0., 1.21), .005);
    float upperLip = rectangle(uv, vec2(-.009, -.08), vec2(1.02, .033), .005);
    float tongue = circle(uv, vec2(.12 , -.5), .23, .005);

    vec3 col = RED * inside;
    col = mix(col, PINK, tongue * inside);
    return vec4(col, saturate(outline + upperLip));
}

vec3 background(vec2 uv)
{
    float angle = atan(uv.y, uv.x);
    float dist = length(uv) * 2.;

    vec3 c1 = mix(DARK_YELLOW_2, DARK_YELLOW_1, dist);
    vec3 c2 = mix(LIGHT_YELLOW_2, LIGHT_YELLOW_1, dist);

    float v = cos(iTime + angle * 8.0) * .5 + .5;
    return mix(c1, c2, smoothstep(0.48, 0.52, v));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{    
    vec2 uv = fragCoord / iResolution.xy;
	float ratio = iResolution.x / iResolution.y;

    uv -= .5;
    uv.x *= ratio;

    vec4 h = head(uv);
    vec4 le = eye(map01(uv, vec4(-.207 * ratio, -.08, -.07 * ratio, .25)));
    vec4 re = eye(map01(uv, vec4(.0, -.08, .155 * ratio, .25)));
    vec4 m = mouth(map01(uv, vec4(-.19 * ratio, -.37, .165 * ratio, .35)));

    vec3 col = background(uv);
    col.rgb = mix(col, h.rgb, h.a);
    col.rgb = mix(col, le.rgb, le.a);
    col.rgb = mix(col, re.rgb, re.a);
	col.rgb = mix(col, m.rgb, m.a);

    fragColor = vec4(col, 1.0);
}

// "Polyhedral Menger" by dr2 - 2018
