#define DEGRAD ((aKey > 0.5)? true : false)

#define PI2 6.283185307
#define PI1 3.141592653
#define FL 0.866025404
#define scale 30.0
f1 ci(v2 p){
	return 1.0-length(p);
}
f1 cc(v2 p , f1 s){
    f1 c = ci(p);
	return	smoothstep(-s	, 0.0	, c) -
        	smoothstep(0.0	, s 	, c);	
}
f1 flower( v2 p , f1 s , f1 disp , i1 row , i1 cei){
    // the first loop make the step between the rings
    // the second loop rotates the UV every for segment
    // the third loop draw an circle and move the UV to next positon
    i1 fcount = 1;
    i1 ceile = cei;
    f1 displace = disp;
    f1 angle = (DEGRAD) ? radians(360.0/f1(cei)) : PI2/f1(cei)*sin(iTime*0.1);
    f1 HFL = disp*sin(radians(360.0/f1(cei)));
    f1 f = cc(p,s)+cc(p*10.0,s*10.0);
    p += vec2(0.5,HFL);
    for(i1 i=0; i<row; i++){
        for(i1 k=0; k<cei; k++){
        	for(i1 j=0; j<(ceile/cei); j++){
        	    p.x -= displace;
                fcount += 1;
        	    f = max(f,cc(p,s));
        	}//lklklj
            v2 sc = v2(sin(angle),cos(angle));
        	p *= mat2(sc.y,sc.x,-sc.x,sc.y); 
        }
        ceile += cei;
        p.y += displace*HFL;
        p.x += displace*0.5;
    }
    return f;   
}
