	vec3 col = vec3(0.3)*clamp(1.0-length(q-0.5),0.0,1.0);

	// raymarch
    float t = intersect(ro,rd);

    if( t>0.0 )
    {
        vec3 pos = ro + t*rd;
        vec3 nor = calcNormal(pos);
        vec3 sor = nor;

        vec3 q = mapP( pos );

        // materials
		col = vec3(0.14);
        float ar = clamp(1.0-0.7*length(q-pos),0.0,1.0);
