    vec2 uv = (fragCoord.xy - iResolution.xy * 0.5) / iResolution.y;
	vec3 ro, rd;
	setupCamera(uv, ro, rd);

    Scene scene = drawScene(ro, rd);
	pc = colourScene(ro, rd, scene);    

    pc += vMarch(ro, rd);

    fragColor = vec4(pc*2.0, 1.0);
}

#define PI 3.14159265359
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   	 //sets 0 as the centre of the screen 
	vec2 r = 2.*fragCoord / iResolution.xy -1.;

    //variables
    float x = r.x;
    float y = r.y;
    float Thickness = 0.3;
    float Yoffset = 0.9; 
    float Height = 0.2;

    //Colors
    vec3 red = vec3(1,0,0);
    vec3 white = vec3(1,1,1);
    vec3 blue = vec3(0,0,1);
    vec3 pixelCol = vec3(0, 0, 0);

    if(abs((x * Height) * sin(PI * (x + iTime)) - (y - Yoffset)) < Thickness) pixelCol = red;
    if(abs((x * Height) * sin(PI * (x + iTime)) - (y + Yoffset)) < Thickness) pixelCol = blue;
    if(abs((x * Height) * sin(PI * (x + iTime)) - y) < (Thickness * 2.0)) pixelCol = white;

    // Output to screen
    fragColor = vec4(pixelCol,1.0);
}

/* 
* Inspired By @Candycat https://blog.csdn.net/candycat1992/article/details/44040273
* Using Polar Coordinate To Calculate Distance
*/

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    vec2 p = (2.*fragCoord.xy-iResolution.xy)/min(iResolution.x,iResolution.y);
    p.y -= 0.45;

    vec2 uv = p;
    vec2 st = vec2(atan(uv.x,uv.y),length(uv));//[-pi,pi]

    //heart curve http://mathworld.wolfram.com/HeartCurve.html
    float r = .3*(2.-2.*cos(st.x)+.85*cos(st.x)*sqrt(abs(.55*sin(st.x)))/(cos(st.x)+1.4));

    //background color
    vec3 bCol = vec3(1.,0.8,0.7-.07*p.y)*(1.-.25*length(p));

    //heart color
    vec3 hCol = vec3(1.,.25*length(p),.75*length(p)*.5);

    ///animation
    float tt = mod(iTime,1.5)/1.5;
    float ss = pow(tt,.2)*.5+.5;
    ss=1.+ss*.5*sin(tt*6.283185*3.+p.y*.5)*exp(-tt*4.);
    p*= vec2(0.5,1.5)+ss*vec2(.5,-.5);

    //smoothstep https://en.wikipedia.org/wiki/Smoothstep
    fragColor = vec4(mix(bCol,hCol,smoothstep(-0.01,0.05,r-length(p))),1.);
}

// @lsdlive

mat2 r2d(float a) {
	float c = cos(a), s = sin(a);
	return mat2(c, s, -s, c);
}

void amod(inout vec2 p, float m) {
	float a = mod(atan(p.x, p.y) - m*.5, m) - m*.5;
	p = vec2(cos(a), sin(a)) * length(p);
}

void mo(inout vec2 p, vec2 d) {
	p.y = abs(p.y) - d.x;
	p.x = abs(p.x) - d.y;
	if (p.y > p.x) p.xy = p.yx;
}

float rep(float p, float m) {
	return mod(p - m*.5, m) - m*.5;
}

vec2 rep(vec2 p, float m) {
	return mod(p - m*.5, m) - m*.5;
}

vec3 rep(vec3 p, float m) {
	return mod(p - m*.5, m) - m*.5;
}

float smin(float a, float b, float k) {
	float h = clamp(.5 + .5*(b - a) / k, 0., 1.);
	return mix(b, a, h) - k * h * (1. - h);
}

float cmin(float a, float b, float k) {
	return min(min(a, b), (a - k + b) * sqrt(.5));
}

float stmin(float a, float b, float k, float n) {
	float s = k / n;
	float u = b - k;
	return min(min(a, b), .5 * (u + a + abs((mod(u - a + s, 2. * s)) - s)));
}

float length8(vec2 p) {
	vec2 q = p*p*p*p*p*p*p*p;
	return pow(q.x + q.y, 1. / 8.);
}

float torus88(vec3 p, vec2 d) {
	vec2 q = vec2(length8(p.xz) - d.x, p.y);
	return length8(q) - d.y;
}


float box(vec3 p, vec3 d) {
	return length(max(abs(p) - d, 0.));
}

float g = 0.;
float id = 0.;
float de(vec3 p) {

    float wl = p.x + 1.25;
    float wr = -p.x + 1.25;
    float fl = p.y + 1.;
    float cl = -p.y + 1.;

    vec3 q = p;
    q.x += sin(q.z*.2)*4.;
    q += iTime;
    q.yz += sin(iTime*.2)*4.;
    q = rep(q, 2.);
    float s1 = length(q) - .01 + sin(iTime*30.)*.004;

    p.x += 1.;
    p = rep(p, 2.);

	float d = box(p, vec3(.2, 1., .2));
    d = smin(d, cl +d*.9, .2);
    d = stmin(d, fl, .2, 3.);

    //d = stmin(d, wl+d*.8, .4, 8.);
    //d = stmin(d, wr+d*.8, .4, 8.);
    d = cmin(d, wl+d*.2, .4);
    d = cmin(d, wr+d*.2, .4);

    d = min(s1, d);


    p.z += .4;
    p.y-=.6;
    q=p;
    q.xy*=r2d(3.14/4.);
    float symb = 1e6;
    symb = min(symb, torus88(q.xzy, vec2(.05, .01)));
    p.y+=.07;
    p.xy*=r2d(3.14/4.);
    symb = min(symb, box(p, vec3(.01, .07, .01)));
    p.xy*=r2d(-3.14/2.);
    symb = min(symb, box(p, vec3(.01, .07, .01)));

    d=min(d,symb);

	g += .01 / (.01 + d*d);
	return d;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord / iResolution.xy - .5;
	uv.x *= iResolution.x / iResolution.y;

	vec3 ro = vec3(0, 0, -3. + iTime);
	vec3 rd = normalize(vec3(uv, 1));


	vec3 p;
	float t = 0., ri;
	for (float i = 0.; i < 1.; i += .01) {
		ri = i;
		p = ro + rd*t;
		float d = de(p);
		if (t > 30.) break;
        d = max(abs(d), .001);
        t+=d*.2;
	}

	 vec3 bg = vec3(.2, .1, .2);

    vec3 col = bg;
    if(t<=30.)
	    col = mix(vec3(.2, .2, .4), bg, uv.y*2.+ri);

    //if(p.y < .13 && p.y > -.2) col = mix(vec3(1, 0,0), bg, uv.y+ri);

    //if (id == 1.)
      //  col = vec3(1., 0.,0.);;

	col+=g*.015;
    col = mix(col, bg, 1.-exp(-.09*t*t));

	fragColor = vec4(col, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec4 aqua = vec4(0.3, 0.6, 0.8, 1.0);
    vec4 blue = vec4(0.4, 0.6, 1.0, 1.0);
    vec4 deepPurple = vec4(0.1, 0.2, 0.5, 1.0);

    vec4 water = mix(aqua, deepPurple, (1.0 - uv.y) * 2.0 - 1.5 + sin(iTime + uv.x * 8.0)/6.0);

    float waveHeight = sin(iTime + uv.x * 4.0)/8.0 +0.6 + (sin(-iTime * 2.0 + uv.x * 16.0)/64.0);

    if(uv.y < waveHeight)
		fragColor = water;
    else
        fragColor = blue;
}

vec3 warp(
    in vec3 p,
    out vec3 q,
    int warpsteps,
    float warpfactor,
    int fractalsteps,
    float res,
    float scale,
    float scaleStep,
    vec3 feedback,
    mat4 ds,
    vec4 ss
);
float grid(vec2 p, float size, float width) {
    p = fract(p * size);
    float gx = smoothstep(0.0, width, p.x) -
        smoothstep(1.0 - width, 1.0, p.x);
    float gy = smoothstep(0.0, width, p.y) -
        smoothstep(1.0 - width, 1.0, p.y);
    return gx * gy;
}
float inside(vec2 st, float target, float tolerance){
  return 1.0 - smoothstep( target, target+tolerance / 2.0, st.y);
}
