float rand(float f, float o, float r, vec4 ss) {
  return o + r * fract(sin(f * ss.x + ss.y) * ss.z + ss.z);
}
vec4 rand4(vec4 v, float o, float r, mat4 d, vec4 s) {
    return vec4(
        rand(dot(v, d[0]), o, r, s),
        rand(dot(v, d[1]), o, r, s),
        rand(dot(v, d[2]), o, r, s),
        rand(dot(v, d[3]), o, r, s)
    );
}
vec3 rand3Rot(vec3 v, mat4 ds, vec4 ss) {
  float x = rand(dot(v, ds[1].xyz), 0.0, 2.0 * 3.14159265358, ss);
  float y = rand(dot(v, ds[2].xyz), 0.0, 2.0 * 3.14159265358, ss);
  float z = rand(dot(v, ds[3].xyz), 0.0, 2.0 * 3.14159265358, ss);
    //*
  return mat3(
      1.0, 0.0, 0.0,
      0.0, cos(x), -sin(x),
      0.0, sin(x), cos(x)
  ) * mat3(
      cos(y), 0.0, sin(y),
      0.0, 1.0, 0.0,
      -sin(y), 0.0, cos(y)
  ) * mat3(
      cos(z), -sin(z), 0.0,
      sin(z), cos(z), 0.0,
      0.0, 0.0, 1.0
  ) * vec3(1.0, 0.0, 0.0);
}
float mix1(float x, float y, float a) {
  return (1.0 - a) * x + a * y;
}
float curve3(float a) {
  return a * a * (3.0 - 2.0 * a);
}
float mix3(float x, float y, float a) {
  return mix1(x, y, curve3(a));
}
float perlinGradient(vec3 pos, mat4 ds, vec4 ss) {
  vec3 F = fract(pos);
    vec3 a = floor(pos)
        , h = ceil(pos)
        , b = vec3(a.xy, h.z)
        , c = vec3(a.x, h.y, a.z)
        , d = vec3(a.x, h.yz)
        , e = vec3(h.x, a.yz)
        , f = vec3(h.x, a.y, h.z)
        , g = vec3(h.xy, a.z);
  float nnn = dot(rand3Rot(a, ds, ss), pos - a), nnp = dot(rand3Rot(b, ds, ss), pos - b);
  float npn = dot(rand3Rot(c, ds, ss), pos - c), npp = dot(rand3Rot(d, ds, ss), pos - d);
  float pnn = dot(rand3Rot(e, ds, ss), pos - e), pnp = dot(rand3Rot(f, ds, ss), pos - f);
  float ppn = dot(rand3Rot(g, ds, ss), pos - g), ppp = dot(rand3Rot(h, ds, ss), pos - h);
  float nn = mix3(nnn, nnp, F.z), np = mix3(npn, npp, F.z);
  float pn = mix3(pnn, pnp, F.z), pp = mix3(ppn, ppp, F.z);
  float n = mix3(nn, np, F.y), p = mix3(pn, pp, F.y);
  return mix3(n, p, F.x);
}
float fractalNoise(vec3 p, int steps, float res, float scale, float scaleStep, vec3 feedback, mat4 dotseed,  vec4 sineseed) {
    float value = 0.0;
    vec4 f = sineseed;
    for(int i = 0; i < 10; i++) {
        if(i < steps) {
            f = rand4(f, 12345.6789012, 32109.8765432, dotseed, sineseed);
            value += 3.0 * perlinGradient(feedback * value + vec3(p.xy / res, p.z), dotseed, f) / scale;
            scale = scale * scaleStep;
            res = res / scaleStep;
        }
    }
    return value * 0.5 + 0.5;
}
vec3 warp(
    in vec3 p,
    out vec3 q,
    int warpsteps,
    float warpfactor,
    int fractalsteps,
    float res,
    float scale,
    float scaleStep,
    vec3 feedback,
    mat4 ds,
    vec4 ss
) {
    q.x = -1.0 + 2.0 * fractalNoise( p + vec3(0.0), fractalsteps, res, scale, scaleStep, feedback, ds, ss );
    q.y = -1.0 + 2.0 * fractalNoise( p + vec3(5.2,1.3,2.4), fractalsteps, res, scale, scaleStep, feedback, ds, ss );
    q.z = -1.0 + 2.0 * fractalNoise( p + vec3(1.2,6.5,3.7), fractalsteps, res, scale, scaleStep, feedback, ds, ss );

    vec3 r = vec3(0.0);
      r.x = -1.0 + 2.0 * fractalNoise( p + warpfactor*q + vec3(1.7,9.2,3.1), fractalsteps, res, scale, scaleStep, feedback, ds, ss );
      r.y = -1.0 + 2.0 * fractalNoise( p + warpfactor*q + vec3(8.3,2.8,5.7), fractalsteps, res, scale, scaleStep, feedback, ds, ss );
      r.z = -1.0 + 2.0 * fractalNoise( p + warpfactor*q + vec3(6.1,3.2,9.1), fractalsteps, res, scale, scaleStep, feedback, ds, ss );

    return warpfactor*r;
  }

//Noice function [0,1]
vec2 T = vec2(0.);
float No(float x, vec2 T){
return fract(9627.5*sin(7933.75*(x + 0.5 + T.x) + 297. + T.y));
}

vec4 Rancol(vec2 x){
return vec4(No(x.x + x.y,T), No(x.x*x.x+ x.y,T), No(x.x*x.x + x.y*x.y,T),1.);
}

//squaress
vec4 grid(vec2 uv, float t){
vec4 C1,C2;
uv *= 20.;
vec2 id = vec2(int(uv.x),int(uv.y));
uv.y += (5.*No(id.x*id.x, T) + 1.)*t*.4	;
uv.y += No(id.x, T);
  id = vec2(int(uv.x), int(uv.y));
uv = fract(uv) - .5;

//if (id == vec2(1,10)){C1 = vec4(1.);}

float d = length(uv);
t *= 10.*No(id.x + id.y, T);
//if uv.x += No(id.x);(uv.x > .48 || uv.y > .48){C1 = vec4(1.);}

float r = .1*sin(t + sin(t)*.5)+.3;
  if (abs( uv.x)<r && abs(uv.y) < r){
  C2 = .5*Rancol(id + vec2(1.)) + vec4(.5);
  }
  if (abs(uv.x)>r+.07 || abs(uv.y)>r + .07){
  C2 += vec4(.7,.9,.8,1.);
  }
  return C2 + C1;
  }

