vec2 c = vec2(1.,0.);
float pi = acos(-1.);

float iScale;

float circle(vec2 x, float r)
{
    return length(x)-r;
}

vec2 cnormal(vec2 x, float r)
{
    return c.xx*(1.-length(x)/r);
}

float rand(vec2 a0)
{
    return fract(sin(dot(a0.xy ,vec2(12.9898,78.233)))*43758.5453);
}

float smoothstep_noise(float x)
{
    float r1 = -1.+2.*rand(floor(x)*c.xx), r2 = -1.+2.*rand(ceil(x)*c.xx);
    return mix(r1, r2, smoothstep(.25, .75, fract(x)));
}

float mfsmoothstep_noise(float x, float f0, float f1, float phi)
{
    float sum = 0.;
    float a = 1.;

    for(float f = f0; f<f1; f = f*2.)
    {
        sum = a*smoothstep_noise(f*x) + sum;
        a = a*phi;
    }

    return sum;
}

float smoothstep_noise2d(vec2 x)
{
    vec2 d = floor(x);
    x = fract(x);
    float x00 = -1.+2.*rand(d),
        x01 = -1.+2.*rand(d+c.yx),
        x10 = -1.+2.*rand(d+c.xy), 
        x11 = -1.+2.*rand(d+c.xx);
    return mix(mix(x00, x01, x.y), mix(x10, x11, x.y), x.x);
}

float mfsmoothstep_noise2d(vec2 x, float f0, float f1, float phi)
{
    float sum = 0.;
    float a = 1.;

    for(float f = f0; f<f1; f = f*2.)
    {
        sum = a*smoothstep_noise2d(f*x) + sum;
        a = a*phi;
    }

    return sum;
}

float branch(vec2 x, vec3 whbhs, float s)
{
    x += .03*c.xy*mfsmoothstep_noise(x.y+s, 1.e1, 1.e2, .2);
    vec2 b = vec2(mix(whbhs.y, whbhs.z, clamp(x.y/whbhs.x, -1., 1.)), whbhs.x);
    return length(max(abs(x)-b,0.));
}


vec2 bnormal(vec2 x, vec3 whbhs, float s)
{
    x += .03*c.xy*mfsmoothstep_noise(x.y+s, 1.e1, 1.e2, .2);
    return c.xx*(1.-abs(2.*x.x)/mix(whbhs.y, whbhs.z,clamp(-x.y/whbhs.x, -1., 1.)));
}

vec2 rot(vec2 x, float p)
{
    vec2 d = vec2(cos(p), sin(p));
    return mat2(d.x, d.y, -d.y, d.x)*x;
}

vec4 branch(vec2 x, vec2 f, vec2 t, float w1, float w2)
{
    return c.xxxx;
}

float func(vec2 x, float f)
{
    return x.y-f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord/iResolution.yy-.5-.4*c.xy;

    iScale = 4.*mod(iTime, .2);

    //uv *= 1.+.03*iScale;

    float px = 100.;
    uv = round(px*uv)/px;

    vec2 x = uv+2.e-1*iTime*c.xy, 
        y = vec2(mod(x.x, .4)-.2, x.y), 
        index = x-y;

    vec4 sdf = c.xyyy;

    //trees
    //(a) leaves
    vec2 z = y;
    y += .35*c.yx - .1*mfsmoothstep_noise(x.x-y.x, 8.e0, 20., .6)*c.yx;
    for(int i=0; i<22; ++i)
    {
        float leafsize = .05+.03*rand(float(i)*index);
        vec2 leaf_offset = vec2(-.1+.2*rand(index+float(i)*21.), -.1+.3*rand(index+float(i)*22.));
        vec2 n = cnormal(y-.2*c.yx-leaf_offset, leafsize);
        vec4 sda = vec4(circle(y-.2*c.yx-leaf_offset, leafsize), max(n.x, n.y)*(c.yxy-2.*c.yyx*rand(float(i+10)*c.xx)+1.*c.xyy*rand(float(i+20)*c.xx))+.1*c.yxy*rand(float(i)*c.xx));
        sdf = mix(sdf, sda, step(sda.x, sdf.x));
    }
    //(b) stem
    vec3 stemc1 = vec3(31., 12., 2.)/255., stemc2 = vec3(145., 57., 9.)/255.;
    vec2 n = bnormal(y, vec3(.5, .3, .4), index.x);
    vec4 sda = vec4(branch(y, vec3(.3, .05, .02), index.x), stemc1+stemc2*max(n.x, n.y)-.2*c.xyy);
    sdf = mix(sdf, sda, step(sda.x, sdf.x));

    //night sky
    vec3 bgcol = mix(vec3(0., 96., 255.)/255., vec3(0., 25., 66.)/255., 1.-.5*uv.y);
    //(a) clouds
    bgcol = mix(bgcol, c.xxx, smoothstep(-.2,.2,uv.y)*clamp(1.7*mfsmoothstep_noise2d(uv-2.2e-2*iTime*c.xy, 5., 100., .45+.01*sin(1.3e1*iTime-2.)), 0., 1.));
	bgcol = mix(bgcol, c.xxx, smoothstep(-.2,.2,uv.y)*clamp(1.7*mfsmoothstep_noise2d(uv-.2-2.1e-2*iTime*c.yx, 7., 100., .45+.01*sin(1.5e1*iTime-3.)), 0., 1.));
    bgcol = mix(bgcol, c.xxx, smoothstep(-.2,.2,uv.y)*clamp(1.*mfsmoothstep_noise2d(uv-.2-2.3e-2*iTime*c.xx, 6., 100., .45+.01*sin(1.7e1*iTime-4.)), 0., 1.));
    //(b) mountains
    for(int i=-2; i<2; ++i)
    {
        n = vec2(mfsmoothstep_noise2d((x.x-10.*float(i)+2.e-2*float(i)*iTime)*c.xy+1.2*x.y*c.yx+.2*float(i)*x.yx, 1., 190., .7),
                 mfsmoothstep_noise2d((x.x-10.*float(i))*c.xy+1.2*x.y*c.yx+.2*float(i)*x.yx, 1., 190., .7)); 
        vec4 sdc = vec4(func(z+.1*float(i)*c.yx, -.3+.3*mfsmoothstep_noise(x.x-10.*float(i)+1.e-2*float(i)*iTime, 1., 19., .6)), mix(stemc1+.1,  .5*stemc2-.3, -.3+(2.*length(n))*c.xxx));
    	bgcol = mix(bgcol, sdc.gba, step(sdc.x, 0.));
    }
    fragColor = vec4(mix(bgcol, sdf.gba, step(sdf.x, 0.)),1.);

    //bouncing blob
    vec2 xi = uv+.5*c.yx-.2*abs(sin(2.*pi*iScale/2.))*c.yx;
    n = cnormal(xi, .1);
    vec4 sdd = vec4(circle(xi, .1),  max(n.x, n.y)*c.xxy+.5*c.xyy);
    fragColor = vec4(mix(fragColor.xyz,sdd.gba, step(sdd.x, 0.)),1.);
    sdd = vec4(circle(xi-.025*c.xx, .02), c.xxx);
    fragColor = vec4(mix(fragColor.xyz,sdd.gba, step(sdd.x, 0.)),1.);
    sdd = vec4(circle(xi-.025*c.xx, .01), c.yyy);
    fragColor = vec4(mix(fragColor.xyz,sdd.gba, step(sdd.x, 0.)),1.);
    vec4 sdb = c.xyyy;
    xi += .01*c.xy;
    xi += .01*c.yx;
    sdf = mix(sdd, sdb, step(sdb.x, sdd.x));
    sdd = vec4(circle(xi-.02*c.xy+.02*c.yx,  .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.03*c.xy+.03*c.yx,  .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.035*c.xy+.035*c.yx,.01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.04*c.xy+.04*c.yx,  .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.045*c.xy+.04*c.yx, .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.055*c.xy+.04*c.yx, .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.06*c.xy+.045*c.yx, .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    sdd = vec4(circle(xi-.065*c.xy+.05*c.yx, .01), c.yyy);
    sdf = mix(sdd, sdf, step(sdf.x, sdd.x));
    fragColor = vec4(mix(fragColor.xyz,sdf.gba, step(sdf.x, 0.)),1.);

    //[add text here]
}

