void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,a,d,v, r = 50., n = 3.82, // 24/2pi
          A = 2.38, H = 4.*cos(A/2.); // A=2.1
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),     // ray direction
          p = 30./q, c;                        // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                  // rotation
   //   c = q, c.x+=r, a = atan(c.z,c.x), q.x = length(c.xz)-r, q.z = r*a,// global curvature
        l = length(q.xy), a = atan(q.y,q.x), d = a-q.z,
        d = min(v=abs( mod(d  ,6.28) -3.14), 
                  abs( mod(d-A,6.28) -3.14)),  // double strand (2pi/3)
        t = length(vec3(l-4.,d,fract(n*a)-.5)) - .3,  // spheres along double spring
        d = a-round(q.z*n)/n-A/2. -3.14 +.5/n,
        t = min(t, v=max(l-4.,(length(vec2((fract(q.z*n-.5)-.5)/n, l*cos(d)-H ))-.05)/n)),// rods
        p += t*D;                              // step forward = dist to obj

 // if (cos(a-q.z)<0.) O.gb *= .9; else O.rg *= .9;   // colored strands
    if (t==v) O.rg *= .9; else O.gb *= .9;       // colored rods vs spheres
}

// Circle line segment collision from:
// https://stackoverflow.com/questions/1073336/circle-line-segment-collision-detection-algorithm

struct circle { vec2 pos; float r; };

bool inCircle(vec2 uv, vec2 xy, float r) {
	return length(xy-uv) < r;   
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 R = iResolution.xy;
    vec2 uv = (fragCoord-R/2.)/R.y;
    vec3 col = vec3(0);

    // Light source
    vec2 Lpos = vec2(0., 0.);
    vec2 L = uv - Lpos;

    // Circle
    circle C = circle(vec2(cos(iTime)/3., sin(iTime)/3.), 0.1);

	vec2 E = uv;	 	// Ray start pos
    vec2 d = L;		    // Ray end pos
    vec2 f = C.pos-uv;	// Direction vector of start pos and circle center.

    // Solve y=ax^2+bx+c
    float a = dot(d,d);				// d dot d
    float b = 2. * dot(d, f);		// 2 * d dot f
    float c = dot(f, f) - C.r*C.r;	// f dot f - r^2

    float discr = b*b-4.*a*c; // discr < 0; no intersection
    						  // discr = 0; 1 intersection
    						  // discr > 0; 2 intersections

    // next time: check for t1, t2, and determine real intersection.
    // https://stackoverflow.com/questions/1073336/circle-line-segment-collision-detection-algorithm

	// Background light color
    col = mix(col, vec3(1), 0.20-length(L)/32.);

    // Light source
    col = mix(col, vec3(1), inCircle(uv, Lpos, 0.1) ? 1.-length(uv-Lpos)/.1 : 0.);
    col = mix(col, vec3(1), inCircle(uv, Lpos, 0.05) ? 1.-length(uv-Lpos)/.05 : 0.);
    col = mix(col, vec3(1), inCircle(uv, Lpos, 0.02) ? 1.-length(uv-Lpos)/.03 : 0.);

    // Light
    col = mix(col, vec3(1), .15 * max(0., 1.-length(uv-Lpos)/.80));
    col = mix(col, vec3(1), .15 * max(0., 1.-length(uv-Lpos)/.40));
    col = mix(col, vec3(1), .15 * max(0., 1.-length(uv-Lpos)/.20));    

    // Shadow
    col = mix(col, vec3(0), discr > 0. 
              			 	&& length(Lpos-uv) > length(Lpos-C.pos)
              				&& length(Lpos-uv) > length(C.pos-uv) ? .50 : 0.); 

    // Circle
    col = mix(col, vec3(1), float(inCircle(uv, C.pos, C.r)));
    col = mix(col, vec3(0), float(inCircle(uv, C.pos, C.r-0.005)));

    // Output to screen
    fragColor = vec4(col,1.0);
}

