distPoint unionP(distPoint dp1, distPoint dp2){
    if(dp1.dist > dp2.dist) return dp2; else return dp1;    
}

distPoint intersectP(distPoint dp1, distPoint dp2){
    if(dp1.dist < dp2.dist) return dp2; else return dp1;    
}

distPoint subtractP(distPoint dp1, distPoint dp2){
	if(dp1.dist < -1.*dp2.dist) return dp2; else return dp1;
}

vec4 saturate4(vec4 color){
	color = vec4(
    	min(color.x,1.),
        min(color.y,1.),
        min(color.z,1.),
        min(color.a,1.)
    );

    return vec4(
        max(color.x,0.),
        max(color.y,0.),
        max(color.y,0.),
        max(color.a,0.)   
    );

}

