vec3 normScene(vec3 p){
    return normalize(vec3(
        distScene(vec3(p.x + EPSILON, p.y, p.z)).dist - distScene(vec3(p.x - EPSILON, p.y, p.z)).dist,
        distScene(vec3(p.x, p.y + EPSILON, p.z)).dist - distScene(vec3(p.x, p.y - EPSILON, p.z)).dist,
        distScene(vec3(p.x, p.y, p.z  + EPSILON)).dist - distScene(vec3(p.x, p.y, p.z - EPSILON)).dist
    ));
}

vec4 colorScene(vec3 p, vec3 eyePos, light[2] lights){
