vec4 march(in vec2 uv){
	vec3 camPos = vec3(0,0,-5);

    vec3 lightPos = vec3(-5,-5,30);//40.+30.*sin(iTime));
    vec3 lightPos2 = vec3(5,5,30);//40.-30.*sin(iTime*2.));
    light lights[2];
    lights[0] = light(lightPos,vec3(0,0,0),.25*vec3(1,1,1));
    lights[1] = light(lightPos2,vec3(0,0,0),.25*vec3(1,1,1));

    float viewPortDistance = 2.0;

    vec3 viewRay = normalize(vec3(uv,viewPortDistance));

    //march

    float dist = 1.0;
    vec3 p = camPos + viewRay * dist;
    for (int i = 0; i < marchSteps; i++){

        dist = distScene(p).dist;
        if (dist < EPSILON){
        	return colorScene(p,camPos, lights);
        }
        p += dist*viewRay; 
    }

    //sonst tunnel
    uv = uv + vec2(0.2*cos(iTime*1.1),0.2*sin(iTime));
    return tunnel(uv);   
}

