    vec2 uv = fragCoord/iResolution.xy.x-vec2(0.5,iResolution.xy.y/iResolution.xy.x/2.0);
	fragColor = march(uv);     
}

#define PI 3.14159265358979323846
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 rotate2D(vec2 _st, float _angle){
    // _st -= 0.5;
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    // _st += 0.5;
    return _st;
}

vec2 tile(vec2 _st, float _zoom){
    _st = _st*.5+.5;
    _st *= _zoom;
    return fract(_st);
}

float box(vec2 _st, vec2 _size, float _smoothEdges){
    _size = -_size*.5;
    vec2 aa = vec2(_smoothEdges*0.5);
    vec2 uv = smoothstep(_size,_size+aa,_st);
    uv *= smoothstep(_size,_size+aa,-_st);
    return uv.x*uv.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = (fragCoord.xy*2.-iResolution.xy)/iResolution.x;
    vec3 c = hsb2rgb(vec3(.06, .86,1.));

    float r = length(st)*1.;
    float s = pow(abs(cos(r*2.-iTime*3.)),9.)*.1;
    st *= 1.-s;
    st = tile(st,25.);
    st = st*2.-1.;

    vec3 color = c;
    st = rotate2D(st,PI*0.25);

    color *= vec3(box(st,vec2(.6),0.1));
    st = rotate2D(st,PI*.75);
    float w = .2;
    color += (c-color)*vec3(box(st,vec2(w, 2.1),.1));
    st = rotate2D(st,PI*.5);
    color += (c-color)*vec3(box(st,vec2(w, 2.1),.1));
    color += s*color*15.;

    fragColor = vec4(color,1.0);
}

#define T(x) mat2(  cos(x+vec4(0,-pi,pi,0)/4.) )
#define N(v,m,t)  ( round ( (v)*(m)+t ) - (t) ) / (m)
#define R iResolution
float pi = 6.283;
void mainImage( out vec4 O, in vec2 I )
{
    float r = -iTime*8.;
    vec2 p = (I+I-R.xy)/R.y*T(r);


    float n = length(p),
     t = N( atan(p.y,p.x) +r , 80./pi ,0. ) -r, 
     d =  N( n, 14.+n*1.6, t/pi);

    O = vec4(1,0,.8,0) * 
        (1.-(
            (1.2*(pow(dot(pow(abs(
                (p-d*cos(t-vec2(0,pi/4.)))
                *T(t+pi/4.)*vec2(d,.6)*T(pi/8.+pi/2e2)
            ),vec2(1.2)),vec2(1)),1./1.2))
            -.02*n)*R.y/4.
            ));
}

