#define PI 3.14
#define PI2 6.28

#define HX vec2(1., 0.)
#define HY vec2(.5, 0.866)
#define HMAT mat2(HX, HY)
#define HSCALE 0.05

//// pixelToCubial
//
//	converts a pixel coordinate to a hex grid cubial coordinates

void pixelToCubial(vec2 uv, out vec3 cubial, out vec3 grid) {

    vec2 axial = inverse(HMAT) * uv;
    cubial = vec3(axial, -axial.x - axial.y) / HSCALE;
    grid = round(cubial);
    vec3 dist = abs(cubial - grid);
    if (dist.x > dist.y && dist.x > dist.z) {

        grid.x = -grid.y - grid.z;
    }
    else if (dist.y > dist.z) {

        grid.y = -grid.x - grid.z;
    }
    else {

        grid.z = -grid.x - grid.y;
    }
}

//// cubialDistance
//
//	finds the distance between 2 hex cell positions

float cubialDistance(vec3 a, vec3 b) {

    vec3 c = abs(a - b);
    return max(c.x, max(c.y, c.z));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord - iResolution.xy * .5) / iResolution.y;

    // find cubial coordinates

    vec3 cubial;
    vec3 grid;
    vec3 mouse;
    pixelToCubial((iMouse.xy - iResolution.xy * .5) / iResolution.y, cubial, mouse);
    pixelToCubial(uv, cubial, grid);


    // get per tile coordinates

    vec3 coord = cubial - grid;
    vec3 dist = abs(coord.xyz - coord.zxy);

    vec3 col = cos(grid + iTime) * .5 + .5;

    // outline

    col *= smoothstep(.2, .2 + (2.0 / iResolution.y) / HSCALE, 1.0 - max(dist.x, max(dist.y, dist.z)));

    // react to mouse input

    float mouseDist;
    if (iMouse.z > .0 && iMouse.w > .0 && (mouseDist = cubialDistance(grid, mouse)) < 4.) {

        float mouseDist = cubialDistance(grid, mouse);
    	col = mix(col, vec3(1.), max(.0, sin(mouseDist - iTime * 4.) * .5 + .5));
    }

    fragColor = vec4(col, 1.);
}

vec3 f(vec2 u, float i)
{
    vec2 a = floor(u*.25)+i;
    vec2 c = vec2(1.,1.123);
    vec3 d = vec3(dot(a,c), dot(a+1.1,c), dot(a+2.1,c));
    d = fract(sin(d)*1111.);
    vec2 b = mod(u,vec2(4.))-1.-(sin(d.yz*iTime*2.)+1.);
    return (fract(sin(d+5.22)*1111.)*.5+.25)/dot(b,b);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 u = 32.*fragCoord/iResolution.x+iTime*.5;
    vec3 l = f(u,0.);
    l = max(l, f(u+vec2(2.,0.),.11));
    l = max(l, f(u+vec2(0.,2.),.22));
    l = max(l, f(u+2.         ,.33));
    l = min(l, f(u+1.         ,.44));

    fragColor = vec4(l,1.);
}

// variant of https://shadertoy.com/view/4sdBW8

void mainImage(out vec4 O, vec2 U) {

    float t = iTime,l,a,d;
    mat2  R = mat2( sin(t+vec4(0,33,11,0)) );
    vec3  q = iResolution,
          D = vec3(.3*(U+U-q.xy)/q.y, -1),    // ray direction
          p = 30./q;                          // marching point along ray 
    O-=O;
    for ( O++; O.x > 0. && t > .01 ; O-=.015 )
        q = p,
        q.xz *= R, q.yz *= R,                 // rotation
        l = length(q.xy), a = atan(q.y,q.x), d = sin(a-q.z), // sin: double
                               //  d = mod(a-q.z,6.28)-3.14, // mod: single
        t = length(vec3(l-4.,d,fract(4.*a)-.5)) - .5,  // spheres along double spring
        p += t*D;                             // step forward = dist to obj

  // if (cos(a-q.z)<0.) O.gb *= .8; else O.rg *= .8; // 2 colored strands
}

