#define USE_WHITE 0 // Time varying pixel color
#define AVOID_DISTORSIONS 1

float dot2( in vec3 v ) { return dot(v,v); }
float udTriangle( vec3 p, vec3 a, vec3 b, vec3 c )
{
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 ac = a - c; vec3 pc = p - c;
    vec3 nor = cross( ba, ac );

    return sqrt(
    (sign(dot(cross(ba,nor),pa)) +
     sign(dot(cross(cb,nor),pb)) +
     sign(dot(cross(ac,nor),pc))<2.0)
     ?
     min( min(
     dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
     dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
     dot2(ac*clamp(dot(ac,pc)/dot2(ac),0.0,1.0)-pc) )
     :
     dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

float circle( vec2 p, vec2 c, float radius) 
{

    return sqrt(pow(p.x-c.x, 2.) + pow(p.y-c.y, 2.)) - radius;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
#if AVOID_DISTORSIONS
    vec2 uv = fragCoord/iResolution.y;
   	uv.x += (iResolution.y - iResolution.x) 
        / max(iResolution.x,iResolution.y) ;
#else    
    vec2 uv = fragCoord/iResolution.xy;
#endif    


#if USE_WHITE
    vec3 col = vec3(1.);
#else
    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
#endif

    // Geometry
    float d0 = udTriangle(vec3(uv.xy, 0), vec3(.5,0.9,0), vec3(.2,.25,0), vec3(0.8,.25,0));
   	float d1 = udTriangle(vec3(uv.xy, 0), vec3(.5,0.1,0), vec3(.8,.7,0), vec3(0.2,.7,0));
    float s0 = circle(uv.xy, vec2(0.2, 0.7), 0.1 );
    float s1 = circle(uv.xy, vec2(0.8, 0.7), 0.1 );
    float s2 = circle(uv.xy, vec2(0.5, 0.1), 0.1 );

    // Scene
    if( d0 > 0. && d0 < 0.01)
    	fragColor = vec4(vec3(0),1.0);
    else
        fragColor = vec4(col,1.0);

    if(s0 <= 0. || s1 <= 0. || s2 <= 0. )
        fragColor = vec4(vec3(0),1.0);

    if(d1 == 0.)
        fragColor = vec4(col,1.0); 
}

