#define LAMB 0
#define METAL 1
#define DIEL 2
#define EMISSIVE 3

#define NUM_OBJECTS 4

#define PI  3.14159265359
#define PI2 6.28318530717

#define time iTime * 0.7

struct Material
{
	int type;
    vec3 albedo;

    // value corresponds to a material. 
    //
    // Roughness for metal.
    // Refract index for dielectrics.
    // Color multiplier for current fake emission mat.
    float v; 
};

vec3 hash33(vec3 p3)
{
	p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+19.19);
    return fract(vec3((p3.x + p3.y)*p3.z, (p3.x+p3.z)*p3.y, (p3.y+p3.z)*p3.x));
}

// Spheres on scene declaration.
Material materials[NUM_OBJECTS];

float udRoundBox( vec3 p, vec3 b, float r )
{
  return length(max(abs(p)-b,0.0))-r;
}

float opRep( vec3 p, vec3 c )
{
    vec3 q = mod(p, c) - 0.5 * c;
    return udRoundBox( q , vec3(0.15, 0.15, 0.15), 0.05);
}

vec2 map( in vec3 pos)
{   
    vec2 res = vec2(opRep(pos - vec3(0.0, 1.0, 2.0), vec3(1.1, 1.1, 1.1)), 1.0);

    return res;
}

vec2 castRay( in vec3 ro, in vec3 rd)
{
    float tmin = 1.0;
    float tmax = 25.0;

    float t = tmin;
    float m = -1.0;

    for( int i = 0; i < 128; i++ )
    {
	    float precis = 0.001 * t;
	    vec2 res = map( ro + rd * t);

        if( res.x < precis || t > tmax ) break;

        t += res.x;
	    m = res.y;
    }

    if( t > tmax ) m = - 1.0;

    return vec2( t, m );
}

vec3 calcNormal( in vec3 pos )
{
    vec2 e = vec2(1.0, -1.0) * 0.5773 * 0.0005;
    return normalize( e.xyy*map( pos + e.xyy ).x + 
					  e.yyx*map( pos + e.yyx ).x + 
					  e.yxy*map( pos + e.yxy ).x + 
					  e.xxx*map( pos + e.xxx ).x );
}

vec3 render(in vec3 ro, in vec3 rd)
{
    vec3 col = vec3(0.7, 0.1, 1.0) - rd.y * 2.2;

    vec2 res = castRay(ro, rd);
    float t = res.x;
	float m = res.y;


    if (m == 1.0)
    {     
        col = materials[0].albedo;
        vec3 pos = ro + t * rd;
    	vec3 nor = calcNormal( pos );

    	vec3  light = normalize( vec3(0.5, -0.5, -0.8) );
    	float dif = clamp( dot( nor, light ), 0.0, 1.0 );
    	col = col * dif;
    }

    return col * 2.;
}

void SetMaterials()
{
    materials[0] = Material(LAMB, vec3(0.0, 0.43, 0.82), 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    SetMaterials();

    vec2 uv = (fragCoord - .5 * iResolution.xy ) / iResolution.y;

    vec3 ro = vec3(4.0 * sin(time * 0.1), 7.0 * cos(time * 0.1), 1.0 + time);

    // 'Look at' vector.
    vec3 la = vec3(-1., .25, 1.) + vec3(.5 + cos(time * .7), 0.5 * cos(time), .5 + sin(time * .5));

    // Uncomment for mouse look.
    //la = vec3(1., .25, 1.0) + vec3(-0.5 + iMouse.x / 50., -0.5 + iMouse.y / 50., 0.5);

    vec3 forward = normalize(la);
    vec3 right = normalize(vec3(forward.z, 0., -forward.x )); 
    vec3 up = cross(forward, right);

    // rd - Final ray direction.
    vec3 rd = normalize(forward + uv.x * right + uv.y * up);

    // render	
    vec3 col = render( ro, rd );

	// gamma
    col = pow( col, vec3(0.4545) );

    // Output to screen
    fragColor = vec4(col, 1.0);
}

float musB(float p){return texture(iChannel0,vec2(p,0.0)).x;}

//perlin noise from here https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
vec4 permute(vec4 x){return mod(((x*34.0)+1.0)*x, 289.0);}
vec3 fade(vec3 t) {return t*t*t*(t*(t*6.0-15.0)+10.0);}
float seed=42.0;

float noise(vec3 P){
  vec3 Pi0 = floor(P);
  vec3 Pi1 = mod(Pi0+1.0, seed);
       Pi0 = mod(Pi0, seed);
  vec3 Pf0 = fract(P);
  vec3 Pf1 = Pf0 - vec3(1.0);
  vec4 ix = vec4(Pi0.x, Pi1.x, Pi0.x, Pi1.x);
  vec4 iy = vec4(Pi0.yy, Pi1.yy);
  vec4 iz0 = Pi0.zzzz;
  vec4 iz1 = Pi1.zzzz;

  vec4 ixy = permute(permute(ix) + iy);
  vec4 ixy0 = permute(ixy + iz0);
  vec4 ixy1 = permute(ixy + iz1);

  vec4 gx0 = ixy0 / 7.0;
  vec4 gy0 = fract(floor(gx0) / 7.0) - .5;
  gx0 = fract(gx0);
  vec4 gz0 = vec4(.5) - abs(gx0) - abs(gy0);
  vec4 sz0 = step(gz0, vec4(.0));
  gx0 -= sz0 * (step(.0, gx0) - .5);
  gy0 -= sz0 * (step(.0, gy0) - .5);

  vec4 gx1 = ixy1 / 7.0;
  vec4 gy1 = fract(floor(gx1) / 7.0) - .5;
  gx1 = fract(gx1);
  vec4 gz1 = vec4(.5) - abs(gx1) - abs(gy1);
  vec4 sz1 = step(gz1, vec4(.0));
  gx1 -= sz1 * (step(.0, gx1) - .5);
  gy1 -= sz1 * (step(.0, gy1) - .5);

  vec3 g000 = vec3(gx0.x,gy0.x,gz0.x);
  vec3 g100 = vec3(gx0.y,gy0.y,gz0.y);
  vec3 g010 = vec3(gx0.z,gy0.z,gz0.z);
  vec3 g110 = vec3(gx0.w,gy0.w,gz0.w);
  vec3 g001 = vec3(gx1.x,gy1.x,gz1.x);
  vec3 g101 = vec3(gx1.y,gy1.y,gz1.y);
  vec3 g011 = vec3(gx1.z,gy1.z,gz1.z);
  vec3 g111 = vec3(gx1.w,gy1.w,gz1.w);

  vec4 norm0 = inversesqrt(vec4(dot(g000, g000), dot(g010, g010), dot(g100, g100), dot(g110, g110)));
  g000 *= norm0.x;
  g010 *= norm0.y;
  g100 *= norm0.z;
  g110 *= norm0.w;
  vec4 norm1 = inversesqrt(vec4(dot(g001, g001), dot(g011, g011), dot(g101, g101), dot(g111, g111)));
  g001 *= norm1.x;
  g011 *= norm1.y;
  g101 *= norm1.z;
  g111 *= norm1.w;

  float n000 = dot(g000, Pf0);
  float n100 = dot(g100, vec3(Pf1.x, Pf0.yz));
  float n010 = dot(g010, vec3(Pf0.x, Pf1.y, Pf0.z));
  float n110 = dot(g110, vec3(Pf1.xy, Pf0.z));
  float n001 = dot(g001, vec3(Pf0.xy, Pf1.z));
  float n101 = dot(g101, vec3(Pf1.x, Pf0.y, Pf1.z));
  float n011 = dot(g011, vec3(Pf0.x, Pf1.yz));
  float n111 = dot(g111, Pf1);

  vec3 fade_xyz = fade(Pf0);
  vec4 n_z = mix(vec4(n000, n100, n010, n110), vec4(n001, n101, n011, n111), fade_xyz.z);
  vec2 n_yz = mix(n_z.xy, n_z.zw, fade_xyz.y);
  float n_xyz = mix(n_yz.x, n_yz.y, fade_xyz.x); 
  return 2. * n_xyz;
}

vec4 castRay(in vec3 eye,in vec3 ray,in float mDis) {
    vec3 pos=eye+ray;
    float t=.0;
    float accamulate=.0;
    vec3 pos2=eye+ray;
    vec4 color;
    float dis=length(eye-pos);
    while(true){
        dis=length(eye-pos);
        if(dis>mDis){
            break;
        }
        if(accamulate>.5){
            return color;
        }
        float nois=noise((pos/1.7)-(vec3(.0,.0,1.0-musB(dis/6.0)*2.0)));
        float fd=dis/mDis;
        color=vec4(musB(dis/6.0)/1.5,
                   fd-nois,
                   dis,
                   dis);
        accamulate+=nois/8.0;
        t+=.1;
        pos=eye+ray*t;
    }

	return color;
}

mat2 rot2(float a){
    return mat2(cos(a),-sin(a),sin(a),cos(a));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=(2.0*fragCoord.xy-iResolution.xy)/iResolution.y;

    vec3 r1=vec3(uv.x,uv.y,-1.0);
    vec3 r=r1;r.xz*=rot2(0.25-sin(iTime*.3)/4.0);r.yz*=rot2(0.25-sin(iTime*.3)/8.0);

    vec3 o=vec3(0,0,iTime*1.0);

    float dis=8.0;
    vec4 t1=castRay(o+r*3.0, r, dis);

    float fog=(t1.w/dis);
    vec3 fogColor=1.0-vec3(musB(dis/16.0),musB(dis/8.0),musB(dis/4.0))/4.0;
    fragColor=vec4(mix(t1.xyz,fogColor,fog),1.0);
}

#define PI 3.14159265359

vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}

vec2 rotate2D (vec2 _st, float _angle) {
    _st =  mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle)) * _st;
    return _st;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (2.0*fragCoord.xy-iResolution.xy)/iResolution.x;
    p *= 1.6;
    vec3 color = hsb2rgb(vec3(fract(iTime*.1),1.,1.));
    float r = length(p);
    float w = .3;
    p = rotate2D(p,(r*PI*6.-iTime*2.));
    color *= smoothstep(-w,.0,p.x)*smoothstep(w,.0,p.x);
    color *= abs(1./(sin(pow(r,2.)*2.-iTime*1.3)*6.))*.4;
	fragColor = vec4(color, 1.);
}

float circle(in vec2 st, in float radius, in float edge){
    vec2 dist = st - vec2(0.5);
    return 1.0 - smoothstep(radius - (radius * edge),
                         radius + (radius * edge),
                         dot(dist , dist) * 4.0);
}

float line(float center, float size, float edge, float y) {
	return max(
        max(
        	smoothstep(center - size - edge, center - size, y) *
            smoothstep(center + size + edge, center + size, y),
        	smoothstep(center + size + edge - 1.0, center + size - 1.0, y)
        ),
        smoothstep(center - size + 1.0 - edge, center - size + 1.0, y)
    );
}

vec3 bottomGrid(in vec2 st, in vec3 col) {
    vec2 lines = vec2(10.0, 20.0);
    float activeVLines = 5.0;
    float maxVlines = 40.0;
    vec2 shift = vec2(mix(lines.x, maxVlines, st.y), lines.y);

    vec2 suv = vec2((st.x * shift.x) - (shift.x * 0.5), st.y * shift.y);
    vec2 fuv = fract(suv);
    vec2 iuv = floor(suv);

    // black
    col *= step(activeVLines, suv.y);

    // glow lines
    vec3 glowCol = vec3(0.3, 1.0, 0.3);
    float time = 1.0 - fract(iTime * 0.6);

    float gvLine = line(0.0, 0.04, 0.08, fuv.x);
    float ghLine = max(
        line(time, 0.12, 0.24, fuv.y),
        line(0.0, 0.12, 0.12, fuv.y) * step(activeVLines - 0.16, suv.y)
    );

    col = mix(col, glowCol, max(ghLine, gvLine) * step(suv.y, activeVLines + .16) * 0.3);

    // lines
    vec3 lineCol = vec3(1.0, 1.0, 1.0);

    float vLine = line(0.0, 0.0025, 0.03, fuv.x);
    float hLine = max(
        line(time, 0.015, 0.06, fuv.y),
        line(0.0, 0.03, 0.03, fuv.y) * step(activeVLines - 0.04, suv.y)
    );

    col = mix(col, lineCol, max(hLine, vLine) * step(suv.y, activeVLines + .04));
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float scaleX = iResolution.x / iResolution.y;
    vec2 sunUV = vec2((uv.x * scaleX) - ((scaleX - 1.0) * 0.5), uv.y);
    vec2 texelSize = vec2(1.0 / iResolution.x, 1.0 / iResolution.y);


    float sunPct = circle(sunUV, 0.5, 0.01);

    // background
    fragColor += (1.0 - fragColor.r) * vec4(0.2, 0.129, 0.286, 1);

    // stars
    fragColor.rgb += vec3(max(90.* fract(dot(sin(fragCoord),fragCoord))-89.5, 0.0));
    fragColor.rgb += vec3(max(70.* fract(dot(cos(fragCoord),fragCoord))-69.7, 0.0));

    // sun haze
    float sunHazePct = circle(sunUV, 0.57, 0.2) * 0.35;
    fragColor.rgb = ((1.0 - sunHazePct) * fragColor.rgb) + (vec3(0.909, 0.167, 0.596) * sunHazePct);

    // sun color
    // get a 0 -> 1 value within our sun
    float sunValue = smoothstep(0.3, 0.63, uv.y);
    // line size increases the closer we are to the bottom of the sun
    float lineSize = floor(mix(80.0, 0.0, sunValue)) * texelSize.y;
    // lerp between our sun colors to get a gradient
    vec3 sunColor = mix(vec3(0.909, 0.167, 0.596), vec3(1, 0.913, 0.305), sunValue);

    // line speed ( 1 - to make it go down)
    float lineInt = 1.0 - fract(iTime * 0.3);

    // line count
    float lineY = fract(uv.y * 14.0);

    // select our lines, invert so that active lines = 0
    float lines = 1.0 - line(lineInt, lineSize, 0.05, lineY);

    // cutoff the top lines
    lines = max(lines, step(0.6, uv.y));

    // cancel out sun on lines
    sunPct *= lines;

    // add main circle
    fragColor.rgb = ((1.0 - sunPct) * fragColor.rgb) + (sunColor * sunPct);

    fragColor.rgb = bottomGrid(uv, fragColor.rgb);
}

vec3 gray = vec3(.5,.5,.5);

float random (in vec2 uv) {
    return fract(sin(dot(uv.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);   
}

float rect(vec2 uv, vec2 pos, vec2 size)
{
	pos -= size/2.;
    float value = step(pos.x,uv.x);
    value = (step(pos.x,uv.x) - step(pos.x+size.x,uv.x)); 
    value *= (step(pos.y, uv.y) - step(pos.y+size.y,uv.y));

    return value;
}

float circle(vec2 uv,float radius, vec2 shapePos, float width)
{

    float ratio = iResolution.x / iResolution.y;

    float dist = distance(shapePos,uv) * 3.0;
    float value =step(radius - width,dist) - step(radius, dist);

    shapePos.x *= ratio;

    return value; 
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
	float ratio = iResolution.x / iResolution.y;

    vec2 size;
    vec2 pos;
    float value;
    vec3 color;
    float radius,width;
    vec2 shapepos;

 	color = mix(gray,color,value);

    //cross
    pos = vec2(.5 ,.5);
    size = vec2(.02,1.);
    value = 1.- rect(uv, pos,size);    
    color = mix(vec3(0,0,0),color,value);    

    pos = vec2(.5 ,.5);
    size = vec2(1,.03);
    value = 1.-rect(uv, pos,size);    
