void mainImage( out vec4 O, vec2 U )
{
    U *= 4./iResolution.y;
    U.x += iTime;                                     // for demo
 // O = vec4( 1.-voronoiB(U).x,voronoi(U).x, 0,0 );   // for tests
    vec2 I = floor(U/2.); 
    bool vert = mod(I.x+I.y,2.)==0.; //if (vert) U = U.yx;
    vec3 H0;
    O-=O;

    for(float i=0.; i<CRACK_depth ; i++) {
        vec2 V =  U / vec2(RATIO,1),                  // voronoi cell shape
             D = CRACK_zebra_amp * fbm22(U/CRACK_zebra_scale) * CRACK_zebra_scale;
        vec3  H = voronoiB( V + D ); if (i==0.) H0=H;
        float d = H.x;                                // distance to cracks
   /*         r = voronoi(V).x,                       // distance to center
              s = STONE_height-STONE_slope*pow(r,profile);// stone interior
    */                                                // cracks
        d = min( 1., CRACK_slope * pow(max(0.,d-CRACK_width),CRACK_profile) );

        O += vec4(1.-d) / exp2(i);
        U *= 1.5 * rot(.37);
    }
    /*
    O = vec4( 
        FAULT==1 ? d * s                              // fault * stone
                 : mix(1.,s, d)                       // crest or stone
            ); */
    if (vert) O = 1.-O; O *= vec4(.9,.85,.85,1);      // for demo

#if MM
    O.g = hash3(uvec3(H0.yz,1)).x;
#endif
}

const float PI = 3.141519;
const float TWO_PI = PI * 2.0;

vec3 rgb2hsb( in vec3 c ){
    vec4 K = vec4(0.0, -1.0 / 3.0, 2.0 / 3.0, -1.0);
    vec4 p = mix(vec4(c.bg, K.wz),
                 vec4(c.gb, K.xy),
                 step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r),
                 vec4(c.r, p.yzx),
                 step(p.x, c.r));
    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6.0 * d + e)),
                d / (q.x + e),
                q.x);
}

//  Function from Iñigo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix(vec3(1.0), rgb, c.y);
}


vec2 rotate2D(float angle, vec2 uv, vec2 pivot)
{
    vec2 newUv = uv - pivot;

    newUv = mat2(cos(angle), -sin(angle), 
                sin(angle), cos(angle)) * newUv;
    return newUv + pivot;
}

vec2 scale(vec2 scale, vec2 uv, vec2 pivot)
{
	vec2 newUv = uv - pivot;
    newUv = mat2(scale.x, 0.0,
                0.0, scale.y) * newUv;
    return newUv + pivot;
}

float fetchFft(int start, int end, float power)
{
    // first row is frequency data (48Khz/4 in 512 texels, meaning 23 Hz per texel)
   	float fft = 0.0;
    for(int i = start; i <= end; ++i)
         fft += texelFetch(iChannel0, ivec2(i, 0), 0).x;
    return pow(fft / float(end - start + 1), power);
}

float bass()
{
    return fetchFft(2, 5, 30.0);
}

float deepBass()
{
    return fetchFft(0, 2, 5.0);
}

float high()
{
    return fetchFft(250, 350, 1.0);
}

float voronoi(vec2 uv, vec2 size)
{
    vec2 iPos = floor(uv);
    //vec2 fPos = fract(uv);

    float minDist = 2.0;
    vec2 closestPoint = vec2(0.0);

    for(int x = -1; x <= 1; ++x)
        for(int y = -1; y <= 1; ++y)
        {
            vec2 iNeighbour = iPos + vec2(x, y);  
            vec2 neighbourPoint;

            neighbourPoint.x = abs(sin(0.6 * iTime + distance(size/2.0, uv)));
            neighbourPoint.y = abs(sin(0.6 * iTime + 0.5 * distance(size/2.0, uv)));

            const float HIGH_K = 0.6;
            neighbourPoint.x += HIGH_K * (high() - 0.5);
            neighbourPoint.y += HIGH_K * (high() - 0.5);

            float dist = distance(uv, iNeighbour + neighbourPoint);           
            if(dist < minDist)
            {
                minDist = dist;
                closestPoint = iNeighbour + neighbourPoint;
            }            
        }

    return minDist + (1.0 - smoothstep(0.07, 0.15, distance(uv, closestPoint)));
    //return smoothstep(0.1, 0.8, minDist) + (1.0 - smoothstep(0.04, 0.11, distance(uv, closestPoint)));
}

float borderLight(vec2 uv, vec2 size)
{
    float d = distance(uv, size * 0.5);
    return smoothstep(0.7, 1.0, d);
}

float centerLight(vec2 uv, vec2 size)
{
    float d = distance(uv, size * 0.5);
    return 1.0 - smoothstep(0.0, 0.6, d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    float ratio = iResolution.x / iResolution.y;
    uv.x *= ratio;

    vec2 center = vec2(0.5) * vec2(ratio, 1.0);
    uv = rotate2D(pow(sin(0.013 * deepBass() + iTime * 0.15), 6.0) * TWO_PI, uv, center);

    vec2 scaledUv = scale(1.6 + 0.6 * sin(vec2(0.3 * iTime + deepBass() * 0.5)), uv, center);

    const vec2 TILES = vec2(15.0);
    scaledUv *= TILES;

    vec2 size = TILES * vec2(ratio, 1.0);

    const float SHADOW_DEPTH = 0.08;
    vec2 shadowDisp = vec2(sin(iTime), cos(iTime)) * SHADOW_DEPTH;

    float v1 = voronoi(scaledUv, size);
    float v2 = voronoi(scaledUv + shadowDisp, size);

    vec3 G = rgb2hsb(vec3(0.0, 255.0, 39.0) / vec3(255.0));
    vec3 B = rgb2hsb(vec3(10.0, 25.0, 255.0) / vec3(255.0));
    vec3 W = rgb2hsb(vec3(1.0));
    vec3 Y = rgb2hsb(vec3(204.0, 115.0, 20.0) / vec3(255.0));

    vec3 mix1 = mix(G, Y, 0.5 + 0.5 * sin(0.5 * iTime - 6.0 * distance(uv, center)));
    vec3 mix2 = mix(Y, B, 0.5 + 0.5 * sin(0.7 * iTime - 8.0 * distance(uv, center)));
    vec3 finalMix = mix(mix1, mix2, 0.5 + 0.5 * sin(iTime * 0.5));

    vec3 col = vec3(0.0);    
	col = mix(finalMix, col, pow(v1, 0.4 + bass()));
    col = mix(col, W, v2 * v2);

    const float HIGHLIGHT_K = 0.35;
    col.z += HIGHLIGHT_K * bass() * borderLight(uv, vec2(ratio, 1.0));
    col.z += HIGHLIGHT_K * bass() * centerLight(uv, vec2(ratio, 1.0));

    col = hsb2rgb(col);
    col = pow(col, vec3(1.5 + bass()));

    fragColor = vec4(col, 1.0);
}

// Working through https://thebookofshaders.com/05/
#define pi 3.14159265359

float plot(vec2 uv, float pct) {
	return smoothstep( pct - 0.02, pct, uv.y ) -
           smoothstep( pct, pct + 0.02, uv.y );
}

float step2(float a, float b, float v) {
	return step(a,v) - step(b,v);    
}

float smoothstep2(float a, float b, float w, float v) {
	return smoothstep(a-w, a, v) - smoothstep(b, b+w, v);
}

float sqr(float x) { return x*x; }

vec2 tile(vec2 uv, float scale) { return fract(uv*scale); }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float t = iTime;

    // How many tiles we want to create.
    float tiles = 6.;

    // Get the integer part of our tiled coordinates
    vec2 uvIntPart; modf(tiles * uv, uvIntPart);

    // Get the index for the tile [0, ..., tiles]
    int tileIndex = int(uvIntPart.y * tiles + uvIntPart.x);

    // Get normalized coordinates [0, 1] for each tile.
    vec2 coord = tile(uv, tiles);

    // Function x, y
    float x, y; x = coord.x; 

    // Select each tile to render a different function.
    switch(tileIndex) {
        // Basic functions
        case 0: y = x;								break;
    	case 1: y = sqr(x); 						break;
        case 2: y = sqrt(x);						break;
        case 3: y = .5*log(x) + 1.;					break;
        case 4: y = pow(x, 5.);						break;
        case 5: y = .5*sin(x * pi * 2.) + .5; 		break;

        // Other functions
        case 6: y = fract(3.*x); 					break;
        case 7: y = ceil(5.*x)/5.;  				break;
        case 8: y = floor(5.*x)/5.;  				break;
        case 9: y = atan(x*pi/2.); 					break;
        case 10: y = asin(x*pi/4.);					break;
        case 11: y = 1.-sqr(x); 					break;

        // Step examples
        case 12: y = step(.5,x);					break;
        case 13: y = step(.4,x) - step(.6,x); 		break;
        case 14: y = x * step2(.4, .6, x);			break;
        case 15: y = step2(.0, .2, fract(4.*x));	break;
        case 16: y = x * step2(.0, .2, fract(4.*x));break;
        case 17: y = sin(x*pi) 
           	  * (1.-step(.2, fract(12.*x)))
              + 0.2 * (step2(.2, 1., fract(12.*x)));break;

        // Smoothstep examples
        case 18: y = smoothstep(.0,1.,x); 			break;
        case 19: y = smoothstep2(.4,.6,.2,x); 		break;
        case 20: y = x * smoothstep2(.4,.6,.1,x);	break;
        case 21: y = x * smoothstep2(fract(4.*x), 
                                     fract(4.*x+0.1)
                                     , 0.2, x); 	break;
        //case 22: y = 0.5 * smoothstep2(mod(4.*x,.5),
        //                              mod(4.*x+0.1,.5)
        //                              , 0.2, x)+.2;	break;

        // Clamp examples
        case 24: y = clamp(x,0.,1.); 				break;
        case 25: y = clamp(x,.5,1.); 				break;
        case 26: y = clamp(x,0.,.5); 				break;
        case 27: y = 1.-clamp(x,.5,1.); 			break;
        case 28: y = 1.-clamp(x,0.,1.);				break;

        // Misc. examples
        case 30: y = fract(sin(x*pi*4.)); 			break;
        case 31: y = fract(abs(sin(x*pi*4.))); 		break;
        case 32: y = mod(x, .25);			 		break;
        case 33: y = sin(x*pi);						break;
        case 34: y = 0.5 + .05 * ( sin(29.*x+8.*t)
                           		 + sin(38.*x+8.*t) 
                          		 + sin(17.*x+8.*t));break;
    }

    // Set color
    vec3 col = vec3(y);

    // Line
    float pct = plot(coord, y);
    col = (1.0-pct)*col+pct*vec3(0,1,0);

    // Grid
    float gridWidth = 0.01;
    col = mix(col, vec3(0), 1.-step(gridWidth, coord.x));
    col = mix(col, vec3(0), 1.-step(gridWidth, coord.y));

    // Output to screen
    fragColor = vec4(col,1.0);
}

#define NUM_OCTAVES 6
#define FBM_NOISE_FUNCTION worley2


float rnd(vec2 u) { return fract(3e5 * sin(dot(u, vec2(1, 78)))); }

vec2 rnd2( vec2 p ) {
    vec2 q = vec2( dot(p,vec2(127.1,311.7)),
                   dot(p,vec2(269.5,183.3)));
    return fract(sin(q)*43758.5453);
}

float valueNoise(vec2 u)
{
	vec2 i = floor(u), f = u-i,
    X = vec2(rnd(i), rnd(i+vec2(1, 0))),
    Y = vec2(rnd(i + vec2(0, 1)), rnd(i + 1.));
    f *= f*(3. - 2.*f);
    u = mix(X, Y, f.y);
    return mix(u.x, u.y, f.x);
}

vec2 valueNoise2(vec2 u) {
	vec2 i = floor(u), f = u-i,
    X = vec2(rnd(i), rnd(i+vec2(1, 0))),
    Y = vec2(rnd(i + vec2(0, 1)), rnd(i + 1.));
    f *= f*(3. - 2.*f);
    u = mix(X, Y, f.y);
    return u;
}

// input, rotation angle, scaling, translation
vec2 rigidTransform(vec2 p, float theta, float scale, vec2 t) {
    float c = cos(theta), s = sin(theta);
    return scale * (mat2(c, s, -s, c) * p) + t;
}

vec2 spin(vec2 u) { return .5 + .5 * sin(iTime + 6.2831*u); }

#define WORLEY_ANIMATION spin
float worley(vec2 u) {
    float d = 1e3, a;
    vec2 k =  floor(u), f = u-k, p, q = k + vec2(0, 0);
    for(int i = -1; i < 2; i++) {
        for(int j = -1; j < 2; j++) {
            p = WORLEY_ANIMATION(valueNoise2(k+vec2(i, j)));
            a = distance(f, vec2(i, j) + p);
            if(a < d) {
            	d = a;
                q = p;
            }
    } }
    return dot(q, vec2(.3,.6));
}

float worley2(vec2 u) {
    float d = 1e4, a;
    float acc = 0., acc_w = 0.;
    vec2 k =  floor(u), f = u-k, p, q = k + vec2(0, 0);
    for(int i = -3; i < 3; i++) {
        for(int j = -3; j < 3; j++) {
            vec2 p_i = vec2(i, j);
            vec2 p_f = WORLEY_ANIMATION(rnd2(k+p_i));
            float d = length(p_i - f + p_f);
            float w = exp(-8. * d);
            acc += w * d;
            acc_w += w;
    } }
    return acc / acc_w;
}

float worley3(vec2 u) {
    float d = 1e4, a;
    float acc = 0., acc_w = 0.;
    vec2 k =  floor(u), f = u-k, p, q = k + vec2(0, 0);
    const int r = 3;
    for(int i = -r; i < r; i++) {
        for(int j = -r; j < r; j++) {
            vec2 p_i = vec2(i, j);
            vec2 p_f = WORLEY_ANIMATION(rnd2(k+p_i));
            float d = length(p_i - f + p_f);
            float w = exp(-8. * d) * (1.-step(sqrt(float(r*r)),d));
            acc += w * valueNoise(k+p_i);
            acc_w += w;
    } }
    return acc / acc_w;
}

float fbm(vec2 u) {
    float v = 0.;
    for(int i = 0; i < NUM_OCTAVES; i++) {
        v += pow(.5, float(i+1)) * FBM_NOISE_FUNCTION(u);
        u = rigidTransform(u, .5, 2., vec2(1e3));
    }
    return v;
}

