// ===========================================
// mandelbrot set attachment positions
//
// coordinates of attachment positions of mandelbrot set 
// this only holds for N>=2 assuming N is integer
//
// there are always N-1 attachments from primary shape to secondary shapes.
// for example there is 1 attachment from cardioid to the left circle when N=2.
//
// coordinates of this attachments can be calculated in this way. 
//
// let 
//		radian = -k*pi/(N-1);   k = 1,3,5,7 ...      
//		radius = (7+N)/(10+N);
// then 
// 		x = radius*cos(radian)
// 		y = radius*sin(radian)
//
// note that k always cycles through N. 
// that means k and mod(k, N) gives same coordinates.
//
// ============================================
// all of this was achieved by experiment. i dont have mathematical proof.
// ============================================

const float pi = 3.14159265359;
const float epsilon = 1e-5;

const float zoom = 4.0;
const float thickness = 3.0/zoom;

// mandelbrot set properties
const int iterations = 100;
const float bailout = 100.0;

// times table properties
const int modular = 200;

// shared properties
const float minPower = 2.0; // minimum power of Z.
const float maxPower = 10.0; // maximum power of Z.
const float duration = 50.0; // transition cycle duration in seconds.


vec2 uvmap(vec2 uv) {
    return (2.0*uv-iResolution.xy)/iResolution.y;
}

vec3 pickColor(float n) {
    return 0.6+0.6*cos(6.3*n+vec3(0,23,21));
}

float smoothout(float dist){
    return smoothstep(thickness/iResolution.y,0.0,dist);
}

float smoothfloor(float x) {
    return x - sin(2.0*pi*x)/(2.0*pi);
}

float clock(){
    float rad = acos(cos(2.0*pi*iTime/duration));
    return (maxPower-minPower)*rad/pi + minPower;
}

float circle(vec2 uv, vec2 C, float r, bool fill)
{
    vec2 p = uv-C;
    float fx = length(p)-r;
    float dist = fill? fx:abs(fx);
    return smoothout(dist);
}

float line(vec2 p, vec2 a, vec2 b)
{
    vec2 pa = p - a, ba = b - a; 
    float h = clamp(dot(pa, ba) / dot(ba, ba), 0.0, 1.0);
    float dist = length(pa - ba * h);
    return smoothout(dist);
}

vec2 function(vec2 z, float n) {
    float r = pow(dot(z, z), n/2.0);
    float th = n*atan(z.y,z.x+epsilon);
    return r*vec2(cos(th), sin(th));
}

vec2 dfunction(vec2 z, vec2 dz, float n){
    // f(f(z))' = f'(f(z))*f'(z)
	vec2 df = n*function(z, n - 1.0);
	return vec2(df.x*dz.x-df.y*dz.y, df.x*dz.y + df.y*dz.x) + vec2(1,0);
}

float mandelbrotDistance(float r, float dr) {
    float dist = r*log(r)/dr;
    return clamp(pow(dist,0.25),0.0,1.0);
}

vec3 mandelbrot(vec2 uv, float n) {
    vec3 set = vec3(0);
    vec2 c = uv;
    vec2 z = c;
    vec2 dz = vec2(1, 0);

    for(int i = 0; i < iterations && dot(z, z) <= bailout; i++) {
	    dz = dfunction(z,dz,n);
	    z = function(z,n) + c;
    }

    float dist = mandelbrotDistance(length(z), length(dz));

    if(dot(z, z) > bailout) set = dist+pickColor(n/5.0)/2.0;

    return clamp(set*0.8,0.0,1.0);
}

vec3 timesTable(vec2 uv, float times)
{
    vec3 col = vec3(0);
    float len = 2.0*pi/float(modular);
    float r = (7.0+times)/(10.0+times); // radius of the circle
    float phase = -pi/(times-1.0);

    col+=circle(uv,vec2(0),r,false);

    for(int i = 0; i < modular; i++) {
        float n = float(i);

        vec2 c = vec2(cos(n*len+phase),sin(n*len+phase))*r;
        vec2 p = vec2(cos(n*len*times+phase),sin(n*len*times+phase))*r;

        col+= circle(uv,c,0.005,true);
        col+= line(uv,c,p)*pickColor(n/float(modular)/3.+iTime/10.0);
    }
    return clamp(col*0.5,0.0,1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    float N = smoothfloor(clock());
    float radian = -pi/(N-1.0);
    float radius = (7.0+N)/(10.0+N);
    vec2 position = vec2(cos(radian),sin(radian))*radius; // attachment position

    vec2 uv = uvmap(fragCoord.xy)/zoom+position;

    vec3 color = mandelbrot(uv,N)
               + timesTable(uv,N);

    fragColor = vec4(color,1.0);
}

