float yPos(float i){
    vec2 p = vec2(SEED, i);

    float r = rand(p);
    return fract(iTime * SP + r);
}

float xPos(float i, float t){
    vec2 p = vec2(i, t - iTime * SP);
    return rand(p) + .375;
}

vec3 triCol(float i, float t){
    vec3 col = COLOR;
    float r = xPos(i + 1.0, t);
    col *= mix(0.9, 1.1, r);
    return col;
}

float atan2(float y, float x) {
 	if(x>0.)return atan(y/x);
    if(x==0.)if(y>0.)return HALFPI;else return -HALFPI;
    if(y<0.)return atan(y/x)-PI;return atan(y/x)+PI;
}
float atan2(vec2 v){return atan2(v.y,v.x);}
float steq(float x,float a,float b){return step(a,x)*step(x,b);}
vec2 cub_(float t,vec2 a,vec2 b){
    float ct=1.-t;
    return 3.*ct*ct*t*a+3.*ct*t*t*b+t*t*t;
}
float cub(float x,vec2 a,vec2 b){
    vec2 it=vec2(0.,1.);
    for (int i=0;i<7;i++) {
        float pos=(it.x+it.y)/2.;
        vec2 r=cub_(pos,a,b);
        if (r.x>x){
            it.y=pos;
        }else{
            it.x=pos;
        }
    }
    return cub_((it.x+it.y)/2.,a,b).y;
}
float isine(float t){return -1.*cos(t*HALFPI)+1.;}
float osine(float t){return sin(t*HALFPI);}
float iquad(float t){return t*t;}
float oc(float t){t=t-1.;return t*t*t+1.;}
vec2 oc(vec2 v){return vec2(oc(v.x),oc(v.y));}
float icirc(float t){return -1.*(sqrt(1.-t*t)-1.);}
vec3 spin(vec3 col_,vec2 fc) {
    vec3 col=col_;
    float a=mod(degrees(atan2(fc-iResolution.xy/2.-.5)),360.);
    float b=mod(iTime*100.,360.);
    float s=25.;
    float mi=mod(b-s,360.);
    float ma=mod(b+s,360.);
    float d=abs(b-a);
    if(d>180.)d=a<b?a-b+360.:b+360.-a;
    if((a>mi||(mi>ma&&a<ma))&&(a<ma||mi>ma))col+=1.-iquad(d/s);
    return col;
}
float mb(){
    return clamp(texture(iChannel0,vec2(0.02,0.2)),0.,1.).x;
}
vec3 barz(float d,vec2 fc,float off,float sp) {
    float a=degrees(atan2(fc-iResolution.xy/2.-.5))/180.+1.;
    a=mod(a+iTime/sp+off+mb()/3.,2.);
    a-=1.;
    if(a<0.)a=-a+0.01;
    float m=mod(a,.025);
    if(m<0.01*(1.+d*.6))return vec3(0.);
    a-=m;
    float v=clamp(texture(iChannel0,vec2(a,0.1)).x,0.,1.);
    if (v>d) return vec3(1.);
    return vec3(0.);
}
float osu_excdot(vec2 uv) {
    const float ds=.07;
    vec2 exc=oc((ds*2.-abs(uv))/ds/2.)*.04;
    return steq(uv.x,-ds-exc.y,ds+exc.y)*steq(uv.y,-ds-exc.x,ds+exc.x);
}
float osu_excbody(vec2 uv) {
    float e=oc((.2-abs(uv.x))/.2)*.04;
    float ew=(uv.y+.15)*.01;
    return steq(uv.x,-.1-ew,.1+ew)*steq(uv.y,-.2-e,.2+e);
}
float osu_u(vec2 uv) {
    float r=1.18181818;
    uv+=vec2(.5/r,.5);
    uv.x*=r;
    uv.y=1.-uv.y;
    float c=1.;
    c-=steq(uv.x,.31,.69)*steq(uv.y,.0,.765-.245*cub(1.-(uv.x-.31)/.38,vec2(.4,-.116),vec2(.994,-.27)));
    float b=.48*cub(1.-uv.x,vec2(.252,-.164),vec2(1.038,-.52));
    return c*steq(uv.x,0.,1.)*steq(uv.y,.02*isine(abs(mod(uv.x,.69)-.155)/.31),.933-b);
}
float osu_sunpy(vec2 uv) {
    float r=1.397928994;
    uv+=vec2(.5/r,.5);
    uv.x*=r;
    if (steq(uv.x,0.,1.)*steq(uv.y,0.,1.)==0.) {
        return 0.;
    }
    uv.y=1.-uv.y;
    float c=1.;
    c-=steq(uv.x,.0,.035+.515*icirc((.3-uv.y)/.3))*steq(uv.y,.0,.3);
    c-=steq(uv.x,.55,1.)*steq(uv.y,.0,.055*isine(clamp((uv.x-.55)/.4,.0,1.)));
    c-=steq(uv.x,.95-.07*isine(clamp((uv.y-.055)/.192/*.195*/,.0,1.)),1.)*steq(uv.y,.055,.28);
    c-=steq(uv.x,.59,.88)*steq(uv.y,.2+.045*isine((uv.x-.59)/.29),.28);
    c-=steq(uv.x,.4+.19*icirc(1.-(uv.y-.2)/.08),.59)*steq(uv.y,.2,.28);
    c-=steq(uv.x,.4+.6*cub((uv.y-.28)/.395,vec2(.408,.011),vec2(.104,1.014)),1.)*steq(uv.y,.28,.675);
    c-=steq(uv.x,1.-.585*icirc((uv.y-.675)/.325),1.)*steq(uv.y,.675,1.);
    c-=steq(uv.x,.0,.415)*steq(uv.y,.94+.06*osine(uv.x/.415),1.);
    c-=steq(uv.x,.0,.085*isine(1.-(uv.y-.75)/.19))*steq(uv.y,.75,.94);
    c-=steq(uv.x,.0,.4)*steq(uv.y,.69,.75+.045*osine(clamp((uv.x-.085)/.315,0.,1.)));
    c-=steq(uv.x,.4,.645)*steq(uv.y,.69,.795-.105*icirc((uv.x-.4)/.245));
    c-=steq(uv.x,.0,.035+.61*cub((uv.y-.3)/.39,vec2(.891,-.042),vec2(.592,.977)))*steq(uv.y,.3,.69);
    return c;
}
float osu_o(vec2 uv) {
    float r=1.091666;
    uv.x*=r;
    uv.y=1.-abs(uv.y);
    uv.x=abs(uv.x);
    float te=cub(uv.x,vec2(.667,.013),vec2(.988,.366));
    float be=1.-cub(clamp(uv.x/.402,0.,1.),vec2(.783,.035),vec2(.915,.241));
    return steq(uv.x,0.,1.)*steq(uv.y,0.+te,1.-.595*be);
}
float osu(vec2 uv) {
    float col=0.;
    col+=osu_excdot((uv-vec2(.806,-.192))*1.4);
    col+=osu_excbody((uv-vec2(.806,.23))*vec2(1.35,.9));
    col+=osu_u((uv-vec2(.379,.0))*1.7);
    col+=osu_sunpy((uv-vec2(-.134,.0))*1.7);
    col+=osu_o((uv-vec2(-.667,.0))*3.4);
    return col;
}
void mainImage(out vec4 fragColor, in vec2 fragCoord){
    float s = 1.2-texture(iChannel0, vec2(0.52,0.2)).x*.4;
    vec2 uv = fragCoord/iResolution.xy * s-(s-1.)*.5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = COLOR;

    // Generate all dem triangles
    for (float i = TRI; i > 0.1; i--){
        float id = i / TRI;
        float y = yPos(id);
        float x = xPos(id, y);
        float s = min(0.89, max(0.071, id * 0.5));
        float shad = tri(
            uv,
            vec2(x, mix(-s, 3.0 + s / 2.0, y)),
            s
        );

        if (shad < 0.1)
        	col = triCol(id, y) * (1.0 - shad);
    }

    // Set background mask
    vec2 mid=vec2(.5*iResolution.x/iResolution.y,.5);
    float dist = distance(uv,mid);
    if (dist > 0.4) {
        col = vec3(0.0);
        if (dist<0.65){
            float sp=3.;
            float el=.1+.2*mb();
            float d=(dist-.4)/.25;
          	col+=barz(d,fragCoord,0.,sp);
          	col+=barz(d,fragCoord,.5,sp);
          	col+=barz(d,fragCoord,1.,sp);
          	col+=barz(d,fragCoord,1.5,sp);
            col*=el;
            col.x*=.75;
            col.y*=.75;
        }
    } else
        if(dist>0.32&&dist<0.37)col=spin(col,fragCoord);

    // Make circle logo shadow
    float dist_shad = distance(uv, vec2(0.5 * iResolution.x / iResolution.y, 0.49));
    float l_shad = abs(dist_shad - 0.4);
    col *= mix(0.3, 1.0, min(1.0, l_shad * 30.0));

    // Make circle logo
    float l = abs(dist - 0.5);
    col += vec3(smoothstep(0.96, 0.97, 1.0 - l));

    if(dist<0.3) //for perf
    col+=vec3(osu((uv-mid)/.02));

    fragColor = vec4(col,1.0);
}

/* "glyphspinner" by mattz
   License: https://creativecommons.org/licenses/by-nc-sa/3.0/

   I've been carrying this shader in my head for a long time, 
   glad I finally had some time to work on it!

   Towards the end of developing a shader, I'm always pleased
   if I realize I've zoned out watching it run instead of 
   putting the finishing touches on it, which certainly was
   the case here.

   Since I couldn't get the glyph edges nicely antialiased, 
   I decided to leave the backgrounda  bit rough, too, which
   you can see if you pause the shader. 

*/

//#define PERSPECTIVE

const vec3 bgcolor = vec3(0, 0.1, 0.1);
const vec3 color_a = vec3(1.0, 0.4, 0);
const vec3 color_b = vec3(0.3, 0, 0.7);
const vec3 outline = vec3(1, 0, 0.2);

float t = 0.0;

// texture is 1024x1024
const float TEX_RES = 1024.;

// texture is 16x16 glyphs
const float GLYPHS_PER_UV = 16.;

// since the texture is uint8 it has a bias to represent 0
const float TEX_BIAS = 127./255.;

// get font UV coords from screen coords
vec2 font_from_screen(vec2 tpos, vec2 char_pos) {    
    return (tpos + char_pos + 0.5)/GLYPHS_PER_UV;
}


float sample_dist_gaussian(vec2 uv) {

    float dsum = 0.;
    float wsum = 0.;

    const int nstep = 3;

    const float w[3] = float[3](1., 2., 1.);

    for (int i=0; i<nstep; ++i) {
        for (int j=0; j<nstep; ++j) {

            vec2 delta = vec2(float(i-1), float(j-1))/TEX_RES;

            float dist = textureLod(iChannel0, uv-delta, 0.).w - TEX_BIAS;
            float wij = w[i]*w[j];

            dsum += wij * dist;
            wsum += wij;

        }
    }

    return dsum / wsum;
}


/* Rotate about x-axis */
mat3 rotX(in float t) {
    float cx = cos(t), sx = sin(t);
    return mat3(1., 0, 0, 
                0, cx, sx,
                0, -sx, cx);
}


/* Rotate about y-axis */
mat3 rotY(in float t) {
    float cy = cos(t), sy = sin(t);
    return mat3(cy, 0, -sy,
                0, 1., 0,
                sy, 0, cy);

}


float approx_font_dist(vec2 p, int cidx) {

    float d = max(abs(p.x) - 0.25,
                  max(p.y - 0.3, -0.28 - p.y));

    vec2 cpos = vec2(float(cidx%16), float(15-cidx/16));
    vec2 uv = font_from_screen(p, cpos);

    float fd = sample_dist_gaussian(uv); 


    d = max(d, fd);


    return d;

}

vec3 map(in vec3 pos) {	

    int ca = int(t) % 26;
    int cb = (ca + 1) % 26;
    int cc = (ca + 2) % 26;

    float da = approx_font_dist(pos.xy, 65+ca);
    float dc = approx_font_dist(pos.xy, 65+cc);

    float ft = fract(t);

    if (ft > 0.95) {
        da = mix(min(da, dc), dc, smoothstep(0.95, 1.0, ft));
    } else if (ft > 0.9) {
        da = mix(da, min(da, dc), smoothstep(0.9, 0.95, ft));
    }

    float db = approx_font_dist(pos.zy, 65+cb);


    return vec3(max(da, db), da, db);

}

/* IQ's distance marcher. */
vec3 castRay( in vec3 ro, in vec3 rd) {

    const int rayiter = 80;
    const float dmax = 8.;

    const float precis = 0.001;   
    float h=2.0*precis;

    float t = 0.0;
    vec2 m = vec2(-1.0);

    for( int i=0; i<rayiter; i++ ) {

        if( abs(h)<precis||t>dmax ) { continue; }

        t += min(0.25, h);
        vec3 res = map( ro+rd*t );
        h = res.x;
        m = res.yz;

    }    

    if (t > dmax) { return vec3(-1); }
    if (abs(h) > 4.0*precis) { return vec3(-1); }

    return vec3(t, m);

}

vec3 shade( in vec3 ro, in vec3 rd ,
           inout vec3 c){

    vec3 tm = castRay(ro, rd);        

    if (tm.x >= 0.0) {

        vec3 pos = ro + tm.x*rd;

        vec3 d = map(pos);


        float flip = mod(t, 2.0) < 1.0 ? -1.0 : 1.0;

        vec3 fg = mix(color_a, color_b,
                      step(flip*d.y, flip*d.z));

        c = mix(fg, outline, smoothstep(0.003, 0.0, abs(d.y-d.z)-0.008));

    }

    return c;

}

float scribble(vec2 p, float k) {

    float scl = k/iResolution.y;

    float kspiral = 0.2;

    vec2 c = p*scl;
    vec2 c0 = floor(c+0.5);

    float d = 1e5;

    for (int i=-1; i<2; ++i) {
        for (int j=-1; j<2; ++j) {

            vec2 cij = c0 + vec2(float(i), float(j));

            vec4 r = textureLod(iChannel1, (cij+0.5)/256., 0.);
            cij += 0.5*(r.xy - 0.5);

            vec2 diff = c - cij;
            float sz = 0.3 + 0.3*(r.z - 0.5);

            float t = r.z *  10253.5721;
            float c = cos(t), s = sin(t);

            diff = mat2(c, -s, s, c) * diff;

            vec2 sq = abs(diff) - sz;
            float dsqr = max(sq.x, sq.y);

            if (r.w < 0.5) {

                if (r.w < 0.25) {
                    d = min(d, abs(dsqr));
                } else {
                    float dx = abs(dot(abs(diff), normalize(vec2(-1,1))));
                    dx = max(dx, dsqr);
                    d = min(d, dx);
                }

            } else if (r.w < 0.75) {

                d = min(d, abs(length(diff) - sz));

            } else {

                diff.x = abs(diff.x);

                float dtri = max(-diff.y, 
                                 dot(diff, vec2(0.8660254037844386, 0.5)));

                d = min(d, abs(dtri-0.85*sz));

            }

        }
    }

    return step(0., d-0.1);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = (fragCoord.xy - .5*iResolution.xy) * 0.8 / (iResolution.y);

    t = 0.45*max(0.0, iTime-2.0);

    float s = scribble(fragCoord + 0.45*iTime*vec2(75., 30), 7.);

    vec3 bgdark = mix(bgcolor, vec3(0), 0.6);
    vec3 bglite = mix(bgcolor, vec3(1), 0.25);
    vec3 color = mix(bgcolor, bgdark, s);

    float rad = 0.05*iResolution.y;
    float m = rad;
    float x0 = m+rad;

    if (max(iMouse.x, iMouse.y) > x0) {        

        float row = floor(4.0 - 4.0 * iMouse.y / iResolution.y);
        float ux = clamp( (iMouse.x - x0) / (iResolution.x - 2.*x0), 0., 1.);

        if (mod(row, 2.0) != 0.0) { ux = 1.0 - ux; }

        t = 6.5 * (ux + row) + 1e-5;

    }

    float u = fract(t);
    u = smoothstep(0.1, 0.9, u);

    float midbump = smoothstep(0.5, 0.0, abs(u-0.5));

    float thetay = -1.5707963267948966*u;
    float thetax = 0.4*midbump;

    mat3 Rview = rotY(thetay)*rotX(thetax); 

#ifdef PERSPECTIVE
    const float f = 1.5;
    vec3 rd = Rview*normalize(vec3(uv, f));
    vec3 ro = Rview*vec3(0,0,-f);
#else
    vec3 rd = Rview*vec3(0, 0, 1);
    vec3 ro = Rview*vec3(uv*0.9, -3.5);
#endif

    if (max(iMouse.z, iMouse.w) > x0) {

        vec2 p = fragCoord - iResolution.xy*vec2(0.5, 0);

        float row = floor(4.0*fragCoord.y/iResolution.y);
        float rowy = (row + 0.5)* iResolution.y * 0.25;

        float dx = sign(p.x);
        float dy = mod(row, 2.0) > 0.0 ? -1.0 : 1.0;
        dy *= dx;

        vec2 ctr = vec2(dx*(0.5*iResolution.x - x0),
                        rowy + dy*rad);

        float dcirc = 1e5;
        float dly = abs(p.y - rowy);
        float dlx = abs(p.x) - (0.5*iResolution.x - m);

        if (p.x > 0.5 || (row != 0. && row != 3.)) { 
            vec2 dctr = p - ctr;
            dcirc = abs(length(dctr) - rad);
            dcirc = max(-dctr.x*dx, max(dctr.y*dy, dcirc));
            dcirc = min(dcirc, max(dctr.y*-dy, abs(dctr.x-dx*rad)));
            dlx = abs(p.x) - (0.5*iResolution.x - x0);
        }

        float dpath = min(dcirc, max(dlx, dly)) - 2.0;

        color = mix(color, bglite, smoothstep(1., 0., dpath));
    }

    shade(ro, rd, color);

    color = pow(color, vec3(1.0/2.2));

    fragColor = vec4(color, 1);

}

//CC0 1.0 Universal https://creativecommons.org/publicdomain/zero/1.0/
//To the extent possible under law, Blackle Mori has waived all copyright and related or neighboring rights to this work.

//returns p's projection onto and distance to line segment p0:p1
vec2 projectToSegment(vec2 p, vec2 p0, vec2 p1) {
    vec2 v = p1 - p0;
    vec2 w = p - p0;

    float b = dot(v,w)/dot(v,v);
    float dist = distance(p, p0 + v * clamp(b, 0.0, 1.0));
    return vec2(b, dist);
}

//returns ith star point coordinates
vec2 pointFromIndex(float i) {
    float ang = i/7.0*6.28;
    return vec2(sin(ang), cos(ang));
}

//antialiases a distance value to an edge
float aaEdge(float edge) {
    float pixelSize = 2.0/iResolution.y*1.1;
    return clamp(edge/pixelSize, 0.0, 1.0);
}


float smin( float a, float b, float k )
{
    // if (k == 0.0) return min(a,b);
    float h = clamp( 0.5+0.5*(b-a)/k, 0.0, 1.0 );
    return mix( b, a, h ) - k*h*(1.0-h);
}

float sclamp(float x, float a, float b, float k) {
    return smin(-smin(-x, -a, k), b, k);
}


//returns color+alpha for distance to a line segment
vec2 colorForDistance(float dist, float projx) {
    float thiccness = 0.035;
    float border = 0.015;

    float pixelSize = 2.0/iResolution.y*1.1;

    float col = aaEdge(thiccness - dist);
    //shading
    col *= mix(0.6,1.0,smin(pow(projx-1.00,2.0)*128.0,1.0,4.0));
    col *= mix(0.6,1.0,smin(pow(projx-0.56,2.0)*512.0,1.0,4.0));
    col *= mix(0.6,1.0,smin(pow(projx-0.35,2.0)*512.0,1.0,4.0));

    float alpha = aaEdge(thiccness + border - dist);

    return vec2(col, alpha);
}

//mixes two colours with alpha blending
vec2 compose(vec2 bottom, vec2 top) {
    float outalpha = top.y + bottom.y * (1.0- top.y);
    float outcol = (bottom.x*bottom.y*(1.0-top.y) + top.x*top.y)/outalpha;
    return vec2(outalpha == 0.0 ? 0.0 : outcol, outalpha);
}

float septagram(vec2 uv, float time) {

    vec2 col = vec2(0.0);
    for (float i = 0.0; i < 7.0; i++) {
        vec2 p0 = pointFromIndex(i*3.0);
        vec2 p1 = pointFromIndex(i*3.0+3.0);
        vec2 proj = projectToSegment(uv, p0, p1);

        //this part does the cool loading fade
        float fillCol = mix(0.1, 1.0, sclamp((time - proj.x - i)*16.0, 0.0, 1.0, 1.0));
        if (time > 15.0) {
            float fillCol2 = mix(0.1, 1.0, cos((time - proj.x - i)*6.283)*0.5+0.5);
            fillCol = mix(fillCol, fillCol2, clamp(time-15.0, 0.0, 1.0));
        }
        vec2 lineCol = colorForDistance(proj.y, proj.x) * vec2(fillCol, 1.0);
        //the first half of the line goes over everything, the second half goes under everything
        if (proj.x > 0.4 && proj.x < 0.5) {
            col = compose(col, lineCol);
        } else if (proj.x > 0.5 && proj.x < 0.6) {
            col = compose(lineCol, col);
        } else if (proj.x > 0.6 && proj.x < 0.7) {
            col = compose(col, lineCol);
        } else if (proj.x < 0.1) {
            col = compose(col, lineCol);
        } else {
            col = compose(lineCol, col);
        }
    }

    float capborder = length(uv) - 0.97 + (0.08 - cos(atan(uv.x,uv.y)*7.0*2.0)*0.08);

    vec2 caps = 1.0 - vec2(aaEdge(capborder*0.9 + 0.015), aaEdge(capborder*0.9));
    col = vec2(min(caps.x, col.x), min(caps.y, col.y));

    //set iChannel0 to a texture to see alpha transparency
    return compose(vec2(texture(iChannel0, uv*0.5).x,1.0), col).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = fragCoord/iResolution.xy*2.0-1.0;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 1.1;

    float col = septagram(uv, iTime*0.5 - 0.5);

    // Output to screen
    fragColor = vec4(vec3(col),1.0);
}

bool SolveSquare(float A, float B, float C, out vec2 x) {
    float D = B*B - 4.0 * A * C;
    if (D < 0.0) return false;
    x.x = (-B - sqrt(D)) / (2.0 * A);
    x.y = (-B + sqrt(D)) / (2.0 * A);
    return true;
}

bool ConeIntersect(float ConeR, vec2 ConeCaps, vec3 ro, vec3 rd, out float t, out vec3 norm) {
    float Al = ConeR * rd.z;
    float Bl = ConeR * ro.z;

    float A = dot(rd.xy, rd.xy) - Al*Al;
    float B = 2.0*(dot(rd.xy, ro.xy) - Al*Bl);
    float C = dot(ro.xy, ro.xy) - Bl*Bl;

    vec2 tt;
    if (!SolveSquare(A, B, C, tt)) return false;
    vec2 zz = ro.zz + rd.zz*tt;

    int n = 2;
    if ((zz.x < ConeCaps.x)||(zz.x > ConeCaps.y)) {
        tt.x = tt.y;
        n--;
    }
    if ((zz.y < ConeCaps.x)||(zz.y > ConeCaps.y)) {
        tt.y = tt.x;
        n--;
    }
    if (n==0) return false;
    t = min(tt.x, tt.y);

    norm.xy = normalize(ro.xy + rd.xy*t);
    norm.z = -ConeR * sign(ro.z + rd.z*t);
    norm = normalize(norm);

    return true;
}

mat3 RotateTransform()
{
    mat3 res;
    float t = iTime;
    res[2] = normalize(vec3(cos(t), sin(t), cos(t)));
    res[1] = vec3(cos(t), sin(t), 0.0);
    res[0] = normalize(cross(res[1], res[2]));
    res[1] = normalize(cross(res[2], res[0]));
    return res;

}

