struct Sphere{
    vec4 pos;
    float radius;
    vec3 color;
};

struct Plane{
    vec4 pos;
    vec4 n;
};

struct RayHit{
    vec3 color;
    vec4 n;
    float dist;
};

struct Viewer{
    vec4 pos;
    vec4 right;
    vec4 up;
    vec4 forward;
};

struct Ray{
    vec4 start;
    vec4 dir;
};

float maxDistance = 100000.0;

float fogDist = 50.0;
vec3 fogColor = vec3(0.0, 0.0, 0.0);

vec3 applyFog(in vec3 col, float dist){
    float fogBlend = clamp(dist/fogDist, 0.0, 1.0);
    return mix(col, fogColor, fogBlend);    
}

RayHit mixRayHits(in RayHit a, in RayHit b, float factor){
    return RayHit(
        mix(a.color, b.color, factor),
        mix(a.n, b.n, factor),
        mix(a.dist, b.dist, factor)
    );
}

RayHit selectClosest(in RayHit a, in RayHit b){
    float mixFactor = float(b.dist < a.dist);
    RayHit result = RayHit(
        mix(a.color, b.color, mixFactor),
        mix(a.n, b.n, mixFactor),
        min(a.dist, b.dist)
    );
    return result;
}


vec3 sceneColor(in vec4 point){
    vec3 color = vec3(1.0, 1.0, 1.0);
    vec3 colorPos = fract(point.xyz);// - vec3(0.5, 0.5, 0.5));
    color.xyz = step(vec3(0.5), colorPos);
    return color;
}

RayHit rayVsPlane(in Ray ray, in Plane plane){
    RayHit result = RayHit(vec3(0.0, 0.0, 0.0), -ray.dir, maxDistance);
    RayHit defaultResult = result;

    float planeDot = dot(ray.dir, -plane.n);
    float normalDist = dot(ray.start - plane.pos, plane.n);
    float t = normalDist / planeDot;
    result.dist = t;
    result.n = plane.n;
    result.color = sceneColor(ray.start + ray.dir * t);//vec3(1.0, 1.0, 1.0);
    //return result;
    return mixRayHits(defaultResult, result, float(
        (planeDot > 0.0) && (t > 0.0)
    ));


    return result;
}

RayHit rayVsSphere(in Ray ray, in Sphere sphere){
    RayHit defaultResult = RayHit(fogColor, -ray.dir, maxDistance);

    //pythogorean solution
    vec4 sphereDiff = sphere.pos - ray.start;
    float sphereDist = dot(sphereDiff, ray.dir);
    float spherePerpDist = sqrt(dot(sphereDiff, sphereDiff) - sphereDist * sphereDist);

    float dt2 = sphere.radius * sphere.radius - spherePerpDist * spherePerpDist;
    float dt = sqrt(dt2);

    float t = sphereDist - dt;//no need for t2, as we haven no refractions
    vec4 hitPos = ray.start + ray.dir * t;
    vec4 sphereNormal = clamp(normalize(hitPos - sphere.pos), -1.0, 1.0);
    RayHit sphereHit = RayHit(
        sphere.color,
        //sphereNormal.xyz * 0.5 + 0.5,
        //clamp(sphereNormal.xyz * 0.5 + 0.5, 0.0, 1.0),
        sphereNormal,
        t
    );
    //sphereHit.color = sphereHit.n.xyz * 0.5 + 0.5;
    return mixRayHits(
        defaultResult, sphereHit, float((spherePerpDist < sphere.radius) && (t > 0.0))
    );


    //return result;
}

Ray makeRay(in vec2 fragCoord, in Viewer viewer){
    vec2 uv = fragCoord/iResolution.xy;    
    uv = uv * 2.0 - 1.0;
    uv.y *= iResolution.y/iResolution.x;
    Ray result = Ray(
        viewer.pos,
        viewer.forward + viewer.right * uv.x + viewer.up * uv.y
    );
    result.dir = normalize(result.dir);
    return result;
}

const int numMovingSpheres = 3;

struct Orbit{
    float period;
    float radius;
    vec4 origin;
    vec4 xVec;
    vec4 yVec;
};

struct SphereConfig{
    Orbit orbit;
    vec3 color;
    float radius;
};

const float pi = 3.14159265359;
//const float epsilon = 0.0001;

vec4 getOrbitPos(in Orbit orbit){

    float angle = fract(iTime/orbit.period) * pi * 2.0;

    return orbit.origin 
        + cos(angle) * orbit.radius * orbit.xVec 
        + sin(angle) * orbit.radius * orbit.yVec;

}

struct SceneConfig{
    Orbit sunOrbit;
    SphereConfig spheres[numMovingSpheres];
    //Orbit orbits[numMovingSpheres];
};

const vec4 zero4 = vec4(0.0, 0.0, 0.0, 0.0);

SceneConfig sceneConfig = SceneConfig(
    Orbit(15.0, 1.0, vec4(0.0, 5.0, 0.0, 0.0), vec4(1.0, 0.0, 0.0, 0.0), vec4(0.0, 0.0, 1.0, 0.0)),
    SphereConfig[numMovingSpheres](
    	SphereConfig(Orbit(3.0, 1.0, zero4, vec4(1.0, 0.0, 0.0, 0.0), vec4(0.0, 0.0, 1.0, 0.0)), 
                     vec3(0.25, 1.0, 0.25), 0.25),
    	SphereConfig(Orbit(5.0, 1.5, zero4, vec4(1.0, 0.0, 0.0, 0.0), vec4(0.0, 1.0, 0.0, 0.0)), 
                     vec3(0.25, 0.25, 1.0), 0.25),
    	SphereConfig(Orbit(7.0, 2.0, zero4, vec4(1.0, 0.0, 0.0, 0.0), vec4(0.0, 0.0, 0.0, 1.0)), 
                     vec3(1.0, 0.25, 0.25), 0.5)
    )
);

struct Scene{
    vec4 sunPos;
    Plane plane;
    Sphere mainSphere;
    Sphere spheres[numMovingSpheres];
};

Scene makeScene(in SceneConfig sceneConfig){
    Scene scene;
    scene.plane = Plane(vec4(0.0, -1.7, 0.0, 0.0), vec4(0.0, 1.0, 0.0, 0.0));
    scene.mainSphere = Sphere(vec4(0.0, 0.0, 0.0, 0.0), 0.5, vec3(1.0, 1.0, 1.0)); 

    for(int i = 0; i < numMovingSpheres; i++){
        vec4 pos = getOrbitPos(sceneConfig.spheres[i].orbit);
        scene.spheres[i].pos = pos;
        scene.spheres[i].color = sceneConfig.spheres[i].color;
        scene.spheres[i].radius = sceneConfig.spheres[i].radius;
    }
    scene.sunPos = getOrbitPos(sceneConfig.sunOrbit);
    return scene;
}


RayHit rayCastBase(in Ray ray, in Scene scene){
    RayHit result = RayHit(fogColor, -ray.dir, maxDistance);
    result = selectClosest(result, rayVsPlane(ray, scene.plane));
    result = selectClosest(result, rayVsSphere(ray, scene.mainSphere));
    for(int i = 0; i < numMovingSpheres; i++){
    	result = selectClosest(result, rayVsSphere(ray, scene.spheres[i]));
    }
    return result;
}

float getLightFactor(in vec4 lightPos, in vec4 worldPos, in vec4 worldNormal, in Scene scene){
    vec4 diff = lightPos - worldPos;
    float lightDist = length(diff);
    vec4 lightDir = normalize(diff);
    float lightDot = clamp(dot(lightDir, worldNormal), 0.0, 1.0);

    //shadow
    Ray lightRay = Ray(scene.sunPos, -lightDir);
    RayHit lightHit = rayCastBase(lightRay, scene);    

    const float shadowBias = 0.01;

    return lightDot * float((lightHit.dist + shadowBias) > lightDist);
}

const int maxSurfaceHits = 4;


vec3 rayCastScene(in Ray originalRay, in Scene scene){
    const vec3 ambientColor = vec3(0.2, 0.2, 0.2);
    vec3 finalColor = vec3(0.0);

    Ray curRay = originalRay;
    float baseFogDist = 0.0;
  	float subRayMultiplier = 1.0;

    for (int i = 0; i < maxSurfaceHits; i++){    
    	RayHit curHit = rayCastBase(curRay, scene);

    	float rayDot = clamp(dot(-curRay.dir, curHit.n), 0.0, 1.0);
   		float fresnSimple = 1.0 - rayDot;
    	fresnSimple = mix(0.1, 1.0, fresnSimple * fresnSimple);
        //fresnSimple = 0.5;    

    	vec3 baseColor = curHit.color;
    	vec4 worldPos = curHit.dist * curRay.dir + curRay.start;
    	vec4 worldNormal = curHit.n;

    	float lightFactor = getLightFactor(scene.sunPos, worldPos, worldNormal, scene);

        vec3 curFinalColor = vec3(0.0);
        curFinalColor += baseColor * ambientColor;
        curFinalColor += baseColor * lightFactor;
        curFinalColor = applyFog(curFinalColor, curHit.dist + baseFogDist);

        finalColor = mix(finalColor, curFinalColor, subRayMultiplier);
    	//finalColor += addedLight * subRayMultiplier;

    	const float bounceBias = 0.001;
    	vec4 reflected = reflect(curRay.dir, curHit.n);
    	vec4 curPos = curRay.start + curRay.dir * curHit.dist;
    	Ray nextRay = Ray(curPos + reflected * bounceBias, reflected);            
        baseFogDist += curHit.dist + bounceBias;

        subRayMultiplier *= fresnSimple;
        curRay = nextRay;
        //break;
    }

    return finalColor;
    //return applyFog(finalColor, curHit.dist);
}

vec4 getCurViewerPosition(){
    const float timePeriod = 30.0;

    const int numPositions = 7;
    vec4 positions[numPositions] = vec4[numPositions](
        vec4(-3.0, 1.0, -3.0, 0.0),
        vec4(3.0, 1.0, -3.0, 0.0),
        vec4(3.0, 1.0, -3.0, -3.0),
        vec4(3.0, 1.0, 0.0, -3.0),
        vec4(0.0, 1.0, 0.0, -3.0),
        vec4(0.0, 0.0, 0.0, -3.0),
        vec4(-3.0, 1.0, -3.0, 0.0)
    );

    float lerpScale = 4.0;

    float fracPeriod = fract(iTime/timePeriod);
    float curPosFloat = fracPeriod * float(numPositions);
    int curPos = int(floor(curPosFloat)) % numPositions;
    float curLerp = fract(curPosFloat);
    curLerp = clamp(curLerp * lerpScale, 0.0, 1.0);
    int nextPos = (curPos + 1) % numPositions;
    return mix(positions[curPos], positions[nextPos], curLerp);

}

vec4 planeProjectNormalize(vec4 v, vec4 n){
    return normalize(v - n *dot(v, n));
}

Viewer makeViewer(){
    vec4 target = vec4(0.0, 0.0, 0.0, 0.0);

    Viewer result = Viewer(
        vec4(0.0, 0.0, -3.0, 0.0),
        vec4(1.0, 0.0, 0.0, 0.0),
        vec4(0.0, 1.0, 0.0, 0.0),
        vec4(0.0, 0.0, 1.0, 0.0)
    );

    result.pos = getCurViewerPosition();
    result.forward = normalize(target - result.pos);
    result.up = planeProjectNormalize(result.up, result.forward);
    result.right = planeProjectNormalize(result.right, result.forward);
    result.right = planeProjectNormalize(result.right, result.up);
    return result;

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    //vec2 uv = fragCoord/iResolution.xy;
    Scene scene = makeScene(sceneConfig);
    Viewer viewer = makeViewer();

    Ray ray = makeRay(fragCoord, viewer);

    vec3 col = rayCastScene(ray, scene);

    // Output to screen
    fragColor = vec4(col,1.0);
}

