#define saturate(x) clamp(x, 0.0, 1.0)

#define MAX_STEPS 100
#define MAX_STEPS_F float(MAX_STEPS)

#define MAX_DISTANCE 20.0
#define MIN_DISTANCE 1.0
#define EPSILON .001
#define EPSILON_NORMAL .001

// ---------------------------------------------------------

// All SG math taken from 
// https://mynameismjp.wordpress.com/2016/10/09/sg-series-part-2-spherical-gaussians-101/
struct SG
{
    vec3 Amplitude;
    vec3 Axis;
    float Sharpness;
};

vec3 EvaluateSG(in SG sg, in vec3 dir)
{
    float cosAngle = dot(dir, sg.Axis);
    return sg.Amplitude * exp(sg.Sharpness * (cosAngle - 1.0f));
}

SG SGProduct(in SG x, in SG y)
{
    vec3 um = (x.Sharpness * x.Axis + y.Sharpness * y.Axis) / (x.Sharpness + y.Sharpness);
    float umLength = length(um);
    float lm = x.Sharpness + y.Sharpness;

    SG res;
    res.Axis = um * (1.0f / umLength);
    res.Sharpness = lm * umLength;
    res.Amplitude = x.Amplitude * y.Amplitude * exp(lm * (umLength - 1.0f));

    return res;
}

vec3 SGIntegral(in SG sg)
{
    float expTerm = 1.0f - exp(-2.0f * sg.Sharpness);
    return 2.0 * 3.141592 * (sg.Amplitude / sg.Sharpness) * expTerm;
}

vec3 ApproximateSGIntegral(in SG sg)
{
    return 2.0 * 3.141592 * (sg.Amplitude / sg.Sharpness);
}

// Equals to the integral of the product of two SGs
vec3 SGInnerProduct(in SG x, in SG y)
{
    float umLength = length(x.Sharpness * x.Axis + y.Sharpness * y.Axis);
    vec3 expo = exp(umLength - x.Sharpness - y.Sharpness) *x.Amplitude * y.Amplitude;
    float other = 1.0f - exp(-2.0f * umLength);
    return (2.0f * 3.141592 * expo * other) / umLength;
}

float SGSharpnessFromThreshold(in float amplitude, in float epsilon, in float cosTheta)
{
    return (log(epsilon) - log(amplitude)) / (cosTheta - 1.0f);
}

SG CosineLobeSG(in vec3 direction)
{
    SG cosineLobe;
    cosineLobe.Axis = direction;
    cosineLobe.Sharpness = 2.133f;
    cosineLobe.Amplitude = vec3(1.17f);
    return cosineLobe;
}

vec3 SGIrradianceInnerProduct(in SG lightingLobe, in vec3 normal)
{
    SG cosineLobe = CosineLobeSG(normal);
    return max(SGInnerProduct(lightingLobe, cosineLobe), 0.0f);
}

vec3 SGIrradianceFitted(in SG lightingLobe, in vec3 normal)
{
    float muDotN = dot(lightingLobe.Axis, normal);
    float lambda = lightingLobe.Sharpness;

    float c0 = 0.36f;
    float c1 = 1.0f / (4.0f * c0);

    float eml = exp(-lambda);
    float em2l = eml * eml;
    float rl = 1.0 / lambda;

    float scale = 1.0f + 2.0f * em2l - rl;
    float bias  = (eml - em2l) * rl - em2l;

    float x  = sqrt(1.0f - scale);
    float x0 = c0 * muDotN;
    float x1 = c1 * x;

    float n = x0 + x1;

    float y = clamp(muDotN, 0.0, 1.0);

    if(abs(x0) <= x1)
        y = (n * n) / x;

    float result = scale * y + bias; 
    return result * ApproximateSGIntegral(lightingLobe);
}

vec3 SGDiffuseInnerProduct(in SG lightingLobe, in vec3 normal, in vec3 albedo)
{
    vec3 brdf = albedo / 3.141592;
    return SGIrradianceFitted(lightingLobe, normal) * brdf;
}

SG DistributionTermSG(in vec3 direction, in float roughness)
{
    SG distribution;
    distribution.Axis = direction;
    float m2 = roughness * roughness;
    distribution.Sharpness = 2.0 / m2;
    distribution.Amplitude = vec3(1.0f / (3.141592 * m2)); 
    return distribution;
}

SG WarpDistributionSG(in SG ndf, in vec3 view)
{
    SG warp; 
    warp.Axis = reflect(-view, ndf.Axis);
    warp.Amplitude = ndf.Amplitude;
    warp.Sharpness = ndf.Sharpness;
    warp.Sharpness /= (4.0f * max(dot(ndf.Axis, view), 0.0001f)); 
    return warp;
}

float GGX_V1(in float m2, in float nDotX)
{
    return 1.0f / (nDotX + sqrt(m2 + (1.0 - m2) * nDotX * nDotX));
}

vec3 SpecularTermSGWarp(in SG light, in vec3 normal, in float roughness, in vec3 view, in vec3 specAlbedo)
{
    SG ndf = DistributionTermSG(normal, roughness);
    SG warpedNDF = WarpDistributionSG(ndf, view);

    vec3 result = SGInnerProduct(warpedNDF, light);

    vec3 warpDir = warpedNDF.Axis;
    float m2 = roughness * roughness;

    // I'm still not sure I understand this, it's obscuring a lot of the fresnel contribution :/
    float nDotL = saturate(dot(normal, warpDir));
    result *= nDotL;

    float nDotV = saturate(dot(normal, view));
    vec3 h = normalize(warpedNDF.Axis + view);

    result *= GGX_V1(m2, nDotL) * GGX_V1(m2, nDotV);

    float powTerm = pow((1.0f - saturate(dot(warpDir, h))), 5.0);
    result *= specAlbedo + (1.0f - specAlbedo) * powTerm;

    return max(result, 0.0f);
}

