SG GetLightSG()
{
    float t = iTime * .5;
    float height = .15;
    vec3 p = vec3(cos(t), height, sin(t));

	SG light;
	light.Axis = normalize(p);
    light.Sharpness = 10.0;
    light.Amplitude = pow(vec3(2.5, 1.5, 1.05), vec3(2.2)) * .35;
    return light;
}

struct Intersection
{
    float totalDistance;
    float mediumDistance;
    float sdf;
    float density;
    int materialID;
};

struct Camera
{
	vec3 origin;
    vec3 direction;
    vec3 left;
    vec3 up;
};

Camera GetCamera(vec2 uv, float zoom, float time)
{
    float dist = 2.35 / zoom;

    vec3 target = vec3(0.0, 0.0, 0.0);
    vec3 p = vec3(0.0, 0.0, 0.0) + vec3(cos(time), 0.0, sin(time)) * dist;

    vec3 forward = normalize(target - p);
    vec3 left = normalize(cross(forward, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(forward, left));

    Camera cam;   
    cam.origin = p;
    cam.direction = normalize(forward - left * uv.x * zoom - up * uv.y * zoom);
    cam.up = up;
    cam.left = left;

    return cam;
}

float sdf(vec3 p)
{
    return length(p) - 1.0;
}

vec3 sdfNormal(vec3 p, float epsilon)
{
    vec3 eps = vec3(epsilon, -epsilon, 0.0);

	float dX = sdf(p + eps.xzz) - sdf(p + eps.yzz);
	float dY = sdf(p + eps.zxz) - sdf(p + eps.zyz);
	float dZ = sdf(p + eps.zzx) - sdf(p + eps.zzy); 

	return normalize(vec3(dX,dY,dZ));
}

vec3 triplanar(vec3 P, vec3 N)
{    
    vec3 Nb = abs(N);

    float b = (Nb.x + Nb.y + Nb.z);
    Nb /= vec3(b);	

    vec3 c0 = texture(iChannel0, P.xy).rgb * Nb.z;
    vec3 c1 = texture(iChannel0, P.yz).rgb * Nb.x;
    vec3 c2 = texture(iChannel0, P.xz).rgb * Nb.y;

    return c0 + c1 + c2;
}

vec3 Render(Camera camera, Intersection isect, vec2 uv)
{
    vec3 p = camera.origin + camera.direction * isect.totalDistance;

    SG lightSG = GetLightSG();    
    SG innerSG = lightSG;

    // Play with these two
    innerSG.Sharpness *= 1.8;
    float innerAmplitude = 1.2;

    if(isect.sdf < EPSILON)
    {
        vec3 normal = sdfNormal(p, EPSILON_NORMAL);
        vec3 diffuse = pow(vec3(.85, .15, .05), vec3(2.2)) * .3;
        vec3 specular = pow(vec3(1.0, .9, .85), vec3(2.2)) * .5;

        float roughness = mix(.025, .2, cos(iTime) * .5 + .5);
        vec3 diff = SGDiffuseInnerProduct(lightSG, normal, diffuse);
        vec3 spec = SpecularTermSGWarp(lightSG, normal, roughness, -camera.direction, specular);        

        diff -= innerAmplitude * SGDiffuseInnerProduct(innerSG, normal, diffuse);
        spec -= innerAmplitude * SpecularTermSGWarp(innerSG, normal, roughness, -camera.direction, specular);

        spec = max(spec, vec3(0.0));
        diff = max(diff, vec3(0.0));

        return spec + diff;
    }
    else
    {
     	vec3 background = vec3(0.0);

        background += EvaluateSG(lightSG, camera.direction);
        background -= innerAmplitude * EvaluateSG(innerSG, camera.direction);

        return background;
    }
}

Intersection Raymarch(Camera camera)
{    
    Intersection outData;
    outData.sdf = 0.0;
    outData.density = 0.0;
    outData.totalDistance = MIN_DISTANCE;

	for(int j = 0; j < MAX_STEPS; ++j)
	{
        vec3 p = camera.origin + camera.direction * outData.totalDistance;
		outData.sdf = sdf(p);

		outData.totalDistance += outData.sdf;

		if(outData.sdf < EPSILON || outData.totalDistance > MAX_DISTANCE)
            break;
	}

    return outData;
}

vec3 filmic(vec3 x)
{
	return ((x*(0.15f*x + 0.10f*0.50f) + 0.20f*0.02f) / (x*(0.15f*x + 0.50f) + 0.20f*0.30f)) - 0.02f / 0.30f;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = (-iResolution.xy + (fragCoord*2.0)) / iResolution.y;    

    if(abs(uv.y) > .8)
        return;

    Camera camera = GetCamera(uv, 0.35, iTime * .25);
    Intersection isect = Raymarch(camera);
    vec3 color = Render(camera, isect, uv);    

    color = max(color, vec3(0.0));

	vec3 vignette = mix(vec3(1.0, .8, .96) * .1, vec3(1.3), (1.0 - smoothstep(.0, 2.1, length(uv))));
    vignette *= vignette;


	vec3 whiteScale = 1.0f / filmic(vec3(6.2f));
    color.rgb = filmic(color.rgb * 24.0 * vignette);    
    color.rgb = clamp(color.rgb * whiteScale, vec3(0.0), vec3(1.0));
    color = pow(color, vec3(.45454));

	fragColor = vec4(color, 1.0);
}

