float getFunctional(vec2 uv) {
    float sine = 5.0 * sin(uv.x*10.0-iTime*2.0) + 2.0 * cos(uv.y * 25.0 + iTime * 2.0);
    float mult = sine * uv.y*2.0+0.05 * 2.0 - uv.y*2.0+0.05;
    return mult;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 a = uv*2.0 - 1.0;
	a *= 10.0;
	vec2 line = vec2(a.x, a.y);
    line.x += getFunctional(uv);
	line.x /= 0.025;
	vec3 p = vec3(abs(line.x));
	p *= vec3(0.85,0.1,0.1);
	fragColor = vec4(p,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    //Inputs
    float gridRows=5.0;
    float gridCols=9.0;
    float brightness=0.5;
    float movingSpeed=1.0;
    float waveSize=0.2;
    float angle = 0.5;


    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    //We find out in which grid the pixel is
    int pixCol=int(uv.x*gridCols);
    int pixRow=int(uv.y*gridRows);

    // Time varying pixel color
    vec3 col = brightness + brightness*cos(iTime*movingSpeed+vec3(pixRow,pixRow,pixRow)/gridRows/waveSize*angle+vec3(pixCol,pixCol,pixCol)/gridCols/waveSize*(1.0-angle));


    // Output to screen
    fragColor = vec4(col,1.0);
}

int pick_rule(vec2 uv, int space) {
    return int(sin(distance(uv, vec2(0.5, 0.5))) * float(space) * 10.) % space;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 iPalette[4];
    iPalette[0] = vec3(0x17, 0x3E, 0xA6) / 255.;
    iPalette[1] = vec3(0xA5, 0x0E, 0x0E) / 255.;
    iPalette[2] = vec3(0xE3, 0x74, 0x00) / 255.;
    iPalette[3] = vec3(0x0D, 0x65, 0x2D) / 255.;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
	int p = pick_rule(uv * sin(iTime + uv.x), iPalette.length());
    int q = pick_rule(uv * cos(iTime + uv.y), iPalette.length());

    float m = smoothstep(-1., 1.,
                           sin(uv.x * uv.y / sin(uv.x))
                           );

    vec3 col = mix(iPalette[p], iPalette[q], m);

    // Output to screen
    fragColor = vec4(col,1.0);
}

