#define EPSILON .00001

// https://www.shadertoy.com/view/MslGD8
vec2 hash( vec2 p ) { p=vec2(dot(p,vec2(127.1,311.7)),dot(p,vec2(269.5,183.3))); return fract(sin(p)*18.5453); }

// https://www.shadertoy.com/view/llG3zy
vec4 voronoi( in vec2 x )
{
    bool isStripes = true;
    float moveDir = mod(floor(x.y), 2.0) > 0.5 ? 1.0 : -1.0;
    if (isStripes) {
        float speedMult = floor(x.y + 1.0) * 0.3;
    	x.x += iTime * moveDir * speedMult ;
    }

    vec2 n = floor(x);
    vec2 f = fract(x);

    // first pass: regular voronoi
	vec2 closestPoint;
    float id;

    float minDist = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 cell = vec2(float(i),float(j));
		vec2 point = hash( n + cell );

        vec2 r = cell - f + (0.5+0.5*sin(iTime+6.2831*point));
        float dist = dot(r,r);

        if( dist < minDist )
        {
            minDist = dist;
            closestPoint = r;
            id = point.x + point.y;
        }
    }

    // second pass: distance to borders,
    // visits only neighbouring cells
    minDist = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 cell = vec2(float(i),float(j));
		vec2 point = hash( n + cell );
        if (isStripes) {
           point = mix(hash( n + cell ), vec2(n + cell) * 0.1, moveDir > 0.0 ? 0.0 : 1.0);
        }
		vec2 r = cell - f + (0.5+0.5*sin(iTime+6.2831*point));

        if( dot(closestPoint-r,closestPoint-r) > EPSILON ) // skip the same cell
        minDist = min( minDist, dot( 0.5*(closestPoint+r), normalize(r-closestPoint) ) );
    }

    return vec4( minDist, closestPoint, id );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord.xy/max(iResolution.x,iResolution.y);

    vec4 voro = voronoi( (10.0 + sin(iTime*0.1)*8.0)*p);
    float dist = voro.x;

    vec2 gb = 0.5 + 0.5*cos(vec2(voro.w+iTime*0.3, voro.w * sin(iTime * 0.2))*6.2831 );	

    fragColor.rgb = vec3(1.0 - step(dist, 0.1), gb);
}

