bool isellipse(float x_center, float y_center, float radius1, float radius2)
{
    bool result = false;

    if (sqrt( radius1*pow(st.x - x_center,2.0) 
             + radius2*pow(st.y - y_center,2.0)
            ) < radius1 * radius2
        )result = true;

    return result;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 circle_position1;
    vec2 circle_position2;
    vec2 circle_position3;

    vec3 col1 = vec3(0.0);
    vec3 col2 = vec3(0.0);
    vec3 col3 = vec3(0.0);
    st = fragCoord/iResolution.xy;
    st*=5.0;
	circle_position1.x = 2.5+1.5*(sin(1.5*iTime));
    // offset in x for second sphere 
    circle_position2.x = 2.5+1.5*(sin(1.5*iTime+1.2));
    // offset in x for third sphere
    circle_position3.x = 2.5+1.5*(sin(1.5*iTime+2.4));

    circle_position1.y  = 2.5+0.5*(sin(2.*iTime));
    // offset in y for second sphere 
    circle_position2.y = 2.5+0.5*(sin(2.*iTime+1.2));
    // offset in y for second sphere 
    circle_position3.y = 2.5+0.5*(sin(2.*iTime+2.4));

    float radius = 1.15;

    float radius2 = radius*0.5;



    float lowerLimit = 0.0;
    float upperLimit = 1.5;


    float color = 1.0;

    col = vec3(0.2,0.2,0.2);


    if (st.y < upperLimit && st.y > lowerLimit){
        color = distance(st.y,upperLimit);
        float perc = (color - lowerLimit) / (upperLimit - lowerLimit);
        col = vec3(perc);

    }


    if(isellipse(circle_position1.x, circle_position1.y , radius, radius2))
    {

    	col1 = vec3(1.0,vec2(0.0));
    }

    // reflection of red "sphere"
    if(isellipse(circle_position1.x, 1.2-0.5*circle_position1.y, radius, radius2))
    {
        float colour = 0.2;
    	col1 = vec3(colour,0.0,0.0);
    }
    if(isellipse(circle_position2.x, circle_position2.y , radius, radius2))
    {
        float colour = 1.0;
    	col2 = vec3(0.0,colour,0.0);
    }
    if(isellipse(circle_position2.x, 1.2-0.5*circle_position2.y , radius, radius2))
    {
        float colour = 0.2;
    	col2 = vec3(0.0,colour,0.0);
    }
    if(isellipse(circle_position3.x, circle_position3.y , radius, radius2))
    {
        float colour = 1.0;
    	col3 = vec3(0.0,0.0,colour);
    }
    if(isellipse(circle_position3.x, 1.2-0.5*circle_position3.y , radius, radius2))
    {
        float colour = 0.2;
    	col3 = vec3(0.0,0.0,colour);
    }



	fragColor = vec4(col1+col+col2+col3,0.5);
}

