 *
 * when marching steps are exhausted, the last closest material is chosen.
 * This causes a trippy blur around the edges of objects and allows for a lower step count.
*/

precision highp float; // possibly numerically unstable with 16-bit floats?

const int KEY_LEFT  = 37;
const int KEY_UP    = 38;
const int KEY_RIGHT = 39;
const int KEY_DOWN  = 40;
float key(int keyid){
    return texelFetch(iChannel2, ivec2(keyid,0),0).x;
}

float mod289(float x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 mod289(vec4 x){return x - floor(x * (1.0 / 289.0)) * 289.0;}
vec4 perm(vec4 x){return mod289(((x * 34.0) + 1.0) * x);}

float noise(vec3 p){
    vec3 a = floor(p);
    vec3 d = p - a;
    d = d * d * (3.0 - 2.0 * d);

    vec4 b = a.xxyy + vec4(0.0, 1.0, 0.0, 1.0);
    vec4 k1 = perm(b.xyxy);
    vec4 k2 = perm(k1.xyxy + b.zzww);

    vec4 c = k2 + a.zzzz;
    vec4 k3 = perm(c);
    vec4 k4 = perm(c + 1.0);

    vec4 o1 = fract(k3 * (1.0 / 41.0));
    vec4 o2 = fract(k4 * (1.0 / 41.0));

    vec4 o3 = o2 * d.z + o1 * (1.0 - d.z);
    vec2 o4 = o3.yw * d.x + o3.xz * (1.0 - d.x);

    return o4.y * d.y + o4.x * (1.0 - d.y);
}

const int MAX_MARCHING_STEPS = 48;
const float MIN_DIST = 0.;
const float MAX_DIST = 512.0; // increase to draw further, I don't think there are actually drawbacks to increasing this? unless shaders can work-steal
const float EPSILON = 0.0001; // used to derive the gradient to get the normal, maybe mess with this

float cubeSDF(vec3 p) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - vec3(1.0, 1.0, 1.0);

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

float boxSDF(vec3 p, vec3 dim) {
    // If d.x < 0, then -1 < p.x < 1, and same logic applies to p.y, p.z
    // So if all components of d are negative, then p is inside the unit cube
    vec3 d = abs(p) - dim;

    // Assuming p is inside the cube, how far is it from the surface?
    // Result will be negative or zero.
    float insideDistance = min(max(d.x, max(d.y, d.z)), 0.0);

    // Assuming p is outside the cube, how far is it from the surface?
    // Result will be positive or zero.
    float outsideDistance = length(max(d, 0.0));

    return insideDistance + outsideDistance;
}

float sphSDF(vec3 p) { // sphere
    return length(p) - 1.0;
}

float cylSDF(vec3 p){ // cylinder
  vec2 d = abs(vec2(length(p.xz),p.y)) - 1.0;
  return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float uni(const float a, const float b){ // union
    return min(a,b);
}
float sub(const float a, const float b){ // subtract
    return max(a,-b);
}
float its(const float a, const float b){ // InTerSection because I can't call it "int"
    return max(a,b);
}

vec3 rep( vec3 p, vec3 c )
{
    return mod(p,c)-0.5*c;
}

#define sca(__f, __p, __s)   ( __f(__p *      (1. / __s))         * __s )
#define scaXZ(__f, __p, __s) ( __f(__p * vec2((1. / __s),1.).xyx) * min(1., __s) )

vec3 rotateY(float theta, vec3 p) {
    float c = cos(theta);
    float s = sin(theta);

    return (mat4(
        vec4(c, 0, s, 0),
        vec4(0, 1, 0, 0),
        vec4(-s, 0, c, 0),
        vec4(0, 0, 0, 1)
    ) * vec4(p, 1.0)).xyz;
}

// a rotatable box used to cut the arrow slots on the towers
float plank(vec3 p, float theta){
	return boxSDF(rotateY(theta, p), vec3(1.1, 0.1, 0.1));
}

const float PI = 3.141592653589793;
float castletowerSDF(vec3 p){
    vec3 p2 = p - vec2(0., 1.05).xyx; // move cutouts up to cut them from the tower
    float cutouts =
        uni(uni(plank(p2,0.)   , plank(p2, PI/2.)),
        	uni(plank(p2,PI/4.), plank(p2, PI/4.*3.)));
    cutouts = uni(cutouts, its(boxSDF(p2, vec3(1.0,0.2,1.0)), sca(cylSDF,p2,0.75)));
    return sub(cylSDF(p),
               cutouts);
}

float corner_inner(vec3 p){
    return castletowerSDF(p - vec2(4., 0.).xyx);
}

float corner(vec3 p){
    return scaXZ(corner_inner,p, 0.125); // spread out space to fit a whole tower in one corner
}

float four(vec3 p){
    vec3 rpt = mod(p, 1.0);
    return corner(vec3(rpt.x, p.y, rpt.z));
}

float castletowersSDF(vec3 p){
    return its(four(p), cubeSDF(p)); // clip to unit cube
}

float drawbridgeSDF(vec3 p){ //drawbridge hole
    vec3 ps = p + vec3(0.,0.3,0.6); // move drawbridge cut down and forwards
    return sca(cylSDF,ps.xzy * vec2(1.,0.6).xxy,0.21);
}

float castlewallsSDF(vec3 p){
    const vec2 o          = vec2(0.1, 0.0);
    const vec3 bodydims   = vec3(0.5, 0.35, 0.45);
    const vec3 cutoutdims = bodydims.zyz; // for inner hollow cutout
    float mainwall = sub(boxSDF(p, bodydims.xyx),
                         boxSDF(p-o.yxy, cutoutdims));

	const vec3 grid = vec2(0.025,0.2).yyx;
    vec3 gridp = rep(p, vec2(0.15, 4.).xyx) + vec2(0., 1.5).xyx; // magic trial-and-error constants
    float gridcutout =
        uni(
            uni(boxSDF(gridp.xyz, grid), boxSDF(gridp.zyx, grid)),
            drawbridgeSDF(p));
    return sub(mainwall, gridcutout);
}

float terrainSDF(vec3 p){ // called by shortestDistanceToSurface()
    float scale = 0.5; // range [0,1.0]
    float sstep = smoothstep(.5, 2., length(p.xz));
    scale *= sstep;

    float height = noise(vec3(p.xz, iTime * 0.3)) * scale;
    return (p.y*0.5) * (1.-scale) + height;
}

float castleSDF(vec3 p) {
    p -= vec2(0.,0.35).xyx;
    return uni(castlewallsSDF(p), castletowersSDF(p));
}

float objectSDF(vec3 p) { // called by shortestDistanceToSurface()
    return castleSDF(p);
}

float sceneSDF(vec3 p){ // called by normal calculator
    return uni(objectSDF(p), terrainSDF(p));
}

/**
 * Return a vec2 containing the shortest distance from the eyepoint to the scene surface along
 * the marching direction, and the type of material at that point.
 * .y =
 *   -1 when depth or march limit hit
 *    0 when grass
 *    1 when stone
 * 
 * eye: the eye point, acting as the origin of the ray
 * marchingDirection: the normalized direction to march in
 */
vec2 shortestDistanceToSurface(vec3 eye, vec3 marchingDirection) {
    const float start = MIN_DIST;
    const vec2    end = vec2(MAX_DIST, -1.0);
    float depth = start;
    float odist, tdist;
    for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
        vec3      p = eye + depth * marchingDirection;
        odist = objectSDF(p);
        tdist = terrainSDF(p);
        float  dist = min(odist,tdist);
        if (dist < EPSILON) {
			break; // close to surface, stop iterating
        }
        depth += dist; // maybe this accumulator should be error-compensated
        if (depth >= end.x) {
            return end; // beyond end, hit nothing
        }
    }
    float  type = step(odist - tdist,0.);
    return vec2(depth,type); // out of steps, hit nothing; still return closest material
}


vec3 rayDirection(float fieldOfView, vec2 size, vec2 fragCoord) {
    vec2 xy = fragCoord - size / 2.0;
    float z = size.y / tan(radians(fieldOfView) / 2.0);
    return normalize(vec3(xy, -z));
}

vec3 estimateNormal(vec3 p) {
    return normalize(vec3(
        sceneSDF(vec3(p.x + EPSILON, p.y, p.z)) - sceneSDF(vec3(p.x - EPSILON, p.y, p.z)),
        sceneSDF(vec3(p.x, p.y + EPSILON, p.z)) - sceneSDF(vec3(p.x, p.y - EPSILON, p.z)),
        sceneSDF(vec3(p.x, p.y, p.z  + EPSILON)) - sceneSDF(vec3(p.x, p.y, p.z - EPSILON))
    ));
}

vec3 phong(vec3 k_d, vec3 k_s, float alpha, vec3 p, vec3 eye,
           vec3 lightPos, vec3 lightColor, vec3 N) {
    vec3 L = normalize(lightPos - p);
    vec3 V = normalize(eye - p);
    vec3 R = normalize(reflect(-L, N));

    float dotLN = dot(L, N);
    float dotRV = dot(R, V);

    if (dotLN < 0.0) {
        // Light not visible from this point on the surface
        return vec3(0.0, 0.0, 0.0);
    } 

    if (dotRV < 0.0) {
        // Light reflection in opposite direction as viewer, apply only diffuse
        // component
        return lightColor * (k_d * dotLN);
    }
    return lightColor * (k_d * dotLN + k_s * pow(dotRV, alpha));
}

/**
 * Return a transform matrix that will transform a ray from view space
 * to world coordinates, given the eye point, the camera target, and an up vector.
 *
 * This assumes that the center of the camera is aligned with the negative z axis in
 * view space when calculating the ray marching direction. See rayDirection.
 */
mat4 viewMatrix(vec3 eye, vec3 center, vec3 up) {
    // Based on gluLookAt man page
    vec3 f = normalize(center - eye);
    vec3 s = normalize(cross(f, up));
    vec3 u = cross(s, f);
    return mat4(
        vec4(s, 0.0),
        vec4(u, 0.0),
        vec4(-f, 0.0),
        vec4(0.0, 0.0, 0.0, 1)
    );
}

vec3 triplanar(vec3 wPos, const vec3 wNorm){
    vec3 blend = normalize(max(abs(wNorm), 0.001));

    wPos *=3.;
    vec3 x = texture(iChannel0, wPos.zy).xyz;
    vec3 y = texture(iChannel0, wPos.xz).xyz;
    vec3 z = texture(iChannel0, wPos.xy).xyz;

    return x * blend.x + y * blend.y + z * blend.z;
}
float triplanarTerrain(vec3 wPos, const vec3 wNorm){
    vec3 blend = normalize(max(abs(wNorm), 0.001));

    wPos *=3.;
    float x = texture(iChannel1, wPos.zy).x;
    float y = texture(iChannel1, wPos.xz).y;
    float z = texture(iChannel1, wPos.xy).z;

    return x * blend.x + y * blend.y + z * blend.z;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float phase = fract(iTime * 0.04) * 2.0 * PI;

    // direction from eye position to frustrum front (i.e. like CRT screen)
	vec3 viewDir = rayDirection(45.0, iResolution.xy, fragCoord);

    vec3 eye = vec3(cos(phase) * 4., 2. + sin(phase*0.5)*4.0, sin(phase) * 4.);

    mat4 viewToWorld = viewMatrix(eye, vec3(0.), vec3(0.0, 1.0, 0.0));

    vec3 worldDir = (viewToWorld * vec4(viewDir, 0.0)).xyz;

    vec2 dist = shortestDistanceToSurface(eye, worldDir);

    if (dist.y < -0.5) {
        // Didn't hit anything
        fragColor = vec4(0.0);
		return;
    }

    // The closest point on the surface to the eyepoint along the view ray
    vec3 p = eye + dist.x * worldDir;

    vec3 N = estimateNormal(p);

    const vec3 K_a = vec3(0.2, 0.2, 0.2);
    vec3 K_d = triplanar(p, N);
    if(dist.y < 0.8){
        // when grass
        K_d = vec3(0.2, 0.8, 0.25) * triplanarTerrain(p, N);
    }
    const vec3 K_s = vec3(0.3, 0.2, 0.1);

    float shininess = 6.;

    const vec3 l = vec3(5.0, 3.0, 0.);
    vec3 lightpos = sin(phase)
        			* l.xyy
        			+ l.zxz;
    const vec3 lightcolor = vec3(1.0, 0.9, 0.9);

    vec3 color = 0.5 * K_a + phong(K_d, K_s, shininess, p, eye, lightpos, lightcolor, N);

    fragColor = vec4(color, 1.0);
}

// --- This started as something else entirely and became an area light with random patterns illuminating some fog and terrain. 
// --- Ray-marching using stuff meant for path-tracing, probably riddled with mistakes, but oh well ...
// --- Thanks to iq for all the ray-marching signed distance fields, noise and terrain rendering resources
// --- http://www.iquilezles.org/www/articles/morenoise/morenoise.htm
// --- Thanks to SebH for volumetric rendering resources
// --- http://www.frostbite.com/2015/08/physically-based-unified-volumetric-rendering-in-frostbite/
// --- Solid Angle for the cool article on equi-angular sampling and sjb for example implementation
// ---  https://www.solidangle.com/research/egsr2012_volume.pdf
// --- Thanks to ferris from logicoma and this amazing intro https://www.youtube.com/watch?v=9r8pxIogxZ0 for inspiration

float rand(vec2 p)
{
    p = 50.0 * fract(p * 0.3183099);
    return fract(p.x * p.y * (p.x + p.y));
}

float rand(float p)
{
    return fract(sin(p) * 54897.3451289);
}

vec2 smoothstepd(float e0, float e1, float x)
{
    if (x < e0)
        return vec2(0.0, 0.0);
    if (x > e1)
        return vec2(1.0, 0.0);

    float t = (x - e0) / (e1 - e0);
    float td = 1.0 / (e1 - e0);
    return vec2(3.0 * t * t - 2.0 * t * t * t, 6.0 * t * (1.0 - t) * td);
}

float valueNoise(in vec2 p)
{
    vec2 X = floor(p);
    vec2 x = fract(p);

    vec2 fn = x * x * x * (6.0 * x * x - 15.0 * x + 10.0);
    float u = fn.x;
    float v = fn.y;

    float a = rand(X + vec2(0.0, 0.0));
    float b = rand(X + vec2(1.0, 0.0));
    float c = rand(X + vec2(0.0, 1.0));
    float d = rand(X + vec2(1.0, 1.0));

    float n = (b - a) * u + (c - a) * v + (a - b - c + d) * u * v + a;
    return 2.0 * n - 1.0;
}

vec3 valueNoiseD(in vec2 p)
{
    vec2 X = floor(p);
    vec2 x = fract(p);

    vec2 fn = x * x * x * (6.0 * x * x - 15.0 * x + 10.0);
    vec2 dfn = 30.0 * x * x * (x * x - 2.0 * x + 1.0);
    float u = fn.x;
    float v = fn.y;
    float du = dfn.x;
    float dv = dfn.y;

    float a = rand(X + vec2(0.0, 0.0));
    float b = rand(X + vec2(1.0, 0.0));
    float c = rand(X + vec2(0.0, 1.0));
    float d = rand(X + vec2(1.0, 1.0));

    float n = 2.0 * ((b - a) * u + (c - a) * v + (a - b - c + d) * u * v + a) - 1.0;
    float dnu = 2.0 * du * ((b - a) + (a - b - c + d) * v);
    float dnv = 2.0 * dv * ((c - a) + (a - b - c + d) * u);
    return vec3(n, dnu, dnv);
}

const mat2 fbmRot2 = mat2(0.8, 0.6,
                         -0.6, 0.8);

const mat2 fbmRot2T = mat2(0.8, -0.6,
                          0.6, 0.8);

vec3 fbm9D(in vec2 x)
{
	float freq = 1.98;
    float noise = 0.0;
    float ampl = 0.8;
    float scale = 0.49;
    vec2 deriv = vec2(0.0);
    mat2 m = mat2(1.0, 0.0, 0.0, 1.0);
    for (int i = 0; i < 9; ++i)
    {
        vec3 res = valueNoiseD(x);  
        deriv += ampl * m * res.yz;
        noise += ampl * res.x / (1.0 + dot(deriv, deriv));
        ampl *= scale;
        x = freq * fbmRot2 * x;
        m = freq * fbmRot2T * m;
    }
    return vec3(noise, deriv);
}

float fbm3D(in vec2 x)
{
	float freq = 2.15;
    float noise = 0.0;
    float ampl = 1.8;
    float scale = 0.45;
    for (int i = 0; i < 3; ++i)
    {
        float res = valueNoise(x);  
        noise += ampl * res;
        ampl *= scale;
        x = freq * fbmRot2 * x;
    }
    return noise;
}

struct SceneData
{
    int materialID;
    float sdf;
};

struct MarchResult
{
    float t;
    int materialID;
};

const vec3 DirectionalLightColor = vec3(1.0, 1.0, 1.0) * 0.9;
const vec3 DirectionalLightDirection = normalize(vec3(-2.0, -1.8, -1.0) - vec3(5.0, -5.0, 5.0 * 2.0));
const vec3 AreaLightColor = vec3(1.0);//vec3(0.909, 0.65, 0.090);//vec3(0.349, 0.909, 0.729);
const vec3 AreaLightPosition = vec3(0.0, 0.55, 2.25);
const vec3 AreaLightNormal = vec3(0.0, 0.0, -1.0);
const vec3 AreaLightSize = vec3(0.732, 0.705, 0.00);

SceneData intersectBox(vec3 p, vec3 b)
{
    vec3 d = abs(p) - b;
    SceneData data;
    data.sdf = min(max(d.x, max(d.y, d.z)), 0.0) + length(max(d, 0.0));
    data.materialID = 1;     
    return data;
}

vec3 getLightIntensity(in vec3 p)
{
    int counter = int((iTime * 5.0)) & 255;
    int shift = int(p.y * 32.0) & 11 + int(p.x * 32.0) & 7;
    int on = (counter >> shift) & 1;
    return vec3(float(on), float(1 - on), 1.0) * 10.0;
}

mat3 getRotation()
{
    float th =(cos(iTime * 2.1)) * 3.14159265 * 0.25; 
    float c = cos(th);
    float s = sin(th);
    mat3 roty = mat3(c, 0.0, s, 0.0, 1.0, 0.0, -s, 0.0, c);
    mat3 rotx = mat3(1.0, 0.0, 0.0, 0.0, 0.8, -0.6, 0.0, 0.6, 0.8);
    return rotx * roty;
}

void sampleAreaLight(in vec2 uv, out vec3 lightPosition, out vec3 lightIntensity)
{
    vec3 normal = transpose(getRotation()) * AreaLightNormal;
    vec3 tangent = normalize(cross(normal, vec3(0.0, 1.0, 0.0)));
    vec3 bitangent = normalize(cross(normal, tangent));
    float u = (0.5 - uv.x) * AreaLightSize.x * 2.0;
    float v = (uv.y - 0.5) * AreaLightSize.y * 2.0;
    lightPosition = AreaLightPosition + u * tangent + v * bitangent;
    lightIntensity = getLightIntensity(lightPosition);  
}

float densityVoumetricFog(in vec3 p)
{
    float res = clamp(fbm3D(p.xz * 0.01 - (iTime * 1.5 - 1.54)) * 0.25 - p.y * 0.02, 0.0, 1.0);

    float fog = (res * 0.8 + 0.1);
    fog = pow(fog, 2.0);
    return fog * 15.0;
}

float rayMarchToAreaLight(in vec3 ro, in vec3 rd)
{
    const float numStep = 4.0;
    float shadow = 1.0;
  	float stepDist = length(rd - ro) / numStep;
    vec3 dir = normalize(rd - ro);
    for(float i = 0.5; i < numStep; i += 1.0)
    {
        vec3 pos = ro + dir * (i / (numStep));
        shadow *= exp(-densityVoumetricFog(pos * 253.0) * stepDist);
    }
    return shadow;
}

void integrateVolumetricFog(in vec3 p, 
                           in vec3 V,
                           in float density,
                           in float d,
                           inout float transmittance, 
                           inout vec3 inscatteredLight,
                           in vec2 fragCoord)
{
    // --- sample a random position on the area light
    transmittance *= exp(-density * d);
    float g = 0.2;
    float u = rand(rand(fragCoord.x * p.x + fragCoord.y + d * 1.5) + 46984.4363);
    float v = rand(rand(fragCoord.y * p.y + fragCoord.x + d * 2.5) + 3428.532546);
    vec3 lightPos;
    vec3 lightCol;
    sampleAreaLight(vec2(u, v), lightPos, lightCol);
    vec3 L = (lightPos - p);
    float G = (dot(normalize(L), transpose(getRotation()) * AreaLightNormal)) / dot(L, L);
    float areaPdf = 1.0 / (AreaLightSize.x * AreaLightSize.y);
    float shadow = rayMarchToAreaLight(p, lightPos);
    float phaseHG = (1.0 / (4.0 * 3.14)) * ((1.0 - g * g) / (pow((1.0 + g * g - 2.0 * g * max(dot(normalize(L), V), 0.0)), 3.0 / 2.0)));
    inscatteredLight += density * transmittance * lightCol * G * phaseHG * d * (1.0 / areaPdf) * shadow;
}

void integrateVolumetricFogFromSampledPosition(in vec3 p, 
                           in vec3 V,
                           in float density,
                           in float d,
                           inout vec3 inscatteredLight,
                           in vec3 lightPos,
                           in vec3 lightCol,
                           in float xPdf)
{
    // same integration as above, but position on the ray is given by equi-angular sampling
    float trans = exp(-density * d);
    float g = 0.2;
    vec3 L = (lightPos - p);
    float G = (dot(normalize(L), transpose(getRotation()) * AreaLightNormal)) / dot(L, L);
    float areaPdf = 1.0 / (AreaLightSize.x * AreaLightSize.y);
    float shadow = rayMarchToAreaLight(p, lightPos);
    float phaseHG = (1.0 / (4.0 * 3.14)) * ((1.0 - g * g) / (pow((1.0 + g * g - 2.0 * g * max(dot(normalize(L), V), 0.0)), 3.0 / 2.0)));
    inscatteredLight += density * trans * lightCol * G * phaseHG  * (1.0 / areaPdf) * (1.0 / xPdf) * shadow;
}

SceneData intersectTerrain(in vec2 p)
{
    p *= 0.4;
    vec3 res = fbm9D(p);
    res.x *= 0.8;
    res.x = res.x + smoothstep(-0.112, 0.1, res.x) * 0.8;
    res.x *= 0.4;
    SceneData data;
    data.sdf = res.x;
    data.materialID = 0;
    return data;
}

vec4 normalTerrain(in vec2 p)
{
   	p *= 0.4;
    vec3 res = fbm9D(p);
    res *= 0.8;
    vec2 mdf = smoothstepd(-0.112, 0.1, res.x);
    res.x = res.x + mdf.x * 0.8;
    res.yz = res.yz + mdf.y * 0.8 * res.yz;
    res.x *= 0.4;
    res.yz *= 0.4;

    return vec4(res.x, normalize(vec3(-res.y, 1.0, -res.z)));
}

SceneData unite(SceneData sd0, SceneData sd1)
{
    if (sd0.sdf < sd1.sdf)
        return sd0;
    else
        return sd1;
}

MarchResult raymarchScene(in vec3 ro, in vec3 rd, out vec4 scattering, in vec2 fragCoord)
{
    float tmin = 0.0;
    float tmax = 50.0;
    float t = tmin;
    float prevt = t;
    float d = 0.0, eps = 0.0;
    float prevd = d;
    float density = 0.0;
    float transmittance = 1.0;
    float stp = 0.0;
    vec3 inscatteredLight = vec3(0.0);
    vec3 inscatteredLightFromEquiAngularSampling = vec3(0.0);
    MarchResult result;
    int stepsEquiAngularSampling = 0;

    for (int i = 0; i < 300; ++i)
    {
        eps = t * 0.001;
        vec3 p = ro + t * rd;    

        density = (densityVoumetricFog(p * 253.0));
        integrateVolumetricFog(p, rd, density, stp, transmittance, inscatteredLight, fragCoord);
        if (stepsEquiAngularSampling < 32)
        {
            float u = rand(rand(fragCoord.x * iResolution.y + fragCoord.y + d * 1.5) + iTime + 1234.32598);
            float v = rand(rand(fragCoord.y * iResolution.x + fragCoord.x + d * 2.5) + iTime * 2.0 + 6234.32598);
            vec3 lightPos;
            vec3 lightCol;
            sampleAreaLight(vec2(u, v), lightPos, lightCol);

            float x0 = rand(rand(fragCoord.y * iResolution.y + fragCoord.x + d * 3.5 + iTime) + 236526.436346);
            // --- equi-angular sampling
            float DT = dot(lightPos - ro, rd);
            float D = length(ro + DT * rd - lightPos);
            float tha = atan(0.0 - DT, D);
            float thb = atan(length(tmax - ro) - DT, D);
            float tsampled = D * tan(mix(tha, thb, x0));
            float pdf = D / ((thb - tha) * (D * D + tsampled * tsampled));
            float x = DT + tsampled;
            vec3 sampledPos = ro + x * rd;

        	float densityFromSampledPos = (densityVoumetricFog(sampledPos * 253.0));
        	integrateVolumetricFogFromSampledPosition(sampledPos, rd, densityFromSampledPos, x, inscatteredLightFromEquiAngularSampling, lightPos, lightCol, pdf); 
        	stepsEquiAngularSampling++;        
        }

        SceneData res = intersectTerrain(p.xz);
        float h = res.sdf;
        d = p.y - h;

        vec3 prot = getRotation() * (p - AreaLightPosition);
        SceneData lightPlane = intersectBox(prot, AreaLightSize);
        SceneData terrain;
        terrain.sdf = d;
        terrain.materialID = res.materialID;
        SceneData scene = unite(terrain, lightPlane);
        d = scene.sdf;
        result.materialID = scene.materialID;
        if (d < eps)
            break;        

        stp = d * 0.32;
        prevt = t;
        prevd = d;
        t += stp;
        if (t > tmax)
            break;
    }
    if (t > tmax)
        t = -1.0;
    else
        t = mix(prevt, t, d/prevd);

    inscatteredLightFromEquiAngularSampling *= 1.0 / float(stepsEquiAngularSampling); 
    scattering = vec4(inscatteredLight + inscatteredLightFromEquiAngularSampling, transmittance);
    result.t = t;
    return result;
}

vec4 renderScene(in vec3 ro, in vec3 rd, in vec2 fragCoord)
{
    vec4 res = vec4(0.0);
    vec4 scattering = vec4(0.0);
    MarchResult m = raymarchScene(ro, rd, scattering, fragCoord);
    if (m.t > 0.0)
    {
        vec3 p = ro + m.t * rd;
        vec3 N = normalTerrain(p.xz).yzw;

        if (m.materialID == 0)
        {
            int numSteps = 256;
            int actualSteps = 0;
            vec3 normal = transpose(getRotation()) * AreaLightNormal;
            vec3 tangent = normalize(cross(normal, vec3(0.0, 1.0, 0.0)));
            vec3 bitangent = normalize(cross(normal, tangent));
            for (int i = 0; i < numSteps; ++i)
            {
                vec3 R = reflect(rd, N);
                float x0 = rand(rand(fragCoord.y * iResolution.y + p.x * p.y * 6543623.22355) +  float(i) * 3428.532546);
                float x1 = rand(rand(fragCoord.x * iResolution.y +  p.z * p.y * 2124435.5346) + float(i) * 9067.214721);
                float x2 = rand(rand(fragCoord.x * iResolution.x +  p.z * p.x * 352445.3466) + float(i) * 6797.745721);
                R = normalize(R + vec3(x0, x1, x2));
                if (dot(R, N) > 0.0)
                {
                	float it = -dot(p - AreaLightPosition, normal) / dot(R, normal);
                	if (it > 0. && it < AreaLightPosition.z)
                	{
	                    vec3 hit = p + it * R;
	                    float x = dot(hit, tangent);
	                    float y = -dot(hit, bitangent);
                    	if (AreaLightPosition.x - AreaLightSize.x < x && x < AreaLightPosition.x + AreaLightSize.x
                            && AreaLightPosition.y - AreaLightSize.y < y && y < AreaLightPosition.y + AreaLightSize.y)
                        {
                            vec3 lightPosition = AreaLightPosition + x * tangent + y * bitangent;
	                        res.xyz += (getLightIntensity(lightPosition)) * (1.0 / float(numSteps));
                	    }
                	}	
                }
            }     
        }
        else if (m.materialID == 1)
        {
            res.xyz = getLightIntensity(p);
        }

        res.xyz = res.xyz * scattering.w + scattering.rgb;
        res.w = 1.0;
    }
    else
        res = vec4(scattering.xyz, 1.0);
    return res;
}

vec3 render(in vec3 ro, in vec3 rd, in vec2 fragCoord)
{        
    vec4 scene = renderScene(ro, rd, fragCoord);
    vec3 color = scene.rgb;

    return color;
}

