void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 pos = fragCoord/iResolution.xy;
    vec2 uv = fragCoord/iResolution.xy;
	pos -= .5;
    pos.x *= iResolution.x/iResolution.y;

    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    float t = iTime;
    float t2 = t*0.2;

    mat2 r1 = mat2(1.0, 2.0,
                   0.1, 0.0);
    mat2 r2 = mat2(2.0, 0.0,
                   0.0, 2.0);
    mat2 r3 = mat2(cos(t2), -sin(t2),
                   sin(t2), cos(t2));
    mat2 r4 = mat2(sin(t2), -cos(t2),
                   cos(t2), sin(t2));

    pos.y *= 3.0;
    pos.xy *= 1.5;

    // Time varying pixel color

    pos.xy = pos.xy * r3;
    pos.xy += pos.xy * r2;
    float f = cos(sin(pos.x * 0.5 + t) * pos.y * 0.5 + t);

    pos.xy += pos.xy * -r4;
    pos.xy += pos.xy * r1;
    float f1 = sin(cos(pos.x * 0.5 + t) * pos.y * 0.5 + t);


    float rd = length(uv);
    vec3 colr = vec3(f);
    vec3 colb = vec3(f1);
    vec3 col = vec3((f - f1) + f);
    vec3 green = vec3(0.2, 1.0, 0.0);
    vec3 green1 = vec3(length(uv), 0.0, 0.0);
    vec3 green2 = green + green1;
    vec3 col2 = col * green2;
    vec3 bg = vec3(rd, 1.0, 0.0);
    vec3 fg = vec3(rd - 0.6, 1.0 - rd, 0.0);

    vec3 col3 = mix(bg, fg, col);



    // Output to screen
    fragColor = vec4(col3, 1.0);
}

// SNOISE function from: https://www.shadertoy.com/view/lsf3RH
float snoise(vec3 uv, float res)
{
	const vec3 s = vec3(1e0, 1e2, 1e3);

	uv *= res;

	vec3 uv0 = floor(mod(uv, res))*s;
	vec3 uv1 = floor(mod(uv+vec3(1.), res))*s;

	vec3 f = fract(uv); f = f*f*(3.0-2.0*f);

	vec4 v = vec4(uv0.x+uv0.y+uv0.z, uv1.x+uv0.y+uv0.z,
		      	  uv0.x+uv1.y+uv0.z, uv1.x+uv1.y+uv0.z);

	vec4 r = fract(sin(v*1e-1)*1e3);
	float r0 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	r = fract(sin((v + uv1.z - uv0.z)*1e-1)*1e3);
	float r1 = mix(mix(r.x, r.y, f.x), mix(r.z, r.w, f.x), f.y);

	return mix(r0, r1, f.z)*2.-1.;
}

float noise(vec2 uv, float baseres)
{
    float n = 0.0;
    for (int i = 0; i < 7; i++)
    {
        float v = pow(2.0, float(i));
        n += (1.5 / v) * snoise(vec3(uv + vec2(1.,1.) * (float(i) / 17.), 1), v * baseres);
    }


    return clamp((1.0 - n) * .5, 0., 1.);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);

    // Tweaking vars
    vec4 color = vec4(0.925, 0.291, 0.023, 1.0);
    vec2 tiling = vec2(1.0, .6);
    vec2 speed1 = vec2(0.05, -0.32);
    vec2 speed2 = vec2(-0.10, -0.39);
    float ss_min = 0.2;
    float ss_max = ss_min + 0.5;
    float noise_sz = 8.0f;

    // Tiled UV
    vec2 tuv = vec2(mod(uv.x * tiling.x, 1.0), mod(uv.y * tiling.y, 1.0));

    // Samplings
    vec2 uv1 = tuv + (speed1 * iTime);
    vec2 uv2 = tuv + (speed2 * iTime);

    // Mask
    vec2 uvt = vec2(uv.x, pow(1.0 - uv.y, 2.2));
    vec2 nuvt = (uvt * 2.0) - 1.0;    
    float v = 1. - length(nuvt);
    v = ((v * .6) + (pow(v, 4.3) * 4.55));

    float samplings = noise(uv1, noise_sz) * noise(uv2, noise_sz);
    float mask = pow(v, 1.2) * samplings;
    float amask = smoothstep(ss_min, ss_max, mask);

    // Output to screen
    fragColor = color + (color * pow((1.0 - uv.y), 3.0) * .3);
    fragColor += samplings * .3;
    fragColor *= (2. * amask);
}

