//spheres and coloured lights from an orthographic projection, with analytical antialiasing and shadows.



#define NUMBER_OF_SPHERES   20
#define NUMBER_OF_LIGHTS    7
#define SURFACE_BRIGHTNESS	0.01


//Disable SIMPLESCENE for performance gains in scenes with a greater numbers of lights and spheres
#define SIMPLESCENE


struct light {
	vec3 pos;
    vec3 col;
};

//hsl to rgb conversion adapted from https://gist.github.com/mjackson/5311256
float hue2rgb( float p, float q, float h ) {
	h = fract(h);
    if(h<0.1616) return p+(q-p)*6.0*h;
    else if(h<0.5) return q;
    else if(h<0.666) return p+(q-p)*(0.666-h)*6.0;
    else return p;
}


vec3 hsltorgb( vec3 hsl ) {
    float h = hsl.x;
    float s = hsl.y;
    float l = hsl.z;
	float q = l<0.5 ? l*(1.0+s) : l+s-l*s;
    float p = 2.0*l-q;
    float r = hue2rgb(p, q, h+0.333);
    float g = hue2rgb(p, q, h);
    float b = hue2rgb(p, q, h-0.333);
    return vec3(r,g,b);
}


// https://www.shadertoy.com/view/4djSRW
vec3 hash( float p ) {
   vec3 p3 = fract(vec3(p) * vec3(0.1031,0.1030,0.0973));
   p3 += dot(p3, p3.yzx+19.19);
   return fract((p3.xxy+p3.yzz)*p3.zyx); 
}


vec3 getnearestpointonlinesegment( vec3 pa, vec3 pb, vec3 t) {
    vec3 l = pb-pa;
    float h = clamp( dot(t-pa,l)/dot(l,l), 0.0, 1.0 );	
    return pa+l*h;
}


vec2 orbit( float r, float s, float p ) {
    r *= sin(iTime*p);
    s *= iTime;
    return vec2( sin(s)*r, cos(s)*r );
}


float distancetocoverage( float d ) {
    return clamp(d*iResolution.y, 0.0, 1.0);
}


float circle( vec2 uv, vec2 p, float r ) {
    float d = r-distance(uv,p);
    return distancetocoverage(d);
}


float sphereshadow( vec3 uv, vec3 lp, vec3 cp, float cr ) {
	vec3 np = getnearestpointonlinesegment(uv, lp, cp);
    float d = cr-distance(np,cp);
  	return distancetocoverage(d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.y;

    vec2 center = vec2(0.5*(iResolution.x/iResolution.y), 0.5);

    vec3 spheres[NUMBER_OF_SPHERES];
    for (int i = 0; i<spheres.length(); i++ ) {
        vec3 h = hash(float(i+500));
        float px = h.x * iResolution.x/iResolution.y;
        float py = h.y;
        float r = mix(0.025, 0.1, h.z);
    	spheres[i] = vec3( px, py, r );
    }

    light lights[NUMBER_OF_LIGHTS];
    for (int i = 0; i<lights.length(); i++ ) {
        vec3 h = hash(float(i+200));
        float d = mix(0.25, 0.8, h.x);								//orbital distance
        float s = mix(0.12, 0.5, abs(h.y*2.0-1.0))*sign(h.y-0.5);	//orbital speed
        float p = mix(0.1, 0.35, h.z);								//occilation speed
        lights[i].pos = vec3(center+orbit(d,s,p), 0.101+sin(iTime+float(i))*0.1);

        //vec3 hc = hash(float(i)+157.0);
        //lights[i].col = hc*hc*hc;
		vec3 hsl = vec3( (float(i)+iTime*0.5)/float(NUMBER_OF_LIGHTS), 0.95, 0.2 );
        lights[i].col = hsltorgb( hsl );
    }
    if(iMouse.w>0.0) lights[0].pos.xy=iMouse.xy/iResolution.y;

    vec3 col = vec3(0.0);




    #ifdef SIMPLESCENE



    for ( int i = 0; i<lights.length(); i++ ) {
        //get ground lighting
        float f = dot(normalize(lights[i].pos-vec3(uv,0.0)), vec3(0.0,0.0,1.0));	//Lambertian reflection from floor
        for ( int j = 0; j<spheres.length(); j++ ) {
            f *= 1.0 - sphereshadow( vec3(uv,0.0), lights[i].pos, vec3(spheres[j].xy,0.0), spheres[j].z );
            if (f==0.0) break;
        }

        float c = 0.0;			//sphere lighting
        float pz = 0.0;			//height of pixel above floor plane
        int id = -1;			//index of sphere under pixel (-1 if there is none)

        //get sphere lighting
        for ( int j = 0; j<spheres.length(); j++ ) {
            if (distance(lights[i].pos, vec3(spheres[j].xy,0.0)) < spheres[j].z) {
                break;	//light is inside sphere
            }
            float p = distance(uv, spheres[j].xy);
            float cz = sqrt(spheres[j].z*spheres[j].z-p*p);			//height of pixel on sphere
            if ( cz>pz ) {
            	pz = cz;
                id = j;
            	vec3 nor = normalize(vec3(uv-spheres[j].xy, pz));	//sphere normal
        		vec3 rd = normalize(lights[i].pos-vec3(uv,pz));			//light ray direction (from surface to light)
                float r = max(dot(rd, nor),0.0);					//Lambertian reflection
        		c = r * circle(uv, spheres[j].xy, spheres[j].z);	//multiply by circle coverage
            }
        }

        //if pixel is on a sphere, search for shadows cast on sphere
        if ( id!=-1 && c>0.0 ) {
        	for ( int j = 0; j<spheres.length(); j++ ) {
                c *= 1.0 - sphereshadow( vec3(uv, pz), lights[i].pos, vec3(spheres[j].xy,0.0), spheres[j].z );
        	}
        }

        col += ( (f + c) * SURFACE_BRIGHTNESS / pow(distance(vec3(uv, pz), lights[i].pos),2.0) ) * lights[i].col;
    }



    #else



    int id = -1;						//index of sphere under pixel (-1 if there is none)
    float pz = 0.0;						//height of pixel above floor plane
    for (int i = 0; i<spheres.length(); i++ ) {
        vec2 sr = spheres[i].xy-uv;
        float cz = spheres[i].z*spheres[i].z - dot(sr, sr);		//(pixel height on spheres[i]) ^ 2
        if ( cz>pz*pz ) {
        	pz = sqrt(cz);
            id = i;
        }
    }

    float coverage = 0.0;				//sphere coverage of pixel
    vec3 nor = vec3(0.0,0.0,1.0);		//surface normal
    if (id!=-1) {
    	coverage = circle(uv, spheres[id].xy, spheres[id].z);
        nor = normalize( vec3(uv-spheres[id].xy, pz) );
    }

    for ( int i = 0; i<lights.length(); i++ ) {      
        //get shadow coverage
        float illumination = 1.0;
        for ( int j = 0; j<spheres.length() && illumination>0.0; j++ ) {
        	illumination *= 1.0 - sphereshadow( vec3(uv,pz), lights[i].pos, vec3(spheres[j].xy,0.0), spheres[j].z );
        }

        //pz = mix(0.0, pz, coverage);
        //nor = mix( vec3(0.0,0.0,1.0), nor, coverage );
        //nor = normalize(nor);

        vec3 rv = lights[i].pos-vec3(uv,pz);
        vec3 rd = normalize(rv);					//light ray direction (from surface to light)
        float gr = max(rd.z,0.0) * (1.0-sphereshadow(vec3(uv,0.0),lights[i].pos,vec3(spheres[id].xy,0.0),spheres[id].z)); //Lambertian reflection from ground plane
        //float gr = max(rd.z,0.0) * (1.0-coverage);
        float sr = max(dot(rd, nor),0.0) * coverage;	//Lambertian reflection from sphere surface

        float f = gr + sr;
        f *= illumination * SURFACE_BRIGHTNESS / dot(rv,rv);
        col += f * lights[i].col;
    }


    #endif

    col *= 2.0;

    //filmic tone mapping -- http://filmicworlds.com/blog/filmic-tonemapping-operators/
    col = max(col-0.004, 0.0);
    col = (col*(6.2*col+0.5))/(col*(6.2*col+1.7)+0.06);

    col += (hash(uv.x*uv.y*iTime*1000.0)-0.5) / 128.0;		//grain

    fragColor = vec4(col,1.0);
}

#define PI 3.1415926536
#define NUM_DOTS_IN_RING 64
#define MAGIC_CONST ((2.0*PI)/float(NUM_DOTS_IN_RING))
#define DOT_SIZE 0.1
#define NUM_RINGS 64
#define RING_SCALE 0.05
#define EPSILON 0.00001
#define BASE_SIZE 0.001
#define SPEED 8.0

float circ( vec2 uv, vec2 cPos, float cSize )
{
    return 1.0-smoothstep(0.0, cSize, length(uv-cPos))/cSize;
}

float ring( vec2 uv, vec2 rPos, float rSize )
{
    //center ourselves about the ring
    vec2 myPos = uv - rPos;

    //some polar fun
    float angle = atan(myPos.y, myPos.x);

    float c = MAGIC_CONST;
    float nearestAngle = c*round(angle/c);
    //return vec3(nearestAngle/(2.0*PI));
    vec2 nearestDotPos;
    nearestDotPos.x = cos(nearestAngle)*rSize;
    nearestDotPos.y = sin(nearestAngle)*rSize;

    float dotSize = DOT_SIZE;
    return circ(myPos, nearestDotPos, dotSize);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x/iResolution.y;

    float col;

    for(int i = NUM_RINGS+1; i > 0; i--) {
        float fi = float(i);
        float scaledTime = SPEED*iTime;
        float scale = mod(scaledTime, 1.0);
        float size = (fi+scale)*RING_SCALE+BASE_SIZE;
        float ringNum = scale+fi;
        float move = (iTime+ringNum/16.0);
        col = ring(uv, vec2(cos(move*0.7)*1.5, sin(cos(move)*2.5)*0.5), size);
        //float col = ring(uv, vec2(0.0), size);
        if(col > EPSILON) {
            float colRange = mod((scaledTime-fi), 8.0);
            col /= 1.0+float(colRange < 4.0);
            break;
        }
    }


    // Output to screen
    fragColor = vec4(vec3(col), 1.0);
}

/*
 * This shader displays a simple scene built using union, intersect, and subtract
 * operations on signed distance fields. 
 */

const int line_dist = 15;

// defines the distance to the surface of a square
// based on an example from this fantastic article: http://jamie-wong.com/2016/07/15/ray-marching-signed-distance-functions/
float sdf_square(vec2 p, vec2 o, float s)
{
    vec2 d = abs(p - o) - vec2(s, s);
    float inside = min(max(d.x, d.y), 0.0);
    float outside = length(max(d, 0.0));
    return inside + outside;
}

// defines the distance to the surface of a circle
// distance from p to origin minus radius
float sdf_circle(vec2 p, vec2 o, float r)
{
    return length(p - o) - r;
}

// pick the closer of two items
float sdf_union(float a, float b) 
{    
    return min(a, b);
}

// pick the further of two items
float sdf_intersect(float a, float b)
{
    return max(a, b);
}

// intersect an item and an inside-out item
float sdf_sub(float a, float b)
{
    return max(a, -b);
}

// percent of screen size
vec2 svec(float x, float y)
{
    return iResolution.xy * vec2(x, y);
}

float psize(float percent)
{
    return iResolution.x * percent;
}

float get_world(vec2 p)
{
    // small circle in bottom right
    float a = sdf_circle(p, svec(0.1, 0.1), psize(0.1));

    // medium circle in center top
    float b = sdf_circle(p, svec(0.5, 0.7), psize(0.2));

    // small circle at center of b
    float b2= sdf_circle(p, svec(0.5, 0.7), psize(0.05));

    // square that moves in a circle around b
    float c = sdf_square(p, svec(0.5 + sin(iTime) / 10.0, 0.7 + cos(iTime) / 10.0), psize(0.15));

    // large circle centered to right of screen
    float d = sdf_circle(p, svec(1.1, 0.5), psize(0.3));

    // large circle at bottom right of screen
    float e = sdf_circle(p, svec(1.1, 0.0), psize(0.3));

    return
        sdf_union(
            a,
            sdf_union(                
                sdf_intersect(
                    sdf_sub(b, b2), 
                    c
                ),                                    
                sdf_sub(d, e)
            )
        );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord;

    float world = get_world(fragCoord);

    // subtract a square at mouse from world
    world = 
        sdf_sub(
            world,
            sdf_square(p, iMouse.xy, 30.0)
        );

    // normalize distance to fit within canvas
    float dcolor = pow( world / iResolution.x, 0.3);

    // pick a cool looking background color
	vec4 bg = vec4(dcolor, 0.0, 1.0 - dcolor, 1.0);

    // if inside an object
    if (world < 0.0)     
        fragColor = vec4(0.6, 0.1, 0.1, 1.0); // red!

    // if on a line of constant distance from the nearest surface (+ animation!)
    else if (int(world) % line_dist == line_dist - (int(iTime * 20.0) % line_dist) - 1)  
        fragColor = vec4(bg.x / 1.2, bg.y / 1.2, bg.z / 1.2, 1.0);    

    // otherwise, draw the cool looking background color
    else    
        fragColor = bg;
}

