    uv.x *= iResolution.x/iResolution.y;

    vec4 finalColor = vec4(0.);

    // Adding the efect Additiv
	AddPannedGrid(finalColor, uv, vec4(0.4,0.7,1.,0.5), vec2(.4,.7), 13., 123.);
    AddPannedGrid(finalColor, uv, vec4(0.4,0.7,1.,0.9), vec2(.4,.7), 10.,45.26);
    AddPannedGrid(finalColor, uv, vec4(0.4,0.7,1.,0.2), vec2(.4,.9), 16.,53.1);

    // Output to screen
    fragColor = finalColor;
}

vec3 bgColor = vec3(0.01, 0.45, 0.1);
vec3 rectColor = vec3(0.01, 0.3, 0.1);

//noise background
const float noiseIntensity = 2.8;
const float noiseDefinition = 0.6;
const vec2 glowPos = vec2(-2., 0.);

//rectangles
const float total = 60.;//number of rectangles
const float minSize = 0.03;//rectangle min size
const float maxSize = 0.08-minSize;//rectangle max size
const float yDistribution = 0.5;


float random(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

float noise( in vec2 p )
{
    p*=noiseIntensity;
    vec2 i = floor( p );
    vec2 f = fract( p );
	vec2 u = f*f*(3.0-2.0*f);
    return mix( mix( random( i + vec2(0.0,0.0) ), 
                     random( i + vec2(1.0,0.0) ), u.x),
                mix( random( i + vec2(0.0,1.0) ), 
                     random( i + vec2(1.0,1.0) ), u.x), u.y);
}

float fbm( in vec2 uv )
{	
	uv *= 5.0;
    mat2 m = mat2( 1.6,  1.2, -1.2,  1.6 );
    float f  = 0.5000*noise( uv ); uv = m*uv;
    f += 0.2500*noise( uv ); uv = m*uv;
    f += 0.1250*noise( uv ); uv = m*uv;
    f += 0.0625*noise( uv ); uv = m*uv;

	f = 0.5 + 0.5*f;
    return f;
}

vec3 bg(vec2 uv )
{
    float velocity = iTime/1.6;
    float intensity = sin(uv.x*3.+velocity*2.)*1.1+1.5;
    uv.y -= 2.;
    vec2 bp = uv+glowPos;
    uv *= noiseDefinition;

    //ripple
    float rb = fbm(vec2(uv.x*.5-velocity*.03, uv.y))*.1;
    //rb = sqrt(rb); 
    uv += rb;

    //coloring
    float rz = fbm(uv*.9+vec2(velocity*.35, 0.0));
    rz *= dot(bp*intensity,bp)+1.2;

    //bazooca line
    //rz *= sin(uv.x*.5+velocity*.8);


    vec3 col = bgColor/(.1-rz);
    return sqrt(abs(col));
}


float rectangle(vec2 uv, vec2 pos, float width, float height, float blur) {

    pos = (vec2(width, height) + .01)/2. - abs(uv - pos);
    pos = smoothstep(0., blur , pos);
    return pos.x * pos.y; 

}

mat2 rotate2d(float _angle){
    return mat2(cos(_angle),-sin(_angle),
                sin(_angle),cos(_angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy * 2. - 1.;
    uv.x *= iResolution.x/iResolution.y;

    //bg
    vec3 color = bg(uv)*(2.-abs(uv.y*2.));

    //rectangles
    float velX = -iTime/8.;
    float velY = iTime/10.;
    for(float i=0.; i<total; i++){
        float index = i/total;
        float rnd = random(vec2(index));
        vec3 pos = vec3(0, 0., 0.);
        pos.x = fract(velX*rnd+index)*4.-2.0;
        pos.y = sin(index*rnd*1000.+velY) * yDistribution;
        pos.z = maxSize*rnd+minSize;
        vec2 uvRot = uv - pos.xy + pos.z/2.;
    	uvRot = rotate2d( i+iTime/2. ) * uvRot;
        uvRot += pos.xy+pos.z/2.;
        float rect = rectangle(uvRot, pos.xy, pos.z, pos.z, (maxSize+minSize-pos.z)/2.);
	    color += rectColor * rect * pos.z/maxSize;
    }

	fragColor = vec4(color, 1.0);
}

// Various knobs to twiddle
#define LEVELS 8
#define OVERSAMPLE_PIX 4
#define OVERSAMPLE_PIX_WIDTH 0.001
#define OVERSAMPLE_TEX 16
#define LARGE_NUMBER 900000.0

bool is_vr = false;
vec3 vr_ori = vec3(0.0);
vec3 vr_ray = vec3(0.0);

// https://gist.github.com/patriciogonzalezvivo/670c22f3966e662d2f83
float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}
vec3 hash33(vec3 p){ 
    float n = sin(dot(p, vec3(7, 157, 113)));    
    return fract(vec3(2097152, 262144, 32768) * n); 
}

// Trefoil knot positions
vec3 trefoil(float t) {
	return vec3(
        sin(t) + 2.0 * sin(2.0 * t),
        cos(t) - 2.0 * cos(2.0 * t),
        -sin(3.0 * t)
    );
}

// Ray-sphere and ray-plane intersections 
float sphere_intersect(vec3 pos, float radius, vec3 eye, vec3 ray) {
    vec3 pos_to_eye = eye - pos;
    float ray_dot_dir = dot(ray, pos_to_eye);
    float determ = ray_dot_dir * ray_dot_dir - dot(pos_to_eye, pos_to_eye) + radius * radius;
    if(determ < 0.0 || -ray_dot_dir - sqrt(determ) < 0.0) {
    	return LARGE_NUMBER;   
    }
    return -ray_dot_dir - sqrt(determ);
}

float xzplane_intersect(float height, vec3 eye, vec3 ray) {
    float determ = (height - eye.y) / ray.y;
    if(determ <= 0.0) {
    	return LARGE_NUMBER;   
    }
    return determ;
}



// View setup
void camera(vec2 coords, out vec3 eye, out vec3 ray) {
    // Calculate an eye position
    eye = vec3(sin(iTime) * 0.5, sin(iTime * 0.3) * 0.5 + 0.5, iTime * 16.0);

    // Camera as eye + imaginary screen at a distance
    vec3 lookat = vec3(0.0, 0.0, iTime * 16.0 + 2.0);
    vec3 lookdir = normalize(lookat - eye);
    vec3 left = normalize(cross(lookdir, vec3(0.0, 1.0, 0.0)));
    vec3 up = normalize(cross(left, lookdir));
    vec3 lookcenter = eye + lookdir;
	vec3 pixelpos = lookcenter + coords.x * left + coords.y * up;
    ray = normalize(pixelpos - eye);
}

// Raytrace the scene
vec3 sphere_pos(vec3 eye, float spherenum) {
	vec3 trefoil_offset = trefoil(3.14 * 2.0 * spherenum / 7.0 + iTime).xzy * 0.7;
	return eye + trefoil_offset + vec3(0.0, -1.0, 4.0);
}

vec4 trace(vec3 eye, vec3 ray) {
    float plane_hit = xzplane_intersect(-2.0, eye, ray);
    float hit_dist = plane_hit;

    vec3 sphere_c_a = sphere_pos(eye, 1.0);
	float sphere_hit_a = sphere_intersect(sphere_c_a, 0.5, eye, ray);
    hit_dist = min(hit_dist, sphere_hit_a);

    vec3 sphere_c_b = sphere_pos(eye, 2.0);
	float sphere_hit_b = sphere_intersect(sphere_c_b, 0.5, eye, ray);
    hit_dist = min(hit_dist, sphere_hit_b);

    vec3 sphere_c_c = sphere_pos(eye, 3.0);
	float sphere_hit_c = sphere_intersect(sphere_c_c, 0.5, eye, ray);
    hit_dist = min(hit_dist, sphere_hit_c);

    float hit_obj = -1.0;
    hit_obj = hit_dist == plane_hit ? 1.0 : hit_obj;
    hit_obj = hit_dist == sphere_hit_a ? 2.0 : hit_obj;
    hit_obj = hit_dist == sphere_hit_b ? 3.0 : hit_obj;
    hit_obj = hit_dist == sphere_hit_c ? 4.0 : hit_obj;
    hit_obj = hit_dist == LARGE_NUMBER ? -1.0 : hit_obj;

    return(vec4(eye + ray * hit_dist, hit_obj));
}

// Colour a non-hit
vec3 background(vec3 dir) {
    float rotval = atan(dir.x + 0.5, dir.y) + iTime * 0.1;
    float noiseval = mod(atan(dir.y, dir.z), 0.1);
    noiseval = rand(vec2(rotval, noiseval)) > 0.5 ? 1.0 : 0.8;
    float dirs = mod(rotval + 0.08, 0.4);
    float diry = mod(rotval, 0.2) < 0.04 ? 1.0 : abs(mod(rotval, 0.2) - 0.12) * 5.0;
    //return texture(iChannel0, 2.0 * vec2((dir.x + 0.5) / 2.0, dir.y)).rgb;
    if(dirs > 0.2) {
    	return vec3(diry, 0.0, diry) * noiseval;
    }
    else {
        return vec3(0.0, diry, diry) * noiseval;
    }
}

// Colour anything whatsoever
vec3 shade(vec3 pos, vec3 dir, float hit_obj, vec3 stack_color) {
    if(hit_obj == 1.0) {
        return vec3(mod(floor(pos.x) + floor(pos.z), 2.0));
    }
    if(hit_obj == LARGE_NUMBER) {
    	return background(dir);
    }
   	return stack_color;
}

// Oversample textures
vec3 shade_supersample(vec3 pos, vec3 dir, float hit_obj, vec3 stack_color) {
    vec3 ddx_a = pos - dFdx(pos);
    vec3 ddy_a = pos - dFdy(pos);
	vec3 ddx_b = pos + dFdx(pos);
    vec3 ddy_b = pos + dFdy(pos);

    vec3 shade_sum = vec3(0.0);
    for(int i = 0; i < OVERSAMPLE_TEX; i++) {
    	vec2 samp_off = vec2(
            rand(vec2(length(pos * 1000.0), 2000.0 * float(i))),
			rand(vec2(length(pos * 7000.0), 3700.0 * float(i)))
        );
        vec3 samp_pos_x = mix(ddx_a, ddx_b, abs(samp_off.x)) * 0.5;
        vec3 samp_pos_y = mix(ddy_a, ddy_b, abs(samp_off.y)) * 0.5;
        shade_sum += shade(samp_pos_x + samp_pos_y, dir, hit_obj, stack_color);
    }
    return(shade_sum / float(OVERSAMPLE_TEX));
}

// One pixel
vec3 pixel(vec2 coords) {
	vec4 hit[LEVELS];
    vec3 ray[LEVELS];
    vec3 eye;

   	camera(coords, eye, ray[0]);
    if(is_vr) {
    	eye = vr_ori;
        ray[0] = vr_ori;
    }
    hit[0] = trace(eye, ray[0]);

    // Trace all hits
    for(int i = 1; i < LEVELS; i++) {
        // Hit sphere?
        if(hit[i - 1].w >= 2.0 && hit[i - 1].w < LARGE_NUMBER) {
			vec3 sphere_c = sphere_pos(eye, hit[i - 1].w  - 1.0);            
            vec3 sphere_n = normalize(hit[i - 1].xyz - sphere_c);
           	ray[i] = reflect(ray[i - 1], sphere_n);
            hit[i] = trace(hit[i - 1].xyz + sphere_n * 0.1, ray[i]);
        }
        else {
         	hit[i].w = LARGE_NUMBER;
        }
    }

    // Shade
    vec3 stackColor = vec3(0.1);
    for(int i = LEVELS - 1; i >= 0; i--) {
        vec3 dir = ray[0];
        if(i != 0) {
            dir = ray[i - 1];   
        }
        stackColor = shade_supersample(hit[i].xyz, dir, hit[i].w, stackColor);
    }
    return stackColor;
}

// Image
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 coords = (2.0 * fragCoord.xy  - iResolution.xy) / max(iResolution.x, iResolution.y);
    fragColor.rgb = vec3(0.0);
    for(int i = 0; i < OVERSAMPLE_PIX; i++) {
        vec2 offset =  hash33(vec3(coords.x, coords.y, float(i))).xy;
    	fragColor.rgb += pixel(coords + offset * OVERSAMPLE_PIX_WIDTH);
    }
    fragColor.rgb /= float(OVERSAMPLE_PIX);
}

void mainVR( out vec4 fragColor, in vec2 fragCoord, in vec3 fragRayOri, in vec3 fragRayDir ) {
    is_vr = true;
    vec2 coords = (2.0 * fragCoord.xy  - iResolution.xy) / max(iResolution.x, iResolution.y);
    fragColor.rgb = vec3(0.0);
    for(int i = 0; i < OVERSAMPLE_PIX; i++) {
        vec2 offset =  hash33(vec3(coords.x, coords.y, float(i))).xy;
    	fragColor.rgb += pixel(coords + offset * OVERSAMPLE_PIX_WIDTH);
    }
    fragColor.rgb /= float(OVERSAMPLE_PIX);
}

#define pi 3.14159265359
//this noise stuff is from iq thanks
float hash (vec2 v) {
    v = floor(v);
    return fract(67.3249*sin(17.1234*length(v-vec2(34.14,123.))));
}
float noise (vec2 v) {
    vec4 n = vec4(floor(v),ceil(v));
    vec4 h = vec4(hash(n.xy),hash(n.zy),hash(n.xw),hash(n.zw));
    return mix(mix(h.x,h.y,v.x-n.x),mix(h.z,h.w,v.x-n.x),v.y-n.y);
}
mat2 r (float a) {
    float s = sin(a), c = cos(a);
    return mat2(s,-c,c,s);
}
float no (vec2 v) {
    float c = 0.;
    for (int i = 1; i < 10; i++) {
        v = 2.*r(0.2944*pi)*v;
        c += 0.2*noise(v)/(1.+length(sin(0.5*v)));
    }
    return c;
}
// flow stuff is from trirop https://www.shadertoy.com/view/MsScWD very cool
vec2 ff (vec2 v) {
    	float n = 1.+0.5*noise(v);
		return 
	        sign(v.y-0.5)*50.*vec2(n*sin(1.4*v.y),0.)/(v.y+3.5)+
	        .1*vec2(sin(-12.*v.y*n),cos(13.*v.x))+
	        1.8*vec2(cos(-6.*v.y),sin(4.*v.x))+
	        1.2*vec2(sin(-1.4*v.y),cos(1.5*v.x))+
	        2.0*vec2(sin(-.5*v.y),cos(.6*v.x))+
	        0.8*vec2(sin(-.2*v.y),cos(.2*v.x*n))
	     ;}
bool star = false;
vec2 mouse;
void sphere (inout vec3 p, inout vec3 d) {
	float r = .7, dp = dot(d,p), pp = dot(p,p), det = dp*dp+r*r-pp;
    if (det < 0.) star = true;
    float x = -dp+sqrt(det);
    p = (p+d*x);
    d = reflect(normalize(p),d);
}

vec3 surface (vec2 uv) {
	vec3 col = 0.*vec3(7.-abs(uv.y))*no (uv);
    for (int i = 0; i < 45; i++) {
		uv += 0.01*(2.+1.5*sin(.1*iTime))*ff(uv);
    }
    float j = no(0.1*uv*pi);
    vec3 c = sin(j*vec3(1,2,3));
    col += abs(mix(c*c*c,vec3(j),abs(1.-uv.y/7.5)));
    return col;
}
vec3 stars (vec2 v) {
	return vec3(pow(1.35*no(0.1*iTime+5.*mouse+3.*v/dot(v,v)),7.));
}
