float the = iTime * 0.50;

r.xz *=mat2(cos(the), -sin(the), sin(the), cos(the));

vec3 o = vec3 (iTime, 0.0, 0.0 );

float t = trace(o, r);

float fog = 1.0 / (1.0 + t * t * 0.1);

vec3 fc = vec3(fog);
    fc.r = 0.1;

fragColor = vec4(fc,10.0);
}

#define N (10)
#define OFFSET (1.0 + SHRINK)

float circle(in vec2 uv, in vec2 pos, in float r)
{
    float d = length(uv - pos);
    return smoothstep( 1./iResolution.x, 0., abs( d - r ) ) ;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.x;

    float t = iTime * 1.25;

    uv.y += 0.05;

    vec2 pos = vec2(0);
    float r = 0.1;
    vec2 m = vec2(1., 0.);

    vec3 centers[N];
	float c = 0.;

    float SHRINK = 0.05 + 0.51 * (0.5 + 0.5 * sin(t));

    for( int i = 0; i < N; i++)
    {
        c = max( c, circle(uv, pos, r) );

        centers[i] = vec3(pos, r);

        pos += OFFSET * r * m;
        r *= SHRINK;
    }

    m = vec2(-m.y, m.x);
    for( int k = 0; k < N-1; k++)
    {
        for( int i = 0; i < N; i++)
        {
            vec3 stored = centers[i];
            vec2 pos = stored.xy;
            float r = stored.z;

            c = max( c, circle(uv, pos + OFFSET * r * m, r * SHRINK ) );
            c = max( c, circle(uv, pos - OFFSET * r * m, r * SHRINK ) );
            c = max( c, circle(uv, pos - OFFSET * r * vec2(-m.y, m.x), r * SHRINK ) );

            pos += OFFSET * r * m;
            r *= SHRINK;
            centers[i] = vec3(pos, r);
        }
        m = vec2(-m.y, m.x);
    }
    fragColor = vec4(sqrt(max(c, 0.))); // Approximate gamma correction.
}

