#define ITER 64.
#define PI 3.141592
#define STP 3

float g = 0.; 

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}


float stmin(float a, float b, float k, float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b),0.5 * (u+a+abs(mod(u-a+st,2.*st)-st)));
}

// iq's palette http://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{return a+b*cos(2.*PI*(c*(t+d)));}


vec2 mo (vec2 p, vec2 d)
{
    p.x = abs(p.x) - d.x;
    p.y = abs(p.y)-d.y;
    if (p.y > p.x) p.xy = p.yx;
    return p;
}


float sphe (vec3 p, float r)
{return length(p)-r;}


float box (vec3 p, vec3 c)
{
    float per = 8.;
    p.x = mod(p.x-per/2., per)-per/2.;
    return length(max(abs(p)-c,0.));
}


float cyl(vec2 p, float r)
{return length(p)-r;}


float prim1 (vec3 p)
{
    p.xy *= rot(iTime);
    p.yz *= rot(iTime);
    float c = min(cyl(vec2(p.x+sin(p.y*0.8),p.z), 0.2), cyl(vec2(p.y, p.z+cos(p.x)), 0.2));
    float bs = box(p, vec3(1.));
    return stmin(c, bs, 0.8, 5.);

}


float fractal(vec3 p)
{
    float prim = prim1(p);
    for (int i=0; i<STP; i++)
    {

        p.yz = mo(p.yz, vec2(2.));
        p.xy = mo(p.xy, vec2(2.));
        p.xy -= 2.;
        prim = stmin(prim,prim1(p), 0.9, 5.);       
    }
    return prim;
}


float SDF (vec3 p)
{
    p = abs(p);
    float d = fractal(p);
    g += 0.01/(0.01+d*d);
    return d;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.001,0.001,-15.); vec3 p = ro;
    vec3 dir = normalize(vec3 (uv, 1.));

    float shad = 0.;

    for (float i =0.; i<ITER; i++)
    {
        float d = SDF(p);
        if (d<0.001)
        {
            shad = i/ITER;
            break;
        }
        p+=d*dir*0.7;
    }

    float t = length(ro-p);

    vec3 pal = palette(length(uv),
                      vec3(0.5),
                      vec3(0.5),
                      vec3 (1.2),
                      vec3(0.1,0.2,0.3));

    vec3 c = mix(vec3(shad), pal,abs(uv.x+uv.y*0.5));	
    // glow from lsdlive, originally from balkhan : https://www.shadertoy.com/view/4t2yW1
	c += g*0.08;

    // Output to screen
    fragColor = vec4(c,1.0);
}

// thx to hg (http://mercury.sexy/hg_sdf)
void pR(inout vec2 p, float a) {
	p = cos(a)*p + sin(a)*vec2(p.y, -p.x);
}

vec3 scene(vec3 p)
{
    pR(p.zx, floor(iTime * 0.5));

    p.y += iTime * 0.001;

    pR(p.yz, iTime * 0.2);
    pR(p.zx, iTime * -0.4);

    p *= 2.5 + sin(iTime / 3.);
    vec3 cell = floor(p);
    p = fract(p + 0.5) - 0.5;

    float grid = length(p - clamp(p, -0.1, 0.1));
    vec3 gridc = vec3(step(0.11, grid * 0.2)) * 0.01;

    pR(p.xy, iTime * 3.);
    float cube = 1. / length(p - clamp(p, -0.1, 0.1)) * 0.0004;
    vec3 cubec = min(vec3(cube), p);

    vec3 c = max(gridc, cubec);
    return c;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy - (iResolution.xy * 0.5)) / iResolution.yy;
    vec3 c = vec3(0.15);

    for(float z = 0.; z < 0.5; z += 1. / 250.)
    {
        c += scene(vec3(uv, z));
    }
    c = clamp(c, 0., 1.);

    float inv = mod(floor((iTime + 1.0) * 0.5), 2.);
    c = (1.0 - c) * inv + c * (abs(inv - 1.0));

    c -= smoothstep(0.55, 1.3, abs(uv.x)) * 0.2; // vignette x
    c -= smoothstep(0.17, 0.7, abs(uv.y)) * 0.2; // vignette y
    c += sin(iTime * 0.4) * 0.08; // ambience
    c -= step(0.35, abs(uv.y)); // letterbox

	fragColor = vec4(c, 1.);
}

// LAB/RGB converstions - https://code.google.com/archive/p/flowabs/
// HSV/RGB conversion - http://lolengine.net/blog/2013/07/27/rgb-to-hsv-in-glsl

vec3 rgb2xyz( vec3 c ) {
    vec3 tmp;
    tmp.x = ( c.r > 0.04045 ) ? pow( ( c.r + 0.055 ) / 1.055, 2.4 ) : c.r / 12.92;
    tmp.y = ( c.g > 0.04045 ) ? pow( ( c.g + 0.055 ) / 1.055, 2.4 ) : c.g / 12.92,
    tmp.z = ( c.b > 0.04045 ) ? pow( ( c.b + 0.055 ) / 1.055, 2.4 ) : c.b / 12.92;
    return 100.0 * tmp *
        mat3( 0.4124, 0.3576, 0.1805,
              0.2126, 0.7152, 0.0722,
              0.0193, 0.1192, 0.9505 );
}

vec3 xyz2lab( vec3 c ) {
    vec3 n = c / vec3( 95.047, 100, 108.883 );
    vec3 v;
    v.x = ( n.x > 0.008856 ) ? pow( n.x, 1.0 / 3.0 ) : ( 7.787 * n.x ) + ( 16.0 / 116.0 );
    v.y = ( n.y > 0.008856 ) ? pow( n.y, 1.0 / 3.0 ) : ( 7.787 * n.y ) + ( 16.0 / 116.0 );
    v.z = ( n.z > 0.008856 ) ? pow( n.z, 1.0 / 3.0 ) : ( 7.787 * n.z ) + ( 16.0 / 116.0 );
    return vec3(( 116.0 * v.y ) - 16.0, 500.0 * ( v.x - v.y ), 200.0 * ( v.y - v.z ));
}

vec3 rgb2lab(vec3 c) {
    vec3 lab = xyz2lab( rgb2xyz( c ) );
    return vec3( lab.x / 100.0, 0.5 + 0.5 * ( lab.y / 127.0 ), 0.5 + 0.5 * ( lab.z / 127.0 ));
}

vec3 lab2xyz( vec3 c ) {
    float fy = ( c.x + 16.0 ) / 116.0;
    float fx = c.y / 500.0 + fy;
    float fz = fy - c.z / 200.0;
    return vec3(
         95.047 * (( fx > 0.206897 ) ? fx * fx * fx : ( fx - 16.0 / 116.0 ) / 7.787),
        100.000 * (( fy > 0.206897 ) ? fy * fy * fy : ( fy - 16.0 / 116.0 ) / 7.787),
        108.883 * (( fz > 0.206897 ) ? fz * fz * fz : ( fz - 16.0 / 116.0 ) / 7.787)
    );
}

vec3 xyz2rgb( vec3 c ) {
    vec3 v =  c / 100.0 * mat3( 
        3.2406, -1.5372, -0.4986,
        -0.9689, 1.8758, 0.0415,
        0.0557, -0.2040, 1.0570
    );
    vec3 r;
    r.x = ( v.r > 0.0031308 ) ? (( 1.055 * pow( v.r, ( 1.0 / 2.4 ))) - 0.055 ) : 12.92 * v.r;
    r.y = ( v.g > 0.0031308 ) ? (( 1.055 * pow( v.g, ( 1.0 / 2.4 ))) - 0.055 ) : 12.92 * v.g;
    r.z = ( v.b > 0.0031308 ) ? (( 1.055 * pow( v.b, ( 1.0 / 2.4 ))) - 0.055 ) : 12.92 * v.b;
    return r;
}

vec3 lab2rgb(vec3 c) {
    return xyz2rgb( lab2xyz( vec3(100.0 * c.x, 2.0 * 127.0 * (c.y - 0.5), 2.0 * 127.0 * (c.z - 0.5)) ) );
}

vec3 hsv2rgb(vec3 c)
{
    vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float h = cos(iTime*0.3);
    if( iMouse.z > 0.0 ){
        h = iMouse.x/iResolution.x;
    }

   	vec3 col = vec3(0.0);
    vec3 hue = hsv2rgb( vec3(h,1.0,1.0) );

    if( uv.x > 0.5 ){
        //RGB
        col = mix( vec3(1.0), hue, (uv.x - 0.5) * 2.0 );
        col = mix( vec3(0.0), col, uv.y);
    }
    else{
        //LAB
        hue = rgb2lab(hue);
        col = mix( rgb2lab(vec3(1.0)), hue, uv.x * 2.0 );
        col = mix( rgb2lab(vec3(0.0)), col, uv.y );
        //col.x = uv.y;
     	col = lab2rgb(col);
    }

    //gamma correction
    //col = pow(col, vec3(1.0/2.2));

    // Output to screen
    fragColor = vec4(col,1.0);
}

