#define PI 3.141592
#define ITER 64.
#define MAT_TUN 0.
#define MAT_SPHE 1.

vec2 moda(vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a-per/2.,per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

// iq's palette http://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a+b*cos(2.*PI*(c*t+d));
}

mat2 rot(float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

vec2 mo (vec2 p, vec2 d)
{
    p.x = abs(p.x)-d.x;
    p.y = abs(p.y)-d.y;
    if (p.y>p.x) p.xy = p.yx;
    return p;
}

float stmin(float a, float b, float k ,float n)
{
    float st = k/n;
    float u = b-k;
    return min(min(a,b), 0.5 * (u+a+abs(mod(u-a+st, 2.*st)-st)));
}

float cyl(vec2 p, float r)
{return length(p)-r;}

float box(vec3 p, vec3 c)
{
    p.xz *= rot(iTime);
    return length(max(abs(p)-c,0.));
}

float sphe(vec3 p, float r)
{return length(p)-r;}

float prim1(vec3 p)
{
    float per = 5.;
    p.xy = mo(p.xy, vec2(10.));
   p.xz = moda(p.xz, 2.*PI/5.);
    p.x -= 2.;
    p.xz *= rot(p.y*0.8);
    return stmin(cyl(p.xz, 0.3), box(vec3(p.x,mod(p.y-per/2.,per)-per/2., p.z),vec3(1.)), 0.8, 5.);
}

vec2 mat_min(vec2 a, vec2 b)
{
    if (a.x < b.x) return a;
    else return b;
}

vec2 tunnel (vec3 p)
{
        p.xy *= rot(p.z*0.2+iTime*0.5);
    float s = prim1(p);
    for (int i = 0; i<8; i++)
    {
        p.xy *= rot(PI/4.);
        p.z -= 8.;
        s = min(s, prim1(p));
    }
    return vec2(s, MAT_TUN);
}

vec2 prim2 (vec3 p)
{
    p.z += cos(p.x + iTime);
    p.x += sin(p.y+iTime*2.);
    p.y -= sin(p.z*2.+iTime*3.);
    return vec2(sphe(p,5.), MAT_SPHE);
}

float g = 0.;

vec2 SDF(vec3 p)
{
    vec2 d = mat_min(tunnel(p), prim2(p));
     // glow from lsdlive, originally from balkhan : https://www.shadertoy.com/view/4t2yW1
    g += 0.01/(0.01+d.x*d.x);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(0.001,0.001,-20.); vec3 p = ro;
    vec3 rd = normalize(vec3(uv,0.7+length(uv)));

    float shad = 0.;
    vec3 c = vec3(0.);
    for (float i = 0.; i<ITER; i++)
    {
		vec2 d = SDF(p);
        if (d.x<0.001)
        {
            shad = i/ITER;
            if (d.y == MAT_SPHE) c = vec3(1.-shad)*vec3(1.,0.,.8);
            if (d.y == MAT_TUN) c = vec3(1.-shad)*vec3(0.,0.1,1.5);
            break;
        }
        p+=d.x*rd*0.5;
    }

    float t = length(ro-p);
    vec3 pal = palette (length(uv),
                       vec3(0.8,0.5,0.),
                       vec3(0.5),
                       vec3 (.2),
                       vec3(0.,0.,0.5));

    c = mix(c, pal, 1.-exp(-0.002*t*t))+g*(0.02+p.z*0.01);

    // Output to screen
    fragColor = vec4(c,1.0);
}

vec2 RES = vec2(1920,1080);

const float PI_x_2 = 6.28318530718;

// From: https://stackoverflow.com/questions/4200224/random-noise-functions-for-glsl
float rand(vec2 co){
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 step = floor(uv*RES) / RES;

    float changeRate = rand(step);

    vec3 col = mix(vec3(1,0,0), vec3(1,1,1), uv.x);
    col.xy *= cos(vec2(col.y, col.z) * PI_x_2) + .9;

    vec3 col_t = 2.0 * cos(changeRate*iTime) * col;

    fragColor = vec4(col_t, 1.0);
}

