#define ITER 100.
#define PI 3.141592

mat2 rot (float a)
{return mat2(cos(a),sin(a),-sin(a),cos(a));}

float smin( float a, float b, float k )
{
    float res = exp( -k*a ) + exp( -k*b );
    return -log( res )/k;
}

// from: https://beta.observablehq.com/@mattdesl/heartbeat-function
float beat (float value, float intensity, float frequency) 
{
    float v = atan(sin(value * 3.14 * frequency) * intensity);
    return (v + 3.14 / 2.) / 3.14;
}

vec2 moda (vec2 p, float per)
{
    float a = atan(p.y,p.x);
    float l = length(p);
    a = mod(a-per/2.,per)-per/2.;
    return vec2(cos(a),sin(a))*l;
}

// iq's palette http://www.iquilezles.org/www/articles/palettes/palettes.htm
vec3 palette (float t, vec3 a, vec3 b, vec3 c, vec3 d)
{
    return a+b*cos(2.*PI*(c*t+d));
}


float cyl (vec2 p, float r)
{return length(p)-r;}


// iq's formula http://iquilezles.org/www/articles/distfunctions/distfunctions.htm
float sdCapsule( vec3 p, vec3 a, vec3 b, float r )
{
    vec3 pa = p - a, ba = b - a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h ) - r;
}


float prim1(vec3 p)
{
    return sdCapsule(p, vec3(0.,-1.5,0.), vec3(0.,1.5,0.), 1.4+p.y*0.3);
}

float cocoon(vec3 p)
{

    vec3 pp = p;
    p.x += sin(p.y*0.7);
    p *= 0.4+beat(iTime,.5,2.);
    float cap = prim1(p);

    p.xz *= rot(iTime);
    p.xz *= rot(p.y*0.5);
    p.xz = moda(p.xz, 2.*PI/3.);
   	p.x -= (2.-abs(p.y-1.)*0.3);
    float branch1 = cyl(p.xz, 0.4);

    p = pp;

    p.xy *= rot(PI/3.);
    p.xz *= rot(iTime*0.2);
    p.xz *= rot(p.y*0.4);
    p.xz = moda(p.xz, 2.*PI/2.);
   	p.x -= (1.+abs(p.y)*0.1);
    float branch2 = cyl(p.xz, 0.3+abs(p.y)*0.03);

    p = pp;

    p.xy *= rot(-PI/3.);
    p.xz *= rot(iTime*0.2);
    p.xz *= rot(p.y*0.4);
    p.xz = moda(p.xz, 2.*PI/2.);
   	p.x -= (1.+abs(p.y)*0.1);
    float branch3 = cyl(p.xz, 0.1+abs(p.y)*0.03);

    return smin(branch3,smin(branch2,smin(cap,branch1,3.), 3.),3.);
}


float g = 0.;

vec4 SDF (vec3 p)
{
    vec4 d = vec4(cocoon(p),p);
     // glow from lsdlive, originally from balkhan : https://www.shadertoy.com/view/4t2yW1
    g += 0.01/(0.01+d.x*d.x);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = 2.*(fragCoord/iResolution.xy)-1.;
	uv.x *= iResolution.x/iResolution.y;

    uv += texture(iChannel1, uv*0.3+iTime*0.1).r *0.08;

    vec3 ro = vec3(0.001,1.,-6.); vec3 p = ro;
    vec3 dir = normalize(vec3 (uv,1.));
    float shad = 0.;
    vec3 c = vec3(0.);

    vec3 pal = palette(length(uv),
                      vec3(0.2),
                      vec3(0.2),
                      vec3(-.12),
                      vec3(0.5,0.,0.2));

    for (float i = 0.; i<ITER; i++)
    {
        float d = SDF(p).x;
        if(d<0.001)
        {
