            c = texture(iChannel0, SDF(p).yz*0.2).xyz
                *vec3(shad)*3.
                *vec3(length(p.z)*0.8,0.1,0.5) 
                + g*0.008*length(uv)*0.6;
            break;
        }
        else c = pal;
        p+= d*dir*0.6;
