// try to find an intersection with the terrain
float raymarchTerrain(in vec3 o, in vec3 rd, float mint, float maxt)
{
	// starting step size
	const float startingDelta = 0.2f;
	// step size scaling with t
	const float scalingDelta = 0.01f;
	// near and far radius
	//const float mint = 0.0f;
    //const float maxt = 20.0f;

	// used for linear interpolation (better intersection)
	// last y
	float ly = 0.0f;
	// last terrain y
	float lh = 0.0f;
	// integrate over the ray path
	float delta = startingDelta;
	for(float t = mint; t<maxt; t+= delta)
	{
		// current sample point on ray
		vec3 p = o+t*rd;
		// evaluate terrain height
		float h = terrainMap(p.xz);
		// if the ray point is beneath the terrain - find the intersection (incorrect if we start inside the terrain)
		if(p.y<h)
		{
			// linearly interpolate the last point and the current one, to estimate
			// the ray-terrain intersection
			float resT = t-delta + delta*(lh-ly)/(p.y-ly-h+lh);
			return resT;
		}
		// record the last ray sample y and terrain y
		ly = p.y;
		lh = h;
		// rescale delta for a speedup
		delta = startingDelta + t*scalingDelta;
	}
	return -1.0f;
}

const vec3 sunDir = normalize(vec3(-0.6f, 0.4f, 0.6f));

// You're not interested in this atm
float terrainShadow(in vec3 o, in vec3 rd)
{
	const float mint = 0.01f;
    float t = mint;
	float res = 1.0f;
	for( int i=0; i<32; i++ )
    {
        vec3  pos = o + t*rd;
        float  env = terrainMap( pos.xz );
        float hei = pos.y - env;
        res = min( res, 32.0*hei/t );
        if( res<0.0001 ) break;
        t += clamp( hei, 1.0+t*0.1, 50.0 );
    }

	return clamp( res, 0.0, 1.0 );
}

vec3 fog( in vec3 col, float t )
{
    vec3 fogCol = vec3(0.4,0.6,1.15);
    return mix( col, fogCol, 1.0-exp(-0.00005*t*t*t) );
}


float raytraceWater(in vec3 o, in vec3 rd)
{
    const float waterHeight = 0.5f;
    float t = (waterHeight-o.y)/rd.y;
    float resT = t<0.0f? -1.0f : t;
    return resT;
}

/*
*	An abomination that serves to replace random
*
*	@param seed: some seed
*
*	@return: "random" number in [0,1)
*/
float hashAO(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

/*
*	Computes ambient occlusion
*
*	@param p: 			Some intersection points which we want to calculate ambient occlusion for
*	@param n: 			The normal at this point
*	@param aoRadius:	Radius of occlusion
*	@param seed:		A seed for the abomination "sampling random numbers"
*
*	@return:		Occlusion coefficient in [0,1], 0 - totally occluded, 1 - totally non-occluded
*/
#define AO_SAMPLES 16
float ao(in vec3 p, in vec3 n, float aoRadius)
{
    float seed = hashAO( dot( p.xy*p.yz, vec2(12.9898, 78.233)+float(iTime) ) );
    float occ = 0.0;
    // integration of the hemisphere - we take AO_SAMPLES number of samples
    for(int k=1;k<AO_SAMPLES;++k)
    {
        // in your implementation u and v would look like this:
        // u = random();
        // v = random();
        // the abomination you see here is because this is glsl code:
        float u = hashAO( 78.233 + seed + float(k)*92.13);
    	float v = hashAO( 10.873 + seed + float(k)*35.00);

        // the next part generates a cosine distributed direction in the upper hemisphere
        // given a normal, ofr more information: http://amietia.com/lambertnotangent.html
        // you can simply use your code from the homework - you don't need to use this one
        // it's just good since it doesn't require you to build a coordinate frame
        float a = 6.2831853 * v;
        u = 2.0*u - 1.0;
        vec3 dir = normalize(n + vec3(sqrt(1.0-u*u) * vec2(cos(a), sin(a)), u));
        // note that since I generate a cosine distributed direction,
        // the pdf is = cos(theta)/pi
        // we want to find the visibility integral from here:
        // https://en.wikipedia.org/wiki/Ambient_occlusion
        // the cosine term cancel out, with the pdf, also the pi from the pdf cancels out
        // with the 1/pi in front of the integral from the wikipedia article

        // this is the part where you check whether this ray intersects anything 
        // in a given radius - if it does - increment the occlusion count
        // if it doesn't - do nothing
    	if(raymarchTerrain(p,dir,0.01,aoRadius)>0.0)
        {
            ++occ;
        }
    }
    // occ/AO_SAMPLES would be the occlusion
    // I want the oposite, so I subtract it from 1
    return 1.0-occ/float(AO_SAMPLES);
}


vec4 renderTerrain(in vec3 o, in vec3 rd)
{
	vec3 col = vec3(0.0f);
	// find intersection between the ray and the terrain (if there is no intersection: t==-1.0f)
	float t = raymarchTerrain(o, rd, 0.01, 20.0);
	if(t>0.0f)
	{
        // You're also not interested in this atm
		// intersection position
		vec3 pos = o+rd*t;
		vec3 nor = terrainNormal(pos.xz);
        float anim = 1.5*(0.5+0.5*abs(sin(iTime*0.5)));
		nor = normalize( nor + 0.8*(1.0-abs(nor.y))*0.8*fbmd_8( anim*pos*0.3*vec3(1.0,0.2,1.0) ).yzw );
		vec3 green = vec3(0.3f, 0.3f, 0.0f);
		vec3 desert = vec3(0.18,0.11,0.10)*.75;
		vec3 interm = mix( desert, green, smoothstep(0.7,0.9,nor.y) );
		float w = (o+t*rd).y/(1.5f*terrainHeight);
		float diff = max(0.0f, dot(sunDir, nor));
		//if(diff>0.001f) diff *= terrainShadow(pos+nor*0.01f, sunDir);
		vec3 lin = 1.0*5.0*vec3(1.0,0.9,0.8)*diff;
		col = interm*w;
		col *= lin;
        col = fog(col, t);

        //vec3 pos = o+rd*t;
        //float ambientTerm = ao(pos, terrainNormal(pos.xz), 3.0);
        //col = vec3(ambientTerm);
	}
	return vec4(col, t);
}


vec3 renderSky( in vec3 ro, in vec3 rd )
{
    // background sky     
    vec3 col = 0.9*vec3(0.4,0.65,1.0) - rd.y*vec3(0.4,0.36,0.4);

    // clouds
    float t = (1000.0-ro.y)/rd.y;
    if( t>0.0 )
    {
        vec2 uv = (ro+t*rd).xz;
        float cl = fbm9( uv*0.002 );
        float dl = smoothstep(-0.2,0.6,cl);
        col = mix( col, vec3(1.0), 0.4*dl );
    }

	// sun glare    
    float sun = clamp( dot(sunDir,rd), 0.0, 1.0 );
    col += 0.8*vec3(1.0,0.6,0.3)*pow( sun, 32.0 );

	return col;
}

float rand(vec2 n) { 
	return fract(sin(dot(n, vec2(12.9898, 4.1414))) * 43758.5453);
}

vec4 renderWater(in vec3 o, in vec3 rd)
{
    vec3 col = vec3(0.0f);
    float tw = raytraceWater(o,rd);
    if(tw>0.0f)
    {
        col = vec3(0.3f, 0.48f, 1.0f);
        vec3 pos = o+rd*tw;
        vec3 ref = reflect(rd, vec3(0.0f, 1.0f, 0.0f));
        float diff = sunDir.y;
        col *= diff;
        vec3 spec = vec3(pow(max(0.0f, dot(ref, sunDir)),10.0));
        col += spec;
        vec3 bon = normalize(ref-vec3(0.0f, 0.4f+0.02f*(2.0f*rand(pos.xz)-1.0f), 0.0f));
        vec4 rf = renderTerrain(pos+vec3(0.0f, 0.2f, 0.0f), bon);
        if(rf.w>0.0f) col *= rf.xyz;
        else
        {
            col *= 1.0f*mix(col, vec3(1.8f), (0.5f*fbm9( pos.xz*7.0f )+0.5f));
        }

        col = fog(col, tw);
    }
    return vec4(col, tw);
}


vec3 render(in vec3 o, in vec3 rd)
{
	vec3 col = renderSky(o,rd);
    vec4 waterCol = renderWater(o,rd);
    vec4 terrCol = renderTerrain(o, rd);
    if(waterCol.w>0.0f && terrCol.w>0.0f)
    {
        if(waterCol.w<terrCol.w)
        	col = waterCol.xyz;
        else
            col = terrCol.xyz;
    }
    else if(waterCol.w>0.0f)
        col = waterCol.xyz;
    else if(terrCol.w>0.0f)
        col = terrCol.xyz;

	// equivalent to col = terrCol.w==1.0f ? terrCol.xyz : col;

	return col;
}

#define PI 3.14159265359f
vec3 render(vec2 ndc, float aspectRatio)
{
	// camera origin
    vec3 o = vec3(0.0f, 5.0f, -6.0f);
	// camera horizontal field of view
    const float fov = 2.0f*PI / 3.0f;
    const float scaleX = tan(fov / 2.0f);
	// camera right vector
    vec3 right = vec3(1.0f, 0.0f, 0.0f)*scaleX;
	// camera forward vector
    vec3 forward = vec3(0.0f, 0.0f, 1.0f);
	// camera up vector
    vec3 up = vec3(0.0f, 1.0f, 0.0f)*scaleX*aspectRatio;
	// ray direction
    vec3 rd = normalize(forward + ndc.x*right + ndc.y*up);
    return render(o, rd);
}

