    // approximate gamma correction
    col = sqrt(col);
	fragColor = vec4(col,1.0f);
}

float distLine(vec3 ro, vec3 rd, vec3 p) {
    return length(cross(p-ro, rd)) / length(rd);
}

float drawPoint(vec3 ro, vec3 rd, vec3 p) {
    p -= .5; // move cube to center of screen
    float d = distLine(ro, rd, p);
    d = smoothstep(.06, .05, d);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 ro = vec3(3.*sin(iTime), 2., -3.*cos(iTime)); // ray origin

    vec3 lookAt = vec3(0.);

    float zoom = 1.3;
    vec3 f = normalize(lookAt - ro);
    vec3 r = cross(vec3(0.,1.,0.), f);
    vec3 u = cross(f, r);

    vec3 c = ro + f*zoom;
    vec3 i = c + uv.x*r + uv.y*u;
    vec3 rd = i-ro; // ray dir.

    float t = iTime;

    float d = 0.;

    d += drawPoint(ro, rd, vec3(0., 0., 0.));
    d += drawPoint(ro, rd, vec3(0., 0., 1.));
    d += drawPoint(ro, rd, vec3(0., 1., 0.));
    d += drawPoint(ro, rd, vec3(0., 1., 1.));
    d += drawPoint(ro, rd, vec3(1., 0., 0.));
	d += drawPoint(ro, rd, vec3(1., 0., 1.));
    d += drawPoint(ro, rd, vec3(1., 1., 0.));
    d += drawPoint(ro, rd, vec3(1., 1., 1.));

    fragColor = vec4(d);
}

#define RADIUS .025
#define STEP 2.
#define BLUE vec3(.11, .15, .2)
#define WHITE vec4(1., .98, .9, 1.)
#define RED vec3(.6, .09, .08)

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 lM = vec2(max(iResolution.x, iResolution.y), min(iResolution.x, iResolution.y));
	vec2 pos = (fragCoord -.5 * lM) / lM.x;

    pos.x += RADIUS;

    fragColor = WHITE;

    if (abs(pos.x - RADIUS) > STEP * RADIUS * 3. || abs(pos.y) > RADIUS * 3.) return;

    float alpha = sin(iTime * 4. + floor((pos.x / (STEP * RADIUS)) / 2.)) * .5 + .5;
    pos = vec2(mod(pos.x, RADIUS * 2. * STEP) - RADIUS, pos.y + min(alpha, .5) / 25.);

    float dist = length(pos);
    if (dist < RADIUS)
        fragColor = mix(WHITE, vec4(mix(RED, BLUE, alpha), 1.), smoothstep(RADIUS, RADIUS * .93, dist));
}

#define ITERATIONS 25
#define MAX_INTERSECTION_DIST 0.001
#define MAX_DIST 5000.0
#define SUN_RAD 2.0

vec3 sun_pos;
// Ray
struct ray_t {
 	vec3 o;
 	vec3 d;    
};

// Sphere Object
struct sphere_t{
	vec3 pos;
    vec3 col;
    float rad;
};

mat3 rot3D (float rad) {
  return mat3 (cos(rad), 0.0, sin(rad),
               0.0	   , 1.0, 0.0     ,
               -sin(rad),0.0, cos(rad)); 
}

float distance_from_sphere(vec3 pos, sphere_t sphere) {
 	return length(pos - sphere.pos) - sphere.rad;  
}

vec3 raymarch (ray_t ray, sphere_t sphere) {

    vec3 current_pos;
    float way_done = 0.0;
    float dist_to_sphere;

    for (int i=0; i<ITERATIONS; ++i) {

       current_pos = ray.o + ray.d*way_done;

       dist_to_sphere = distance_from_sphere(current_pos, sphere); 

        if (dist_to_sphere < MAX_INTERSECTION_DIST) {

            float shading = 0.0;

            if (sun_pos != sphere.pos) {
               shading = dot (normalize(sun_pos), normalize(current_pos-sphere.pos));
            }else{
               shading = 1.0;
            }

        	return vec3(sphere.col.r * shading+0.1,
                        sphere.col.g * shading+0.1,
                        sphere.col.b * shading+0.1);  
        }

        if (way_done > MAX_DIST) break;

        way_done += dist_to_sphere;

    }

    return vec3(0.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy * 2.0 -1.0;
    uv.x *= iResolution.x / iResolution.y;

    // Camera Position
    vec3 camera 	= vec3(0.0,0.0,-4.0);            

    ray_t ray 		= ray_t(camera,vec3(uv, 1.));

    sphere_t sphere[2];

    sphere[0] = sphere_t(vec3(0.0,-1.0, 1.0),vec3(0.5, 0.5, .5), 2.0);
    sphere[1] = sphere_t(vec3(sin(iTime)*SUN_RAD, 2.5, 1.+cos(iTime)*SUN_RAD),vec3(1.0, 1.0, .0), 1.0);

    sun_pos = sphere[1].pos;

    vec3 col = vec3 (0.0);

    for (int i=0; i<sphere.length(); i++) 
    	col += raymarch(ray, sphere[i]);


    fragColor = vec4(col,1.0);
}

float star(in vec2 uv, in float t)
{
    float phi = atan(uv.y, uv.x);
    float d = length(uv)
        * (1. 
           + 0.15 * sin( phi * 4. + 0.2 * t) 
           + 0.1 * sin( 1.2 * phi * 10. + 0.6 * t) 
           + 0.2 * sin( phi * phi * 2. + 0.4 * t) 
          ) * 7.7 * ( 1. + 0.1 * sin(t) );
    d = log(d*d);
    return 0.7 * d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = ( fragCoord - .5 * iResolution.xy ) / iResolution.x;

    float t = 1.4 * iTime;
    float d = 0.5 * min(star(uv, t), star(uv, t - 2.1));
    d = (1. + 0.1 * sin(t)) * min(d, star(uv, t - 3.1));

    fragColor = vec4( sqrt( smoothstep(1., 0., d) ) );
}

#define SPEED 8.0
#define HEIGHT 0.05
#define WAVELENGTH -100.0
#define DIEOFF 0.6
#define DROPS 40.0
#define TIMEDIEOFF 0.6
#define DISTANCEDIEOFF 38.2
#define DROPFRQ 5000.0
#define PROPSPEED 3.0

vec4 plasma( vec2 uv )
{
    float d = 0.0;
    for (float i = 0.0; i < 200.0; ++i) {
        float j = max(0.0, 3.14 - distance(uv, vec2(
            sin(i + iTime * mod(i * 2633.2363, 0.42623)) * 12.0,
            cos(i  * 0.617 + iTime * mod(i * 36344.2363, 0.52623)) * 12.0
        )));
        d += cos(j);
     }
    float r = cos(d * 6.0) * 0.5 + 0.5;
    float g = cos(d * 3.0) * 0.5 + 0.5;
    return vec4(r, g, max(r, g), 1.0);
}

float h(vec2 uv) {
    float h = 0.0;
    for (float i = 0.0; i < DROPS; ++i) {
        vec2 c = vec2(
            mod(cos(i * 343.7345), 0.001) * 1000.0,
            mod(cos(i * 634.3634), 0.001) * 1000.0 * iResolution.y / iResolution.x);
        float d = distance(uv, c);
        float t = mod(cos(i * 734.6345), 0.001) * DROPFRQ;
        t = mod(t - iTime, DROPFRQ / 1000.0);
        if (t * PROPSPEED< d) continue;
        h -= HEIGHT * sin(d*WAVELENGTH + SPEED*iTime) * t
                        * pow(d*DISTANCEDIEOFF,-TIMEDIEOFF * t);
     }
    return h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    float n = h(uv),
        d = h(uv - 0.00001) - n;

    fragColor = plasma((uv + n)* 6.0 ) + min(0.1,max(-0.1, d * 10000.0)) * 0.5;
}

