// Curve closest to the center
CurvePart bezierInner(vec3 p) {
    vec4 bez, bezPart;
    vec3 a, b, c,
        aTan, cTan, aNor, cNor,
        tan, nor;
    float offset;

    a = a0;
    b = b0;
    c = c0;
    aTan = aTan0;
    cTan = cTan0;
    aNor = aNor0;
    cNor = cNor0;
    bezPart = bezierPoint(a, b, c, p);
    bez = bezPart;
    tan = mix(aTan, cTan, bez.w);
    nor = mix(aNor, cNor, bez.w);
    offset = 0.;

    a = a1;
    b = b1;
    c = c1;
    aTan = aTan1;
    cTan = cTan1;
    aNor = aNor1;
    cNor = cNor1;
    bezPart = bezierPoint(a, b, c, p);
    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 1.;
    }

    a = a2;
    b = b2;
    c = c2;
    aTan = aTan2;
    cTan = cTan2;
    aNor = aNor2;
    cNor = cNor2;
    bezPart = bezierPoint(a, b, c, p);
    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 2.;
    }

    sepa = length(p - bez.xyz) - sepR;

    return CurvePart(
        bez,
        tan,
        nor,
        offset
    );
}

// Curve that loops around the outside
CurvePart bezierOuter(vec3 p) {
    vec4 bez, bezPart;
    vec3 a, b, c,
        aTan, cTan, aNor, cNor,
        tan, nor;
    float offset;

    a = a3;
    b = b3;
    c = c3;
    aTan = aTan3;
    cTan = cTan3;
    aNor = aNor3;
    cNor = cNor3;
    bezPart = bezierPoint(a, b, c, p);
    bez = bezPart;
    tan = mix(aTan, cTan, bez.w);
    nor = mix(aNor, cNor, bez.w);
    offset = 0.;

    a = a4;
    b = b4;
    c = c4;
    aTan = aTan4;
    cTan = cTan4;
    aNor = aNor4;
    cNor = cNor4;
    bezPart = bezierPoint(a, b, c, p);
    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 1.;
    }

    float sepd = length(p - bez.xyz) - sepR;


    a = a5;
    b = b5;
    c = c5;
    aTan = aTan5;
    cTan = cTan5;
    aNor = aNor5;
    cNor = cNor5;
    bezPart = bezierPoint(a, b, c, p);
    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 2.;
    }

    a = a6;
    b = b6;
    c = c6;
    aTan = aTan6;
    cTan = cTan6;
    aNor = aNor6;
    cNor = cNor6;
    bezPart = bezierPoint(a, b, c, p);
    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 3.;
    }

    float sepc = 1e12;

    a = a7;
    b = b7;
    c = c7;
    aTan = aTan7;
    cTan = cTan7;
    aNor = aNor7;
    cNor = cNor7;
    bezPart = bezierPoint(a, b, c, p);

    sepc = min(sepc, length(p - bezPart.xyz) - sepR);

    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 4.;
    }

    a = a8;
    b = b8;
    c = c8;
    aTan = aTan8;
    cTan = cTan8;
    aNor = aNor8;
    cNor = cNor8;
    bezPart = bezierPoint(a, b, c, p);

    sepc = min(sepc, length(p - bezPart.xyz) - sepR);

    if (switchBezier(p, bez, bezPart) > 0.) {
        bez = bezPart;
        tan = mix(aTan, cTan, bez.w);
        nor = mix(aNor, cNor, bez.w);
        offset = 5.;
    }

    float sepb = length(p - bez.xyz) - sepR;
    sepa = max(sepa, sepb);
    sepc = max(sepc, sepd);
    sepa = min(sepa, sepc);

    return CurvePart(
        bez,
        tan,
        nor,
        offset + 6.
    );
}

Curve TrefoilCurve(vec3 p) {

    vec4 bez, bezPart;

    float side = 0.;
    float tFlip = 1.;

    float cell = 0.;

    float repetitions = 3.;
    float angle = TAU / repetitions;


    if (p.z > 0.) {
        p.z *= -1.;
        p.y *= -1.;
        pR(p.xy, angle / -2.);
        side = 1.;
        tFlip = -1.;
    }
    pR(p.xy, angle / -2.);
    cell = pModPolar(p.xy, repetitions);
    pR(p.xy, angle / 2.);

    float outer = 0.;

    CurvePart curve = bezierInner(p);
    CurvePart outerCurve = bezierOuter(p);

    if (switchBezier(p, curve.bez, outerCurve.bez) > 0.) {
        outer = 1.;
        curve = outerCurve;
    }

    float an = cell * angle;

    mat3 m = mat3(
        cos(an), -sin(an), 0,
        sin(an), cos(an), 0,
        0, 0, 1
    );

    mat3 m2 = mat3(
        cos(angle / -2.), -sin(angle / -2.), 0,
        sin(angle / -2.), cos(angle / -2.), 0,
        0,0,-1
    );
    mat3 m3 = mat3(1,0,0,0,-1,0,0,0,1);

    if (side > 0.) {
        m = m * m2 * m3;
    }

    float parts = 24.;

    float sectionCurveOffset;

    vec3 tangent = curve.tangent;
    vec3 normal = curve.normal;
    vec3 binormal = cross(tangent, normal);

    normal *= -1.;

    if (side > 0.) {
        binormal *= -1.;
    }

    tangent = normalize(tangent);
    binormal = normalize(binormal);
    normal = normalize(normal);

    float flip = -1. + side * 2.;
    cell = mod(cell * flip, 3.);
    float offset = cell * 5. + side * 7.;
    float t = (offset - (curve.bez.w + curve.offset) * flip);
    t = mod(t / 15., 1.);

    vec3 position = curve.bez.xyz;

    return Curve(
        position * m,
        curve.tangent * m,
        normal * m,
        binormal * m,
        t
    );
}

void pModTrefoil(inout vec3 p, float len) {
    Curve curve = TrefoilCurve(p);
    float x = dot(p - curve.position, curve.normal);
    float y = dot(p - curve.position, curve.binormal);
    float z = curve.t;
    p = vec3(x, y, z);
    p.z -= 0.0666;
    p.z *= len;
}


