struct Hit {
    Model model;
    vec3 pos;
    bool isBackground;
    vec3 normal;
    vec3 rayOrigin;
    float rayLength;
    vec3 rayDirection;
};

// https://www.shadertoy.com/view/Xds3zN
float calcAO( in vec3 pos, in vec3 nor )
{
    AO_PASS = true;
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float hr = 0.01 + 0.12*float(i)/4.0;
        vec3 aopos =  nor * hr + pos;
        float dd = map( aopos ).dist;
        occ += -(dd-hr)*sca;
        sca *= 0.95;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 );
}

vec3 drawNova(vec3 col, vec2 uv) {
    float scale = .3;
    uv *= -1.;
    uv /= scale;
    uv.y -= .5;
    uv.x /= 1.4;
    uv.x += 1.2;

    vec2 uv2 = uv;
    pR(uv2, PI * .25);
    vec2 warp = sin((uv2 + .25 + time) * 5.) * .1;
    uv += warp;

    float d = fNova(uv, .133);
    float dOffset = fNova(uv - vec2(.05,-.1), .133);

    d *= scale;
    dOffset *= scale;

    col = mix(col, vec3(.3), smoothstep(.005, .0, d - .02));
    col = mix(col, vec3(.0), smoothstep(.005, .0, dOffset + .01));

    // Outline
    d = max(d - .04, -d + .01);
    col = mix(col, TRAIN_WHITE, smoothstep(.005, .0, d));

    return col;
}

vec3 render(Hit hit, vec3 col) {
    if ( ! hit.isBackground) {
        float ao = calcAO(hit.pos, hit.normal);
        col = hit.model.material;

        if (hit.model.id == 1) {
            col = drawNova(col, hit.model.uv);
        }

        vec3 diffuse = mix(vec3(.5,.5,.6) * 1., vec3(1), ao);
        col *= diffuse;
    }
    return col;
}


// --------------------------------------------------------
// Ray Marching
// Adapted from: https://www.shadertoy.com/view/Xl2XWt
// --------------------------------------------------------

const float MAX_TRACE_DISTANCE = 3.;
const float INTERSECTION_PRECISION = .0001;
const int NUM_OF_TRACE_STEPS = 150;

const int NORMAL_STEPS = 6;
vec3 calcNormal(vec3 pos){
    vec3 eps = vec3(.0001,0,0);
    vec3 nor = vec3(0);
    float invert = 1.;
    for (int i = 0; i < NORMAL_STEPS; i++){
        nor += map(pos + eps * invert).dist * eps * invert;
        eps = eps.zxy;
        invert *= -1.;
    }
    return normalize(nor);
}

Hit raymarch(vec3 rayOrigin, vec3 rayDirection){

    float currentDist = INTERSECTION_PRECISION * 2.0;
    float rayLength = 0.;
    Model model;

    for(int i = 0; i < NUM_OF_TRACE_STEPS; i++){
        if (currentDist < INTERSECTION_PRECISION || rayLength > MAX_TRACE_DISTANCE) {
            break;
        }
        model = map(rayOrigin + rayDirection * rayLength);
        currentDist = model.dist;
        rayLength += currentDist * (1. - model.underStep);
    }

    bool isBackground = false;
    vec3 pos = vec3(0);
    vec3 normal = vec3(0);

    if (rayLength > MAX_TRACE_DISTANCE) {
        isBackground = true;
    } else {
        pos = rayOrigin + rayDirection * rayLength;
        normal = calcNormal(pos);
    }

    return Hit(
        model,
        pos,
        isBackground,
        normal,
        rayOrigin,
        rayLength,
        rayDirection
    );
}

mat3 calcLookAtMatrix(vec3 ro, vec3 ta, vec3 up) {
    vec3 ww = normalize(ta - ro);
    vec3 uu = normalize(cross(ww,up));
    vec3 vv = normalize(cross(uu,ww));
    return mat3(uu, vv, ww);
}

float backgroundMap(vec2 uv) {
    float scale = 1.5;
    float repeat = 3.55;

    uv.y *= -1.;
    uv /= scale;

    vec2 uv2 = uv;
    uv.x += time * repeat;
    vec2 uv3 = uv;

    pMod1(uv.x, repeat);
    uv.x += 1.2;

    pR(uv3, PI * .5);
    uv3 -= time * repeat;

    vec2 warp = vec2(0);
    warp += sin((uv3 / repeat * PI * 1.) * 2.) * .2;
    warp *= sin(uv2.x * PI * 1.5 + PI / 2.) * .5 + .5;
    warp += sin(uv2 * PI + PI / 2.) * .1;

    uv += warp;

    float d = fNova(uv, .06) * scale;

    return smoothstep(-0.01, .0, d);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {

    time = iTime;
    time *= .25;
    time = mod(time, 1.);

    vec2 p = (-iResolution.xy + 2.0*fragCoord.xy)/iResolution.y;

    p.y *= -1.; // Last minute adjustment, put the train on top
    p.y -= .03;

    vec3 camPos = vec3(-1.,0,.25) * .95;
    vec3 camTar = vec3(0,-.0025,0);
    vec3 camUp = vec3(0,0,1);
    mat3 camMat = calcLookAtMatrix(camPos, camTar, camUp);
    focalLength = 18.191446;
    vec3 rayDirection = normalize(camMat * vec3(p, focalLength));

    vec3 bg = mix(vec3(1), TRAIN_WINDOW, 1. - backgroundMap(p));

    Hit hit = raymarch(camPos, rayDirection);
    vec3 color = render(hit, bg);

    color = pow(color, vec3(1.,.9,.8)); // Brighten and tint a little blue
    color = pow(color, vec3(1. / 2.2)); // Gamma

    fragColor = vec4(color,1);
}

const int N = 70;
const int S[N] = int[N](0 , 1 , 3 , 6 , 2  , 7 , 13 , 20 , 12 , 21 , 
                        11, 22, 10, 23, 9  , 24, 8  , 25 , 43 , 62 ,
                        42, 63, 41, 18, 42 , 17, 43 , 16 , 44 , 15 , 
                        45, 14, 46, 79, 113, 78, 114, 77 , 39 , 78 , 
                        38, 79, 37, 80, 36 , 81, 35 , 82 , 34 , 83 , 
                        33, 84, 32, 85, 31 , 86, 30 , 87 , 29 , 88 , 
                        28, 89, 27, 90, 26 , 91, 157, 224, 156, 225);

float semi_circle_dis(vec2 p, vec2 m, float r){
    float p_dis = abs(distance(p,m)-r);
    float l_dis = distance(p,m-vec2(r,0));
    float r_dis = distance(p,m+vec2(r,0));
    if(p.y>m.y) return p_dis;
    return min(l_dis,r_dis);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 sp = ((fragCoord*2.-vec2(0,iResolution.y))/iResolution.y)*(cos(iTime*.5)+1.1)*30.;
    float MinRes = min(iResolution.y,iResolution.x);
	float  LineEpsilon =  30./MinRes;
    float dis = 1e20;

    for(int i=0;i<N-1;i++){
        vec2  mp = vec2(float(S[i]+S[i+1])*.5,0);
        float r =  float(abs(S[i]-S[i+1]))*.5;
        dis = min(
            dis,
            semi_circle_dis(
                bool(i&1) ? sp : sp*vec2(1,-1),
                mp,
                r
            )
        );

    }
   fragColor += smoothstep(1.,0.,LineEpsilon/dis);
}

void mainImage( out vec4 O, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy,
    f = (uv - 0.5) * 2.5;
    f.x *= iResolution.x/iResolution.y;
    float angle = atan(f.x,f.y),
     t = iTime * .3,
     d = length(f) - t;
    float r = sin(fract(cos(d*3.7))*10.);  
    float c = smoothstep(r-.3, r+.3, cos(angle*5.));
    O = vec4(c);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float flameWidth = 0.06,
        numberOfFlames = 500.0,
        swayAmount = 0.4,
        swaySpeed = 0.2,
        moveSpeed = 0.2,
        patchHeight = 0.4,
        redness = 2.0,
        yellowness = 0.8,
        whiteness= 0.5;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float x = uv.x * 1.4 - 0.4;

    float h = 0.0;

    for (float i = 0.0; i < numberOfFlames; ++i) {
        // how far across is this flame?
        float xx = mod(i * 744.44, 1.0) * mod(i * 434.64, 1.0);
        // make them move
        xx += cos(iTime * 0.4632 * mod(1324.34 * i, 0.2) + 412.523 * i) * moveSpeed;
        // make them wave from side to side
        xx += sin(uv.y * swayAmount) * cos(i * 64534.34) * swaySpeed;
        // combine them all
        h += max(0.0,
           (1.0 - uv.y)
                * (flameWidth - abs(x - xx))
                 * max(0.0, cos((uv.y - iTime) * (mod(1324.34 * i, 10.5) + patchHeight)))
        );
    }  

    fragColor = vec4(h * redness, h * yellowness, h * whiteness, 1.0);
}

// starting from MlSXR1
precision highp float;

float ball(vec2 p, float fx, float fy, float ax, float ay) 
{
    float t = iTime * 0.5;
    vec2 r = vec2(p.x + cos(t * fx) * ax, p.y + sin(t * fy) * ay);	
    return 0.05 / length(r);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 q = fragCoord.xy / iResolution.xy;
    vec2 p = -1.0 + 2.0 * q;	
    p.x	*= iResolution.x / iResolution.y;

    float col = 0.2;
    float a = 1.;
    float b = 2.;
    float c = 0.1;
    float d = 0.2;
    for (int i = 0; i < 10;i++)
    {
    	col += ball(p, a, b, c, d);
        a += 0.01;
        b = tan(a * 4.);
        c += 0.1;
        d = tan(c * 4.);
    }	
    fragColor = vec4(col * .94, col * .31, col * .14, 1.0);
}

#define mul(a,b) ((b)*(a))
