#define Degree2Raduis(a) ((a) * 3.1415926 / 180.0)
#define Raduis2Degree(a) ((a) * 180.0 / 3.1415926)

float rand(vec2 co)
{
    return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

vec2 rand2(vec2 p)
