const float scrollspeed = 0.1;

const int small_count = 25;
const vec4 small_color = vec4( 0.7, 0.7, 1.0, 1.0 );
const float small_size = 0.003;
const float small_flicker = 2.5;

const int big_count = 6;
const vec4 big_color = vec4( 1.0, 1.0, 0.6, 1.0 );
const float big_size = 0.005;
const float big_flicker = 1.5;

void iteration(inout float a, inout float b)
{
    float t = 0.111 * a + 1.123 * fract(123.456 * b);
    float v = 0.999 * b - 3.456 * fract(456.123 * a);
    a = t;
    b = -v;
}

float starfield(int index, float seed, int count, vec2 uv, float radius, float flicker)
{
    int n = count + 3;
    float w = 1.0 / float(count);

    float a = 2.1 + float(index) + seed;
    float b = 1.2 + seed;
    float c = 0.0;

    for( int i = 0; i < n; i++ )
    {
        iteration( a, b );

        if( i > 2 )
        {
            float x = float(i - 3) * w + fract(a) * w / 2.;
            float y = fract(b) * iResolution.y/iResolution.x;
	        vec2 star = vec2( x, y );
            float d = distance(star, uv);
            float r = (1. + 0.5 * abs(sin(2.*(x+y) + radius + iTime*flicker))) * radius;
            c = max(c, smoothstep(r, r - 0.001, d));
        }
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.x;

    int index = int( iTime * scrollspeed );
    float offset_x = mod( iTime * scrollspeed, 1. );

    fragColor = vec4(0, 0, (1.-uv.y) * 0.1, 0.);

    fragColor += small_color
        * starfield(index, 0., small_count, vec2(uv.x + offset_x, uv.y), small_size, small_flicker);
    fragColor += small_color
        * starfield(index + 1, 0., small_count, vec2(-1.0 + uv.x + offset_x, uv.y), small_size, small_flicker);

    fragColor += big_color
        * starfield(index, 1., big_count, vec2(uv.x + offset_x, uv.y), big_size, big_flicker);
    fragColor += big_color
        * starfield(index + 1, 1., big_count, vec2(-1.0 + uv.x + offset_x, uv.y), big_size, big_flicker);

    fragColor = pow(fragColor, vec4(1./2.2));
}

// Mellow purple flow background - Del 16/06/2018
const float pi = 3.14159;

vec4 MellowPurple(vec2 pos)
{
    pos *= 0.5;
    vec4 col = vec4(0.32,0.19,0.35,1.0);
    float d = length(pos);
    float tt = iTime*0.08;
    float iter = (pos.x*pos.y);
    iter *= 0.5+sin(pos.x*0.5+tt*0.95)+0.5;
    float r = sin(sin(tt)*pos.y);
    r += .4;
    float d1 = sin(tt+pos.x+pos.y);
    float val = sin(tt+iter*pi );
    float brightness = 0.25 / abs( d1 * val - r);
//    brightness +=  0.25 /abs( d1+0.1 * val - r);
//    brightness += 0.25 / abs( d1-0.1 * val - r);
    brightness +=  0.25 /abs( d1+0.4 * val - r);
    brightness += 0.25 / abs( d1-0.4 * val - r);
    brightness *= 0.05;
    brightness = brightness/(brightness + 1.);
    brightness*=0.75-d;
    col += brightness;
    return col;
}


