#define FAR     80.0
#define NRM_OFS 0.002


float sadd(float a, float b)
{
	float k = 0.5;
    float h = clamp(0.5 + 0.5 * (a - b) / k, 0.0, 1.0);
    return mix(a, b, h) - k * h * (1.0 - h);
}

float map_torus(vec3 o)
{
    o.x += 0.50;
    o.y += 0.45;

    float rx = cos(iTime * 4.0);
    float rz = cos(iTime * 4.0 + 0.12);
    mat3 trz = mat3(
        cos(rz), -sin(rz), 0,
        sin(rz), cos(rz), 0,
        0, 0, 1
    );

    mat3 trx = mat3(

        1, 0, 0,
        0, cos(rx), -sin(rx),
        0, sin(rx), cos(rx)
    );

    vec3 p = inverse(trx) * inverse(trz) * o;
    vec2 q = vec2(length(p.xz) - 0.5, p.y);
    return length(q) - (cos(iTime * 18.0) * 0.05 + 0.15);
}

float map_sphere(vec3 o)
{
    vec3 q = o + vec3(
        0.45 - abs(cos(iTime * 2.0) * 0.45),
        0.35 - abs(cos(iTime * 2.0) * 0.45),
        0);
    return length(q) - 0.25;
}

float map_sphere_2(vec3 o)
{
    float x = cos(iTime * 14.0) - sin(iTime * 14.0);
    float z = sin(iTime * 14.0) + cos(iTime * 14.0);
	vec3 q = o + vec3(x, 0, z) * 0.25;
    return length(q) - 0.25;
}

float world(vec3 p)
{
    float st = sadd(map_sphere(p), map_torus(p));
    float sst = sadd(st, map_sphere_2(p));
    return sst;
}

float raymarch(vec3 o, vec3 r)
{
    float t = 0.0;
    vec3 p = vec3(0, 0, 0);
    float d = 0.0;
    float ad = 0.0;
    for (int i = 0; i < 128; i++)
    {
        p = o + r * t;
        d = world(p);
        ad = abs(d);
        if (ad < 0.01)
        {
            return t;
        }
        t += d;
    }

    return FAR;
}



vec3 get_normal(vec3 p)
{
    vec2 e = vec2(NRM_OFS, 0);
    float d1 = world(p + e.xyy), d2 = world(p - e.xyy);
    float d3 = world(p + e.yxy), d4 = world(p - e.yxy);
    float d5 = world(p + e.yyx), d6 = world(p - e.yyx);

    return normalize(vec3(
        d1 - d2,
        d3 - d4,
        d5 - d6
    ));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - iResolution.xy * 0.5) / iResolution.y;
    vec3 o = vec3(cos(iTime) * 0.5 - 0.5, 0, -4);
    vec3 r = normalize(vec3(uv, 1.15));
    r = normalize(vec3(r.xy, r.z - length(r.xy) * .15));

    float cr = 0.0;
    float d = raymarch(o, r);
    if (d < FAR)
    {
        vec3 hit = o + r * d;
        vec3 normal = get_normal(hit);
        float ndu = dot(normal, vec3(0, 0.75, -0.25));

        vec3 color1 = vec3(0.25, 0.25, 0.25);
        vec3 color2 = vec3(1, 1, 1);
        vec3 fncolor = mix(color1, color2, ndu);
        fncolor.x = max(fncolor.x, 0.15);
        fncolor.z = max(fncolor.z, sin(iTime) * 0.1 + 0.1);
        fncolor.xz += uv * 0.5 * ndu;
        fragColor = vec4(fncolor, 1.0);
    }
    else
    {
        fragColor = vec4(0.45, 0.25, 0.25, 1.0) + vec4(uv, length(uv), 0) * 0.2;
    }
}

#define EPSILON 0.01
#define MAXSTEPS 128
#define NEAR 0.1
#define FAR 100.0
#define TWOPI 6.28319

precision mediump float;

struct Camera {
    vec3 pos;
    vec3 dir;
};

float rand (vec2 st) {
    return fract(sin(dot(st.xy,vec2(12.9898,78.233)))*43758.5453123);
}

float udfBox( vec3 p, vec3 b ) {
    return length(max(abs(p)-b, 0.0));
}

float dot2( in vec3 v ) {
    return dot(v,v);
}

float udfQuad( vec3 p, vec3 a, vec3 b, vec3 c, vec3 d ) {
    vec3 ba = b - a; vec3 pa = p - a;
    vec3 cb = c - b; vec3 pb = p - b;
    vec3 dc = d - c; vec3 pc = p - c;
    vec3 ad = a - d; vec3 pd = p - d;
    vec3 nor = cross( ba, ad );

    return sqrt(
        (sign(dot(cross(ba,nor),pa)) +
         sign(dot(cross(cb,nor),pb)) +
         sign(dot(cross(dc,nor),pc)) +
         sign(dot(cross(ad,nor),pd))<3.0)
        ?
        min( min( min(
            dot2(ba*clamp(dot(ba,pa)/dot2(ba),0.0,1.0)-pa),
            dot2(cb*clamp(dot(cb,pb)/dot2(cb),0.0,1.0)-pb) ),
                 dot2(dc*clamp(dot(dc,pc)/dot2(dc),0.0,1.0)-pc) ),
            dot2(ad*clamp(dot(ad,pd)/dot2(ad),0.0,1.0)-pd) )
        :
        dot(nor,pa)*dot(nor,pa)/dot2(nor) );
}

vec2 sceneSDF(vec3 p) {

    // x is distance to scene, y is object id

    float id = 0.0;
    float cr = 5.0;

    vec3 q = p - vec3(0.0, 0.0, 10.0);
    vec3 ca = vec3(-cr,  cr,  cr);
    vec3 cb = vec3( cr,  cr,  cr);
    vec3 cc = vec3(-cr, -cr,  cr);
    vec3 cd = vec3( cr, -cr,  cr);
    vec3 ce = vec3(-cr,  cr, -cr);
    vec3 cf = vec3( cr,  cr, -cr);
    vec3 cg = vec3(-cr, -cr, -cr);
    vec3 ch = vec3( cr, -cr, -cr);

    vec3 b1 = vec3(1.0);
    float box1 = udfBox(p - vec3(-2.0, -cr+b1.y, 10.0), b1);

    vec3 b2 = vec3(1.5, 3.0, 1.5);
    float box2 = udfBox(p - vec3(2.0, -cr+b2.y, 9.0), b2);

    float tot = FAR;

    float back = udfQuad(q, ca, cb, cd, cc);
    float right = udfQuad(q, cb, cf, ch, cd);
    if (right < EPSILON) { id = 1.0; }
    float left = udfQuad(q, ce, ca, cc, cg);
    if (left < EPSILON) { id = 2.0; }
    float top = udfQuad(q, ce, cf, cb, ca);
    float bottom = udfQuad(q, cc, cd, ch, cg);

    tot = min(tot, back);
    tot = min(tot, right);
    tot = min(tot, left);
    tot = min(tot, top);
    tot = min(tot, bottom);
    tot = min(tot, box1);
    tot = min(tot, box2);

    return vec2(tot, id);
}

vec3 sceneNormal(vec3 p) {
    vec3 v1 = vec3(1.0, -1.0, -1.0);
    vec3 v2 = vec3(-1.0, -1.0, 1.0);
    vec3 v3 = vec3(-1.0, 1.0, -1.0);
    vec3 v4 = vec3(1.0, 1.0, 1.0);
    return normalize(
        v1 * sceneSDF(p + v1*EPSILON).x +
        v2 * sceneSDF(p + v2*EPSILON).x +
        v3 * sceneSDF(p + v3*EPSILON).x +
        v4 * sceneSDF(p + v4*EPSILON).x
    );
}

float distToSurface(Camera c, out vec3 ip, out float id) {
    float depth = NEAR;
    for (int i=0; i<MAXSTEPS; i++) {
        ip = c.pos + c.dir * depth;
        vec2 distToScene = sceneSDF(ip);
        id = distToScene.y;
        if (distToScene.x < EPSILON) {
            return depth;
        }
        depth += distToScene.x;
        if (depth >= FAR) {
            return FAR;
        }
    }
    return depth;
}

float lambert(vec3 norm, vec3 lpos) {
    return max(dot(norm, normalize(lpos)), 0.0);
}

float shadow(vec3 ro, vec3 rd) {
	// based off of http://www.iquilezles.org/www/articles/rmshadows/rmshadows.htm
    float res = 1.0;
    float ph = 1e20;
    float tmin = 0.1;
    float tmax = 2.0;
    float k = 8.0;

    for (float t = tmin; t < tmax;) {
        vec2 h = sceneSDF(ro + rd * t);
        if (h.x < EPSILON) {
            return 0.0;
        }
        float y = (h.x * h.x) / (2.0 / ph);
        float d = sqrt((h.x * h.x) - (y * y));
        res = min(res, k * d / max(0.0, t - y));
        ph = h.x;
        t += h.x;
    }
    return res;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (2. * fragCoord.xy - iResolution.xy) / iResolution.y;
    vec3 nrmDir = normalize(vec3(uv, 1.0));
    Camera camera = Camera(vec3(0.0, 0.0, 0.0), nrmDir);
    vec3 endPt;
    vec3 col = vec3(0.0);
    float id = 0.0;
    float t = distToSurface(camera, endPt, id);

    if (t < FAR) {
        vec3 nrm = sceneNormal(endPt);

        if (id == 1.0) {
            col = vec3(0.8, 0.1, 0.1);
        }
        else if(id == 2.0) {
            col = vec3(0.1, 0.8, 0.1);
        }
        else {
            col = vec3(0.9);
        }

        vec3 lpos = vec3(4.5 * sin(iTime), -4.0, 7.5 + 4.5 * cos(iTime)); // light position top

        float lamb = lambert(nrm, lpos - endPt);
        float shad = shadow(endPt, normalize(lpos - endPt));

        col *= lamb;
        col *= max(0.2, shad);

    } 

    col = pow(col, vec3(0.4545)); // gamma
    fragColor = vec4(col, 1.0);
}

// this was inspired by the following two shaders:
// https://www.shadertoy.com/view/Mdf3z7
// https://www.shadertoy.com/view/MlfczH

#define A 2. * iTime
#define MinimumDistance 0.0009
#define asgt(i) abs(sin(i * iTime))
#define acgt(i) abs(cos(i * iTime))
#define sgt(i) sin(i * iTime)
#define cgt(i) cos(i * iTime)
#define Giter 5
#define MaxSteps 10
#define Iterations 10
#define raySteps 32 // changing this to 64 might open your third eye

float DE(in vec3 z) {
    vec3 Offset = vec3(0.92858,0.92858,0.32858);
    Offset.y += 0.5*asgt(0.05);
    Offset.x -= 0.2*cgt(0.1);

    float Scale = 2.0 + 1.2*asgt(0.1);
    Scale += 0.2*sgt(1.);

	z  = abs(2.-mod(z,4.0));

    z.x *= (1. - 0.01*sgt(1.));
    z.y *= (1. + 0.01*cgt(1.0));
    z.z *= (1. + 0.01*sgt(1.0));

	float d = 10.0;
	for (int m = 0; m < Iterations; m++) {	
		z = abs(z);
		if (z.x<z.y){ z.xy = z.yx;}
		if (z.x< z.z){ z.xz = z.zx;}
		if (z.y<z.z){ z.yz = z.zy;}
		z = Scale*z-Offset*(2.*Scale-1.);
		if( z.z < -0.5*Offset.z*(Scale-1.0))  z.z+=Offset.z*(Scale-1.0);

        d = min(d, length(z) * pow(Scale, float(-m)-1.0));
	}

	return d-0.001;
}

vec3 Glow(vec3 z, float b){
    z = fract( z * .2) * 2. -1.;

    for( int i = 0; i < Giter; ++i){ 
        float d = clamp( dot(z, z), .05, .65); 
        z = abs( z ) / d - 1.31;
    }
    return z;
}

vec3 fog(float a,vec3 b,float c){ 
    return 1. / (( 1. + 2.*b/a + b * b/(a*a))*c );
}

vec3 W(vec3 fr,float b,float c,float d){
    vec3 e=(fog( .1, abs(fr), d)*1. +
            fog( .1, vec3(length( fr.yz ), length( fr.zx ), length( fr.xy )), d) * 5.0 
            * (sin( A * vec3(2.1, 1.3, 1.7) + b*10.) + 1.)); 

    return (e * 7. + e.yzx * 1.5 + e.zxy * 1.5) * max( .5 -c *(200.+500.*asgt(1.)) / d, 0. ) / d * 70.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord.xy / iResolution.xy *2. -1.;
    uv.x *= iResolution.x/iResolution.y;
    float speed = 0.5;
    vec3 base = vec3(0.1, 0.3*sgt(0.1), 0.3*cgt(0.1)) * speed;

    vec3 camPos = 0.2*iTime * base;
	vec3 target = camPos + base;
	vec3 camUp  = vec3(0.0,1.0,0.0);

	vec3 camDir   = normalize(target-camPos); 
	camUp = normalize(camUp-dot(camDir,camUp)*camDir); 
	vec3 camRight = normalize(cross(camDir,camUp));

    vec3 rayDir = normalize( (camRight * uv.x  + camUp * uv.y + camDir));
    vec3 pos, color; float totalDistance, distance, v;

    for(int i = 0; i < raySteps; ++i){
        pos = camPos + rayDir * totalDistance;
        distance = DE(pos);        
        v = sin( A * .1 + pos.z);
        vec3 x = Glow( pos, v);
        totalDistance += distance + .001; 
        color += ( W( x, v, distance, 1.) ) * (distance + 0.001);      
    }
    fragColor = vec4( pow( color, vec3( .45 )), .5);
}

float ds(in float st, in float l0, in float l2){
  float l1 = (l0+l2)*0.5;
  return  smoothstep( l0, l1, st ) - smoothstep( l1, l2, st );
}

vec2 transUV(in vec2 uv){
    vec2 uvt = uv;
    uvt.x+=sin(uvt.x*26.0+iTime*0.6)*0.02;
    uvt.y+=sin(uvt.y*22.0+iTime*0.6)*0.02;
    return uvt;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying gradient
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
	//col = fract((uv.x+uv.y)*100.0) < 0.5 ? vec3(1.0,1.0,1.0) : vec3(0.0,0.0,0.0);

    // Grid
    vec2 uvt = transUV(uv);


   	float zebra = ds(fract((uvt.x+uvt.y)*20.0),0.0,0.8);
    vec3 colZebra = vec3(zebra);

    float countX=20.0;
    float countY=12.0;
    float width = 0.1;
    float frakX =fract(uvt.x*countX);
    float frakY =fract(uvt.y*countY);
    float gridX = ds(frakX,0.0,width);
    float gridY = ds(frakY,0.0,width);

    vec3 finalCol = col*zebra + vec3(gridX*0.3)+ vec3(gridY*0.3);

    // Output to screen
    fragColor = vec4(finalCol,1.0);
