mat2 rot(float angle){
	return mat2(cos(angle), -sin(angle), sin(angle), cos(angle));
}

float distCube(vec3 pos, float radius){
	return length(max(abs(pos) - radius, 0.)) - 0.05 * ((0.5 * sin(iTime + 5. * pos.y) + 0.5));
}

float distSphere(vec3 pos, float radius){
	return length(pos) - radius;
}

float distScene(vec3 pos){
    //slightly tilt the scene
    pos.yz = rot(0.4) * pos.yz;

    //rotate
    pos.xz = rot(iTime + sin(iTime) + 0.75) * pos.xz;

    vec3 posCube = pos;
    //twist
    posCube.xz = rot(iTime + sin(iTime + 5. * posCube.y) + 0.75) * posCube.xz;

    //repeat around y axis
    float angle = atan(posCube.z, posCube.x);
    float r = length(posCube.xz);    
   	float div = PI / 8.;
    angle = abs(mod(angle, div) - 0.5 * div);
    posCube.x = r * cos(angle);
    posCube.z = r * sin(angle);

    posCube.xz = rot(PI / 4.) * posCube.xz;    

    //twisted rounded cube
    float dist = distCube(posCube, 0.15);

    //repeat without twisting for the spheres
    angle = atan(pos.z, pos.x);
    float q = floor(angle / div);
    angle = abs(mod(angle, div) - 0.5 * div);
    pos.x = r * cos(angle);
    pos.z = r * sin(angle);

    //aggressive spheres
    float tr = abs(0.25 * sin(2.5 * sin(0.5 * iTime + 0.5)));
    dist = min(dist, distSphere((pos - vec3(1. * tr + 0.2, 0.15 * sin(3. * sin(iTime + 1.)) + 0.05, 0.)), 0.02 * (mod(q, 2.) + 0.5)));    
    dist = min(dist, distSphere((pos - vec3(2. * tr + 0.2, 0.15 * sin(3. * sin(iTime + 0.8)) + 0.05, 0.)), 0.025 * (mod(q + 1., 2.) + 0.5)));
    dist = min(dist, distSphere((pos - vec3(3. * tr + 0.2, 0.15 * sin(3. * sin(iTime + 0.5)) + 0.05, 0.)), 0.03 * (mod(q, 2.) + 0.5)));
    dist = min(dist, distSphere((pos - vec3(4. * tr + 0.25, 0.15 * sin(3. * sin(iTime + 0.2)) + 0.05, 0.)), 0.035 * (mod(q + 1., 2.) + 0.5)));

	return dist;
}

vec3 getNormal(vec3 pos){
	return normalize(vec3(distScene(pos + vec3(EPSN, 0., 0.) - distScene(pos - vec3(EPSN, 0., 0.))),
                         distScene(pos + vec3(0., EPSN, 0.) - distScene(pos - vec3(0., EPSN, 0.))),
                         distScene(pos + vec3(0., 0., EPSN) - distScene(pos - vec3(0., 0., EPSN)))));
}

vec3 render(vec2 uv){
	vec3 eye = vec3(0., 0., 3.);
    vec3 ray = normalize(vec3(uv, 0.) - eye);
    vec3 col = vec3(0.2);

    //raymarch
    vec3 pos = eye;
    float s, dist;
    bool hit = false;
    for(s = 0.; s < STEPS; s++){
    	dist = distScene(pos);
        if(abs(dist) < EPS){
        	break;
        }
        pos += dist * ray;
    }

    //color
    if(s < STEPS) col = 0.5 * getNormal(pos) + 0.5;
    return col;
}

